INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "やばいですね☆なの！", "ペコリーヌのプリン", "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1, 3, 100);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "主さま、なの！", "コッコロのプリン", "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 2, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 1, 3, 101);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "キャルのプリン", "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 3, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 0, 3, 102);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "おにーたん♪なの！", "シェフィのプリン", "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 4, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 1, 3, 103);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ハーハッハッハ！\n世界征服なのじゃなのー！", "イリヤのプリン", "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 5, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 1, 3, 104);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "お兄ちゃんのエッチ！　なの！", "アカリのプリン", "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 6, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 1, 3, 105);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "ヨリのプリン", "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 7, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 1, 3, 106);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "シノブのプリン", "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 8, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 0, 3, 107);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "インフェルノシールド！なの！", "ジュンのプリン", "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 9, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 1, 3, 108);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "トモのプリン", "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 10, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 1, 3, 109);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "タイガーヒーローボンバー！\nなの！", "マツリのプリン", "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 11, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 1, 3, 110);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ま、いいんじゃん？\nしらんけどなの", "クロエのプリン", "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 13, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 1, 3, 111);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ぷりーん★なの！", "チエルのプリン", "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 14, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 1, 3, 112);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "端的に換言すれば\n青春の味なの？", "ユニのプリン", "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 15, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 1, 3, 113);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "幼なじみはあんた…なの？", "サレンのプリン", "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 16, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 1, 3, 114);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ひゃああんなの～！", "スズメのプリン", "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 17, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 1, 3, 115);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "サルキデバレヤ！なの！", "クルミのプリン", "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 18, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 0, 3, 116);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "プリンフルスイングなの～！", "アヤネのプリン", "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 19, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 1, 3, 117);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "みらくるみやりん\nくるりんぱ～☆なの！", "マホのプリン", "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 20, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 1, 3, 118);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ウルフェンバイトなの！", "マコトのプリン", "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 21, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 1, 3, 119);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "なんくるないさ～！なの！", "カオリのプリン", "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 22, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 1, 3, 120);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ふふん。簡単な推理なの", "カスミのプリン", "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 23, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 1, 3, 121);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "エリザベス～！なの～！", "マヒルのプリン", "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 24, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 1, 3, 122);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "シオリのプリン", "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 25, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 1, 3, 123);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "もふもふにしてあげるなの！", "リマのプリン", "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 26, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 1, 3, 124);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "めんどくさいな～なの…", "リンのプリン", "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 27, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 0, 3, 125);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "プリンの権能を解放するの～！", "ラビリスタのプリン", "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 28, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 1, 3, 126);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "お姉ちゃんパワー全開！なの！", "シズルのプリン", "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 29, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 0, 3, 127);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "お兄ちゃん、プリンをよこすの！", "リノのプリン", "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 30, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 1, 3, 128);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "困った時はお互いさまさま！\nなの！", "ヒヨリのプリン", "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 31, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 1, 3, 129);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "フラワリングヒールなの～♪", "ユイのプリン", "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 32, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 1, 3, 130);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "スラッシュテンペスト！なの。", "レイのプリン", "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 33, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 1, 3, 131);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "おーほっほほほ！なの！", "アキノのプリン", "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 34, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 1, 3, 132);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "プリしゅわ最高～！なの！", "ユカリのプリン", "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 35, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 1, 3, 133);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "にゃにゃにゃ！なの！", "タマキのプリン", "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 36, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 1, 3, 134);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "効率全開！なの！", "ミフユのプリン", "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 37, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 0, 3, 135);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "あらあら～なの～♪", "ミサトのプリン", "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 38, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 1, 3, 136);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "スヤスヤ～…なの…", "ハツネのプリン", "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 39, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 1, 3, 137);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ぼっちは卒業なの～！", "アオイのプリン", "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 40, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 1, 3, 138);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "リズムあげるのー！", "ノゾミのプリン", "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 41, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 1, 3, 139);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "風の加護を！なの！", "チカのプリン", "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 42, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 1, 3, 140);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "騎士さんのために\nしたんじゃありません！なの！", "ツムギのプリン", "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 43, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 1, 3, 141);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "宴の始まりなの！", "クリスティーナのプリン", "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 12, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 0, 3, 142);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "一刀両断、覚悟しな！なの！", "ルカのプリン", "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 44, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 1, 3, 143);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "きゅぴーん！キタコレ！なの！", "ナナカのプリン", "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 45, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 1, 3, 144);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "人呼んで、疾風のミヤコなの！", "アンナのプリン", "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 46, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 1, 3, 145);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "クスクス…クスクスクス…なの", "エリコのプリン", "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 47, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 1, 3, 146);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "ミツキのプリン", "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 48, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 1, 3, 147);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "私は子どもではない！なの！", "モニカのプリン", "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 49, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 1, 3, 148);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "ニノンのプリン", "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 50, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 1, 3, 149);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "…ぐふふ、じゅるり～なの", "クウカのプリン", "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 51, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 0, 3, 150);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "あぁ、ミヤコはなんて美しいの～", "ユキのプリン", "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 52, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 1, 3, 151);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "アユミのプリン", "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 53, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 0, 3, 152);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "にひひ…イタズラの時間なの！", "ミソギのプリン", "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 54, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 0, 3, 153);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ぴょんぴょこぴょーん、なの♪", "ミミのプリン", "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 55, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 0, 3, 154);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "へんたいふしんしゃさんなの！", "キョウカのプリン", "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 56, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 0, 3, 155);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ハート狙い撃ち！なの！", "スズナのプリン", "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 57, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 1, 3, 156);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "授業を始めます♪なの！", "イオのプリン", "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 58, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 0, 3, 157);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "立派なレディ、なの！", "ミサキのプリン", "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 59, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 1, 3, 158);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "ホマレのプリン", "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 60, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 1, 3, 159);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "たぎってきたの！", "カヤのプリン", "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 61, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 1, 3, 160);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "下剋上、やってやるですなの！", "イノリのプリン", "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 62, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 0, 3, 161);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "どけどけ～！なの～！", "ムイミのプリン", "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 63, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 1, 3, 162);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ミラーミラー！なの！", "ネネカのプリン", "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 64, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 1, 3, 163);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "プリンに忠誠を誓いますわ！\nなの！", "クレジッタのプリン", "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 65, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 1, 3, 164);
INSERT INTO `v1_ade92582220f991a805a092d30df1efc6b811879499bf32ce2329e381c6079a7` VALUES (5, "ギルド管理協会へようこそ！\nなの！", "カリンのプリン", "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 66, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 1, 3, 165);
