INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 190804, 1, 60000, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 1, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 2, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 3, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60000, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 4, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 5, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 6, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60000, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 7, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 8, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 9, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 10, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 11, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60000, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 12, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 13, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 14, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 15, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60000, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 16, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60000, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 17, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 190804, 0, 60000, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 18, 3, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 190804, 0, 60000, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 19, 3, "カリン", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 101201, 0, 80001, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 20, 0, "ハツネ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 101201, 0, 80001, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 21, 0, "ハツネ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 103801, 0, 80001, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 22, 1, "シオリ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 103801, 0, 80001, "来年も、\n一緒にいられたら…\nうれしいな", 23, 1, "シオリ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 103601, 0, 80001, "あなたと一緒にいるようになって、\nもう1年ですか…", 24, 0, "キョウカ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 103601, 0, 80001, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 25, 0, "キョウカ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 102001, 0, 80001, "おにいちゃんとあってから、\n1年たったよ～！", 26, 1, "ミミ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 102001, 0, 80001, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 27, 1, "ミミ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100401, 0, 80001, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 28, 2, "ミソギ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100401, 0, 80001, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 29, 2, "ミソギ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 101001, 0, 80001, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 30, 0, "マホ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 101001, 0, 80001, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 31, 0, "マホ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 104401, 0, 80001, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 32, 1, "イリヤ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 104401, 0, 80001, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 33, 1, "イリヤ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 101701, 0, 80001, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 34, 2, "カオリ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 101701, 0, 80001, "これからも、どうぞよろしく\nお願いするさ～！", 35, 2, "カオリ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 102801, 0, 80001, "あんたと出会ってからもうそんなに\nたつのね～", 36, 0, "サレン", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 102801, 0, 80001, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 37, 0, "サレン", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 102501, 0, 80001, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 38, 1, "スズメ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 102501, 0, 80001, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 39, 1, "スズメ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 104601, 0, 80001, "振り返れば、今までいろんなことが\nあったにゃあ…", 40, 0, "タマキ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 104601, 0, 80001, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 41, 0, "タマキ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 104801, 0, 80001, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 42, 1, "ミフユ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 104801, 0, 80001, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 43, 1, "ミフユ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 103201, 0, 80001, "あなたさまと出会ってもう1年…", 44, 0, "アキノ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 103201, 0, 80001, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 45, 0, "アキノ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 103401, 0, 80001, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 46, 1, "ユカリ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 103401, 0, 80001, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 47, 1, "ユカリ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 107101, 0, 80001, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 48, 0, "クリスティーナ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 107101, 0, 80001, "ともかくこれからも\n愉しませてくれよ☆", 49, 0, "クリスティーナ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 104701, 0, 80001, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 50, 1, "ジュン", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 104701, 0, 80001, "見えないから分からないって？\nそれもそうだな…", 51, 1, "ジュン", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 103701, 0, 80001, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 52, 0, "トモ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 103701, 0, 80001, "これからもよろしくね！", 53, 0, "トモ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100501, 0, 80001, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 54, 1, "マツリ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100501, 0, 80001, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 55, 1, "マツリ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100701, 0, 80001, "オマエと会って1年なの！\n全然成仏もできなかったし、", 56, 0, "ミヤコ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100701, 0, 80001, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 57, 0, "ミヤコ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 103101, 0, 80001, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 58, 1, "シノブ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 103101, 0, 80001, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 59, 1, "シノブ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100901, 0, 80001, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 60, 0, "アンナ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100901, 0, 80001, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 61, 0, "アンナ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 101301, 0, 80001, "君と出会ってもう1年だって！\n早いねー！", 62, 1, "ナナカ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 101301, 0, 80001, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 63, 1, "ナナカ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105101, 0, 80001, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 64, 0, "ミツキ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105101, 0, 80001, "これからも、いろんなところを\n調べさせてね？", 65, 0, "ミツキ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105601, 0, 80001, "お前さんと出会ってもう1年か\n助太刀するつもりが", 66, 1, "ルカ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105601, 0, 80001, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 67, 1, "ルカ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 102701, 0, 80001, "早いものですわね、あなた様と\nお会いして、もう1年…", 68, 2, "エリコ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 102701, 0, 80001, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 69, 2, "エリコ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 102301, 0, 80001, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 70, 0, "アヤネ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 102301, 0, 80001, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 71, 0, "アヤネ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 104201, 0, 80001, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 72, 1, "チカ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 104201, 0, 80001, "私のことを見守ってください\nできることなら、ずっと…", 73, 1, "チカ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 102101, 0, 80001, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 74, 2, "クルミ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 102101, 0, 80001, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 75, 2, "クルミ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 104901, 0, 80001, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 76, 0, "シズル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 104901, 0, 80001, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 77, 0, "シズル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 101101, 0, 80001, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 78, 1, "リノ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 101101, 0, 80001, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 79, 1, "リノ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100201, 0, 80001, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 80, 0, "ユイ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100201, 0, 80001, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 81, 0, "ユイ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100101, 0, 80001, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 82, 1, "ヒヨリ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100101, 0, 80001, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 83, 1, "ヒヨリ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 100301, 0, 80001, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 84, 2, "レイ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 100301, 0, 80001, "キミに背中を預けられる日が、\n早くきますように", 85, 2, "レイ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105801, 0, 80001, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 86, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105801, 0, 80001, "これからもずっと、覚えていて\nくださいね！", 87, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105901, 0, 80001, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 88, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105901, 0, 80001, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 89, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 106001, 0, 80001, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 90, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 106001, 0, 80001, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 91, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 112101, 1, 60001, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 92, 1, "スズメ", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 93, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 94, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 95, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 96, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60001, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 97, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 98, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 99, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 100, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 101, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60001, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 102, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 103, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 104, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 105, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 106, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 107, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 108, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60001, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 109, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 110, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 111, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 112, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 113, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 114, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60001, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 115, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60001, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 116, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 112101, 0, 60001, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 117, 3, "スズメ", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 112101, 0, 60001, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 118, 3, "スズメ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105801, 0, 80003, "ヒューヒュー♪\nドンドンパフパフー♪", 119, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105901, 0, 80003, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 120, 1, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105901, 0, 80003, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 121, 1, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 106001, 0, 80003, "え？　ちょっと\nいきなりなにっ？", 122, 2, "キャル", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 106001, 0, 80003, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 123, 2, "キャル", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105801, 0, 80003, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 124, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 105801, 0, 80003, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 125, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 105901, 0, 80003, "ですからその日に向けて\n今から気分を盛り上げているのです", 126, 1, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 106001, 0, 80003, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 127, 2, "キャル", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 105901, 0, 80003, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 128, 1, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 105801, 0, 80003, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 129, 0, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 106001, 0, 80003, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 130, 2, "キャル", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 105901, 0, 80003, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 131, 1, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 106001, 0, 80003, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 132, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 190801, 0, 80003, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 133, 1, "カリン", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 106001, 0, 80003, "げっ…カリンじゃない\nなんでもないわよ！", 134, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 190801, 0, 80003, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 135, 1, "カリン", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 106001, 0, 80003, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 136, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 190801, 0, 80003, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 137, 1, "カリン", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 106001, 0, 80003, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 138, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 106001, 0, 80003, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 139, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 190801, 0, 80003, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 140, 1, "カリン", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 106001, 0, 80003, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 141, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 190801, 0, 80003, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 142, 1, "カリン", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 106001, 0, 80003, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 143, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 105901, 0, 80003, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 144, 0, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 105801, 0, 80003, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 145, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 105901, 0, 80003, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 146, 0, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 105901, 0, 80003, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 147, 0, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 105801, 0, 80003, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 148, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 105801, 0, 80003, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 149, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 105901, 0, 80003, "あとは、牧場に素敵な温泉が\n出来たとか…", 150, 0, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 105801, 0, 80003, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 151, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 105901, 0, 80003, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 152, 0, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 105801, 0, 80003, "えへへ、ありがとうございます\nコッコロちゃん！", 153, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 105901, 0, 80003, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 154, 0, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 105801, 0, 80003, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 155, 1, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 105801, 0, 80003, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 156, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 106001, 0, 80003, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 157, 1, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 105801, 0, 80003, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 158, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 106001, 0, 80003, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 159, 1, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 105801, 0, 80003, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 160, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 106001, 0, 80003, "…積極的？\nなんの話よ？", 161, 1, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 105801, 0, 80003, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 162, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 105801, 0, 80003, "ムーサの町で\nきゃるきゃる～ん☆　って！", 163, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 106001, 0, 80003, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 164, 1, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 105801, 0, 80003, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 165, 0, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 106001, 0, 80003, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 166, 1, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 106001, 0, 80003, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 167, 1, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 106001, 0, 80003, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 168, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 105901, 0, 80003, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 169, 1, "コッコロ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 106001, 0, 80003, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 170, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 105901, 0, 80003, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 171, 1, "コッコロ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 106001, 0, 80003, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 172, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 105901, 0, 80003, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 173, 1, "コッコロ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 106001, 0, 80003, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 174, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 105901, 0, 80003, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 175, 1, "コッコロ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 106001, 0, 80003, "かくし芸？", 176, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 105901, 0, 80003, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 177, 1, "コッコロ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 106001, 0, 80003, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 178, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 105801, 0, 80003, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 179, 0, "ペコリーヌ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 106001, 0, 80003, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 180, 2, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 105901, 0, 80003, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 181, 1, "コッコロ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 105801, 0, 80003, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 182, 0, "ペコリーヌ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 106001, 0, 80003, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 183, 2, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 105901, 0, 80003, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 184, 1, "コッコロ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 106001, 0, 80003, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 185, 2, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 106001, 0, 80003, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 186, 2, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 106001, 0, 80003, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 187, 2, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 105801, 0, 80003, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 188, 0, "ペコリーヌ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 105901, 0, 80003, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 189, 1, "コッコロ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 106001, 0, 80003, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 190, 2, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 106001, 0, 80003, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 191, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 106001, 0, 80003, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 192, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 106001, 0, 80003, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 193, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 106001, 0, 80003, "あー\nあー\n…声、これで大丈夫かしら？", 194, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (34, 0, "", 106001, 0, 80003, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 195, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 106001, 0, 80003, "オイッスー☆", 196, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 106001, 0, 80003, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 197, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 0, "", 106001, 0, 80003, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 198, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (38, 0, "", 106001, 0, 80003, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 199, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 105901, 0, 80003, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 200, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 105901, 0, 80003, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 201, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 105901, 0, 80003, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 202, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 105901, 0, 80003, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 203, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 105901, 0, 80003, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 204, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 105901, 0, 80003, "オイッスー☆", 205, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 105901, 0, 80003, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 206, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 105901, 0, 80003, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 207, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 105901, 0, 80003, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 208, 0, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 105801, 0, 80003, "オイッスー☆", 209, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 105801, 0, 80003, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 210, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 105801, 0, 80003, "あと６日で2.5周年ですか…", 211, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 105801, 0, 80003, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 212, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 105801, 0, 80003, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 213, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 105801, 0, 80003, "オイッスー☆", 214, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 105801, 0, 80003, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 215, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 105801, 0, 80003, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 216, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 105801, 0, 80003, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 217, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 105801, 0, 80003, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 218, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 105801, 0, 80003, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 219, 0, "ペコリーヌ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 105801, 0, 80003, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 220, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 105901, 0, 80003, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 221, 1, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 105801, 0, 80003, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 222, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 106001, 0, 80003, "…………", 223, 2, "キャル", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 105901, 0, 80003, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 224, 1, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 106001, 0, 80003, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 225, 2, "キャル", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 105801, 0, 80003, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 226, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 106001, 0, 80003, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 227, 2, "キャル", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (34, 0, "", 105801, 0, 80003, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 228, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 105801, 0, 80003, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 229, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (42, 0, "", 106001, 0, 80003, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 230, 2, "キャル", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 105901, 0, 80003, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 231, 1, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (43, 0, "", 106001, 0, 80003, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 232, 2, "キャル", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 105801, 0, 80003, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 233, 0, "ペコリーヌ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 190801, 0, 80003, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 234, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 190801, 0, 80003, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 235, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 190801, 0, 80003, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 236, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 190801, 0, 80003, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 237, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 190801, 0, 80003, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 238, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 190801, 0, 80003, "次にコッコロさんですが\nこちらは発声練習をしています", 239, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 190801, 0, 80003, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 240, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 190801, 0, 80003, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 241, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 190801, 0, 80003, "あれ？\nもしかして緊張で気を失って…？", 242, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 190801, 0, 80003, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 243, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 190801, 0, 80003, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 244, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 190801, 0, 80003, "それでは\nまた明日お会いしましょう♪", 245, 0, "カリン", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (44, 0, "", 106001, 0, 80003, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 246, 0, "キャル", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 0, "", 105801, 0, 80003, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 247, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 105901, 0, 80003, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 248, 1, "コッコロ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (45, 0, "", 106001, 0, 80003, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 249, 0, "キャル", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (38, 0, "", 105801, 0, 80003, "ひどいですよぉキャルちゃ～ん！", 250, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 105901, 0, 80003, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 251, 1, "コッコロ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 105801, 0, 80003, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 252, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (46, 0, "", 106001, 0, 80003, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 253, 0, "キャル", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (34, 0, "", 105901, 0, 80003, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 254, 1, "コッコロ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 105801, 0, 80003, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 255, 2, "ペコリーヌ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 105801, 0, 80003, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 256, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (47, 0, "", 106001, 0, 80003, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 257, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (42, 0, "", 105801, 0, 80003, "忘れてきちゃいました☆", 258, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (48, 0, "", 106001, 0, 80003, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 259, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 105901, 0, 80003, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 260, 1, "コッコロ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (49, 0, "", 106001, 0, 80003, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 261, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (43, 0, "", 105801, 0, 80003, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 262, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (50, 0, "", 106001, 0, 80003, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 263, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 105901, 0, 80003, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 264, 1, "コッコロ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (51, 0, "", 106001, 0, 80003, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 265, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 0, "", 105901, 0, 80003, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 266, 1, "コッコロ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (52, 0, "", 106001, 0, 80003, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 267, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (44, 0, "", 105801, 0, 80003, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 268, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (38, 0, "", 105901, 0, 80003, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 269, 1, "コッコロ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (45, 0, "", 105801, 0, 80003, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 270, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (53, 0, "", 106001, 0, 80003, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 271, 2, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (46, 0, "", 105801, 0, 80003, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 272, 0, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 190801, 0, 80003, "こんにちは\n【ギルド管理協会】のカリンです", 273, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 190801, 0, 80003, "昨日の生放送は\nいかがだったでしょうか？", 274, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 190801, 0, 80003, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 275, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 190801, 0, 80003, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 276, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 190801, 0, 80003, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 277, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 190801, 0, 80003, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 278, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 190801, 0, 80003, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 279, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 190801, 0, 80003, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 280, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 190801, 0, 80003, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 281, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 190801, 0, 80003, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 282, 0, "カリン", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (47, 0, "", 105801, 0, 80003, "準備はいいですかっ？\n…いっきますよー！", 283, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (54, 0, "", 106001, 0, 80003, "祝！", 284, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 105901, 0, 80003, "2.5周年！", 285, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (48, 0, "", 105801, 0, 80003, "おめでとうございま～す☆", 286, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (55, 0, "", 106001, 0, 80003, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 287, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 105901, 0, 80003, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 288, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (49, 0, "", 105801, 0, 80003, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 289, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (56, 0, "", 106001, 0, 80003, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 290, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 105901, 0, 80003, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 291, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (57, 0, "", 106001, 0, 80003, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 292, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (50, 0, "", 105801, 0, 80003, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 293, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (58, 0, "", 106001, 0, 80003, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 294, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (51, 0, "", 105801, 0, 80003, "記念日だから\n平気平気♪\nやばいですね☆", 295, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (59, 0, "", 106001, 0, 80003, "意味分かんないわよ！", 296, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (42, 0, "", 105901, 0, 80003, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 297, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (43, 0, "", 105901, 0, 80003, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 298, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (52, 0, "", 105801, 0, 80003, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 299, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (60, 0, "", 106001, 0, 80003, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 300, 2, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (44, 0, "", 105901, 0, 80003, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 301, 1, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (53, 0, "", 105801, 0, 80003, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 302, 0, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (54, 1, "105901,45,106001,61", 105801, 0, 80003, "2.5周年\nありがとうございます！", 303, 0, "ペコリーヌ・コッコロ・キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 190804, 1, 60002, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 304, 1, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 305, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 306, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 307, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 308, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60002, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 309, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 310, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 311, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 312, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 313, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60002, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 314, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 315, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 316, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 317, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 318, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 319, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 320, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60002, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 321, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 322, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 323, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 324, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 325, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 326, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60002, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 327, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60002, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 328, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 190804, 0, 60002, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 329, 3, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 190804, 0, 60002, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 330, 3, "カリン", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105901, 0, 80005, "ささ、シェフィさまはこちらへ…", 331, 4, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 106401, 0, 80005, "コッコロさん、どこへ行くの…？\n…え？", 332, 0, "シェフィ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 105801, 0, 80005, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 333, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105901, 0, 80005, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 334, 4, "コッコロ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 106401, 0, 80005, "ええと…\n二人とも何をしているの？", 335, 0, "シェフィ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 106001, 0, 80005, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 336, 1, "キャル", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 106401, 0, 80005, "あの日…？", 337, 0, "シェフィ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 105801, 0, 80005, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 338, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 105801, 0, 80005, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 339, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 190801, 0, 80005, "やばいですね☆", 340, 2, "カリン", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 106001, 0, 80005, "って、なにしれっと\n参加してんのよカリン！", 341, 1, "キャル", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 190801, 0, 80005, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 342, 2, "カリン", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 190801, 0, 80005, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 343, 2, "カリン", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 105801, 0, 80005, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 344, 3, "ペコリーヌ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 106401, 0, 80005, "えっ！？　ええとええと…！\nあ、あと14日…！", 345, 0, "シェフィ", 0, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 105801, 0, 80005, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 346, 3, "ペコリーヌ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 105901, 0, 80005, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 347, 1, "コッコロ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 105901, 0, 80005, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 348, 1, "コッコロ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 105801, 0, 80005, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 349, 3, "ペコリーヌ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 105901, 0, 80005, "さすがでございます\nペコリーヌさま", 350, 1, "コッコロ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 106001, 0, 80005, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 351, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 106401, 0, 80005, "しゃなり、しゃなり…\nふふ、案外できるものね", 352, 2, "シェフィ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 105801, 0, 80005, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 353, 3, "ペコリーヌ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 105901, 0, 80005, "さ、次はキャルさまの\n番でございますよ", 354, 1, "コッコロ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 106001, 0, 80005, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 355, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 106401, 0, 80005, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 356, 2, "シェフィ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 106001, 0, 80005, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 357, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 106001, 0, 80005, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 358, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 105901, 0, 80005, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 359, 1, "コッコロ", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 106001, 0, 80005, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 360, 0, "キャル", 0, 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 105901, 0, 80005, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 361, 1, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 105801, 0, 80005, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 362, 3, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 105901, 0, 80005, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 363, 1, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 106401, 0, 80005, "聖学祭…って、なにかしら？", 364, 2, "シェフィ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 105901, 0, 80005, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 365, 1, "コッコロ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 106001, 0, 80005, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 366, 0, "キャル", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 106001, 0, 80005, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 367, 0, "キャル", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 105801, 0, 80005, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 368, 3, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 106001, 0, 80005, "いや、そのまんますぎでしょ…", 369, 0, "キャル", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 106401, 0, 80005, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 370, 2, "シェフィ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 105801, 0, 80005, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 371, 3, "ペコリーヌ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 106401, 0, 80005, "えっ？　ええ…\nぎゅーっ…？", 372, 2, "シェフィ", 0, 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 106001, 0, 80005, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 373, 0, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 106401, 0, 80005, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 374, 2, "シェフィ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 106001, 0, 80005, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 375, 0, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 105901, 0, 80005, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 376, 1, "コッコロ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 106001, 0, 80005, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 377, 0, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 105801, 0, 80005, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 378, 3, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 106001, 0, 80005, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 379, 0, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 106001, 0, 80005, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 380, 0, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 106401, 0, 80005, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 381, 2, "シェフィ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 105801, 0, 80005, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 382, 3, "ペコリーヌ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 105901, 0, 80005, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 383, 1, "コッコロ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 106001, 0, 80005, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 384, 0, "キャル", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, 0, "", 106401, 0, 80005, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 385, 2, "シェフィ", 0, 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 105801, 0, 80005, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 386, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 105801, 0, 80005, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 387, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 105801, 0, 80005, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 388, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 106001, 0, 80005, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 389, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 106001, 0, 80005, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 390, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 106401, 0, 80005, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 391, 2, "シェフィ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 105901, 0, 80005, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 392, 1, "コッコロ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 105801, 0, 80005, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 393, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 106001, 0, 80005, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 394, 0, "キャル", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 105801, 0, 80005, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 395, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 105801, 0, 80005, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 396, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 105801, 0, 80005, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 397, 3, "ペコリーヌ", 0, 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, 0, "", 190801, 0, 80005, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 398, 2, "カリン", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, 0, "", 190801, 0, 80005, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 399, 2, "カリン", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 106001, 0, 80005, "…えっ！？", 400, 1, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 105801, 0, 80005, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 401, 3, "ペコリーヌ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 106401, 0, 80005, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 402, 0, "シェフィ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 105901, 0, 80005, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 403, 4, "コッコロ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 106401, 0, 80005, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 404, 0, "シェフィ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 106001, 0, 80005, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 405, 1, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 105801, 0, 80005, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 406, 3, "ペコリーヌ", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 106001, 0, 80005, "ぶっ殺すぞ！？", 407, 1, "キャル", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, 0, "", 190801, 0, 80005, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 408, 2, "カリン", 0, 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 105901, 0, 80005, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 409, 1, "コッコロ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 106001, 0, 80005, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 410, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 106401, 0, 80005, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 411, 2, "シェフィ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 105801, 0, 80005, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 412, 3, "ペコリーヌ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 106001, 0, 80005, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 413, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 106401, 0, 80005, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 414, 2, "シェフィ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (16, 0, "", 105901, 0, 80005, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 415, 1, "コッコロ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 106401, 0, 80005, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 416, 2, "シェフィ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (17, 0, "", 105901, 0, 80005, "もちろんでございます\n仲良くお勉強いたしましょう", 417, 1, "コッコロ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 106001, 0, 80005, "しれっと教育ママが\n生まれてる…？", 418, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 105801, 0, 80005, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 419, 3, "ペコリーヌ", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 106001, 0, 80005, "え～～～～！？", 420, 0, "キャル", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 106001, 0, 80005, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 421, 0, "キャル", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 106401, 0, 80005, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 422, 2, "シェフィ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (18, 0, "", 105901, 0, 80005, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 423, 1, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 105801, 0, 80005, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 424, 3, "ペコリーヌ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 106401, 0, 80005, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 425, 2, "シェフィ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 106001, 0, 80005, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 426, 0, "キャル", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (19, 0, "", 105901, 0, 80005, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 427, 1, "コッコロ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 106401, 0, 80005, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 428, 2, "シェフィ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 106001, 0, 80005, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 429, 0, "キャル", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 106001, 0, 80005, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 430, 0, "キャル", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 105801, 0, 80005, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 431, 3, "ペコリーヌ", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 106001, 0, 80005, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 432, 0, "キャル", 0, 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 106401, 0, 80005, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 433, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 106401, 0, 80005, "…えっ？\n生放送の意気込み！？\n突然なに！？", 434, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 106401, 0, 80005, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 435, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 106401, 0, 80005, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 436, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 106401, 0, 80005, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 437, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 106401, 0, 80005, "それから、それから…！\nぅ、ぅぅっ…！", 438, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 106401, 0, 80005, "―びえぇぇ～んっ！！！", 439, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (20, 0, "", 105901, 0, 80005, "どうされましたか、シェフィさま", 440, 1, "コッコロ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 106401, 0, 80005, "ううっ…ひっく…\nママッ…！", 441, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (21, 0, "", 105901, 0, 80005, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 442, 1, "コッコロ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (22, 0, "", 105901, 0, 80005, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 443, 1, "コッコロ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 106401, 0, 80005, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 444, 0, "シェフィ", 0, 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (23, 0, "", 105901, 0, 80005, "主さま\n3.5周年まであと５日\nでございます", 445, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (24, 0, "", 105901, 0, 80005, "ですがその前に\n生放送がありますね", 446, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (25, 0, "", 105901, 0, 80005, "…生放送に向けた\n意気込みでございますか？", 447, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 105901, 0, 80005, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 448, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 105901, 0, 80005, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 449, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 105901, 0, 80005, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 450, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 105901, 0, 80005, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 451, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 105901, 0, 80005, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 452, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 105901, 0, 80005, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 453, 0, "コッコロ", 0, 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (26, 0, "", 105801, 0, 80005, "3.5周年まであと４日！\nやばいですね☆", 454, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 106001, 0, 80005, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 455, 1, "キャル", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (27, 0, "", 105801, 0, 80005, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 456, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (28, 0, "", 105801, 0, 80005, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 457, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (29, 0, "", 105801, 0, 80005, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 458, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 106001, 0, 80005, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 459, 1, "キャル", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (30, 0, "", 105801, 0, 80005, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 460, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 105801, 0, 80005, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 461, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 105801, 0, 80005, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 462, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (34, 0, "", 106001, 0, 80005, "だからあたしはやるなんて一言も…", 463, 1, "キャル", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 105801, 0, 80005, "オイッスー☆", 464, 0, "ペコリーヌ", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 106001, 0, 80005, "聞きなさいっての！！", 465, 1, "キャル", 0, 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (31, 0, "", 106401, 0, 80005, "3.5周年まであと３日…\nそして生放送は、明日…", 466, 2, "シェフィ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 106401, 0, 80005, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 467, 2, "シェフィ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 106401, 0, 80005, "おいっす！\n…こうじゃなかったわね\nええと確か…", 468, 2, "シェフィ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (34, 0, "", 106401, 0, 80005, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 469, 2, "シェフィ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 106401, 0, 80005, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 470, 2, "シェフィ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (34, 0, "", 105801, 0, 80005, "やってますねシェフィちゃん！", 471, 3, "ペコリーヌ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 106001, 0, 80005, "なによ、考えることは\nみんな同じってこと？", 472, 0, "キャル", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (32, 0, "", 105901, 0, 80005, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 473, 1, "コッコロ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 106401, 0, 80005, "みんな…もちろんよ！", 474, 2, "シェフィ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 105801, 0, 80005, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 475, 3, "ペコリーヌ", 0, 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, 0, "", 190801, 0, 80005, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 476, 2, "カリン", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, 0, "", 190801, 0, 80005, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 477, 2, "カリン", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 105801, 0, 80005, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 478, 3, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, 0, "", 190801, 0, 80005, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 479, 2, "カリン", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (33, 0, "", 105901, 0, 80005, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 480, 4, "コッコロ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, 0, "", 190801, 0, 80005, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 481, 2, "カリン", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 0, "", 106001, 0, 80005, "大丈夫…大丈夫…\nあたしは大丈夫…", 482, 1, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, 0, "", 190801, 0, 80005, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 483, 2, "カリン", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 0, "", 106401, 0, 80005, "あうー？", 484, 0, "シェフィ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 1, "105901,34,106001,38,190801,12", 105801, 0, 80005, "あ…", 485, 4, "みんな", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 106001, 0, 80005, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 486, 1, "キャル", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (35, 0, "", 105901, 0, 80005, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 487, 4, "コッコロ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (38, 0, "", 105801, 0, 80005, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 488, 3, "ペコリーヌ", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, 0, "", 190801, 0, 80005, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 489, 2, "カリン", 0, 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (38, 0, "", 106401, 0, 80005, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 490, 2, "シェフィ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 105801, 0, 80005, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 491, 3, "ペコリーヌ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 106001, 0, 80005, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 492, 0, "キャル", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (36, 0, "", 105901, 0, 80005, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 493, 1, "コッコロ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 106401, 0, 80005, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 494, 2, "シェフィ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 105801, 0, 80005, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 495, 3, "ペコリーヌ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (37, 0, "", 105901, 0, 80005, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 496, 1, "コッコロ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 106001, 0, 80005, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 497, 0, "キャル", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 106401, 0, 80005, "へ、へぇ…", 498, 2, "シェフィ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 105801, 0, 80005, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 499, 3, "ペコリーヌ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 106401, 0, 80005, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 500, 2, "シェフィ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (42, 0, "", 106401, 0, 80005, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 501, 2, "シェフィ", 0, 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (42, 0, "", 105801, 0, 80005, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 502, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (42, 0, "", 106001, 0, 80005, "祝！", 503, 1, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (38, 0, "", 105901, 0, 80005, "3.5周年！", 504, 4, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (43, 0, "", 106401, 0, 80005, "おめでとうございます！", 505, 0, "シェフィ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, 0, "", 190801, 0, 80005, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 506, 2, "カリン", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, 0, "", 190801, 0, 80005, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 507, 2, "カリン", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (43, 0, "", 105801, 0, 80005, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 508, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (44, 0, "", 105801, 0, 80005, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 509, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (43, 0, "", 106001, 0, 80005, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 510, 1, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (44, 0, "", 106001, 0, 80005, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 511, 1, "キャル", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (44, 0, "", 106401, 0, 80005, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 512, 0, "シェフィ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (45, 0, "", 106401, 0, 80005, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 513, 0, "シェフィ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (39, 0, "", 105901, 0, 80005, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 514, 4, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (40, 0, "", 105901, 0, 80005, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 515, 4, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (41, 0, "", 105901, 0, 80005, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 516, 4, "コッコロ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (45, 0, "", 105801, 0, 80005, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 517, 3, "ペコリーヌ", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (46, 1, "105901,42,106001,45,106401,46,190801,16", 105801, 0, 80005, "これからも\nよろしくおねがいします！", 518, 4, "みんな", 0, 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 190804, 1, 60003, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 519, 1, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 520, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 521, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 522, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 523, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60003, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 524, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 525, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 526, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 527, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 528, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60003, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 529, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 530, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 531, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 532, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 533, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 534, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 535, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60003, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 536, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60003, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 537, 2, "", 4, 0);
