INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60005, "今日の運勢は小吉！　空を眺めると気持ちが晴れやかに！　　　　眺めるだけじゃなくて、ひとっ飛びしちゃいましょうかっ♪", 596, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60005, "今日の運勢は小吉！　掃除をすると運勢が開けそう！　　　　　　「わ、めんど」「ヴルムっ！　新年早々サボっちゃダメなんだからね！」", 597, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 190804, 0, 60005, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 598, 3, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 190804, 0, 60005, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 599, 3, "カリン", 0, 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, 0, "", 190804, 1, 60006, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 600, 1, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は姫吉！　勇気を持って、新しいことに挑戦しよう！\n幸先がいいねえ少年！　今年もいい一年になるよう祈ってるよ。", 601, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は姫吉！　どんなピンチも簡単に乗り越えられそう！\nよい結果が出たようで何よりです。さて、待人の方は……ふふ。", 602, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は姫吉！　あの人と仲直り……できちゃうかも！？\nはあ……この私があなたの運勢に興味を抱くわけないでしょう？", 603, 2, "", 1, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60006, "今日の運勢は大吉！　身近な友達との絆が深まる予感……？\n騎士様は、どなたと友誼を結ぶのでしょう？　気になります……", 604, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は大吉！　ラッキーアイテムは「蛇の抜け殻」！\nこのアタシにお目にかかれるなんて……アンタ、幸せものね♪", 605, 2, "", 2, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は中吉！　気になるあの人に会えちゃうかも！？\n『初夢を正夢にする枕』であなたに会え……な、なんでもない！", 606, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は中吉！　子供に戻って、一日中遊びつくそう！\nお年玉でけん玉ゲット！　おじちゃん、もしかめ一万回やって！", 607, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 1, 60006, "今日の運勢は中吉！　今日はおうちで、ゆーっくりくつろごう。\nお雑煮よし。みかんよし。さ、こたつで寝正月だ。君も入る？", 608, 2, "", 3, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は小吉！　突然のドッキリに注意！\nおや、後ろに髪の長い女の霊が──フフ、冗談です。私ですよ。", 609, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 0, "", 0, 2, 60006, "今日の運勢は小吉。慌てなくても、幸運はきっとやってくる。\n運は自分で掴むもの──媛が言ってました。でも、どうやって？", 610, 2, "", 4, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, 0, "", 190804, 0, 60006, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 611, 3, "カリン", 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, 0, "", 190804, 0, 60006, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 612, 3, "カリン", 0, 5);
