INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "カリン", 1, 60000, 1, 1, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 2, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 3, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60000, 2, 4, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 5, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 6, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60000, 2, 7, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 8, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 9, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 10, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 11, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60000, 2, 12, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 13, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 14, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 15, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60000, 2, 16, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60000, 2, 17, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "カリン", 0, 60000, 3, 18, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "カリン", 0, 60000, 3, 19, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ハツネ", 0, 80001, 0, 20, 1, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 101201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ハツネ", 0, 80001, 0, 21, 2, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 101201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "シオリ", 0, 80001, 1, 22, 1, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 103801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "シオリ", 0, 80001, 1, 23, 2, "来年も、\n一緒にいられたら…\nうれしいな", 0, 103801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キョウカ", 0, 80001, 0, 24, 1, "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 103601, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キョウカ", 0, 80001, 0, 25, 2, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 103601, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ミミ", 0, 80001, 1, 26, 1, "おにいちゃんとあってから、\n1年たったよ～！", 0, 102001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ミミ", 0, 80001, 1, 27, 2, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 102001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ミソギ", 0, 80001, 2, 28, 1, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 100401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ミソギ", 0, 80001, 2, 29, 2, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 100401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "マホ", 0, 80001, 0, 30, 1, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 101001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "マホ", 0, 80001, 0, 31, 2, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 101001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "イリヤ", 0, 80001, 1, 32, 1, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 104401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "イリヤ", 0, 80001, 1, 33, 2, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 104401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "カオリ", 0, 80001, 2, 34, 1, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 101701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "カオリ", 0, 80001, 2, 35, 2, "これからも、どうぞよろしく\nお願いするさ～！", 0, 101701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "サレン", 0, 80001, 0, 36, 1, "あんたと出会ってからもうそんなに\nたつのね～", 0, 102801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "サレン", 0, 80001, 0, 37, 2, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 102801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "スズメ", 0, 80001, 1, 38, 1, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 102501, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "スズメ", 0, 80001, 1, 39, 2, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 102501, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "タマキ", 0, 80001, 0, 40, 1, "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 104601, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "タマキ", 0, 80001, 0, 41, 2, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 104601, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ミフユ", 0, 80001, 1, 42, 1, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 104801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ミフユ", 0, 80001, 1, 43, 2, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 104801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "アキノ", 0, 80001, 0, 44, 1, "あなたさまと出会ってもう1年…", 0, 103201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "アキノ", 0, 80001, 0, 45, 2, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 103201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ユカリ", 0, 80001, 1, 46, 1, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 103401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ユカリ", 0, 80001, 1, 47, 2, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 103401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "クリスティーナ", 0, 80001, 0, 48, 1, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 107101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "クリスティーナ", 0, 80001, 0, 49, 2, "ともかくこれからも\n愉しませてくれよ☆", 0, 107101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "ジュン", 0, 80001, 1, 50, 1, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 104701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "ジュン", 0, 80001, 1, 51, 2, "見えないから分からないって？\nそれもそうだな…", 0, 104701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "トモ", 0, 80001, 0, 52, 1, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 103701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "トモ", 0, 80001, 0, 53, 2, "これからもよろしくね！", 0, 103701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "マツリ", 0, 80001, 1, 54, 1, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 100501, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "マツリ", 0, 80001, 1, 55, 2, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 100501, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ミヤコ", 0, 80001, 0, 56, 1, "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 100701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ミヤコ", 0, 80001, 0, 57, 2, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 100701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シノブ", 0, 80001, 1, 58, 1, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 103101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シノブ", 0, 80001, 1, 59, 2, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 103101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "アンナ", 0, 80001, 0, 60, 1, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 100901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "アンナ", 0, 80001, 0, 61, 2, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 100901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ナナカ", 0, 80001, 1, 62, 1, "君と出会ってもう1年だって！\n早いねー！", 0, 101301, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ナナカ", 0, 80001, 1, 63, 2, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 101301, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ミツキ", 0, 80001, 0, 64, 1, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 105101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ミツキ", 0, 80001, 0, 65, 2, "これからも、いろんなところを\n調べさせてね？", 0, 105101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ルカ", 0, 80001, 1, 66, 1, "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 105601, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ルカ", 0, 80001, 1, 67, 2, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 105601, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "エリコ", 0, 80001, 2, 68, 1, "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 102701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "エリコ", 0, 80001, 2, 69, 2, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 102701, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "アヤネ", 0, 80001, 0, 70, 1, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 102301, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "アヤネ", 0, 80001, 0, 71, 2, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 102301, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "チカ", 0, 80001, 1, 72, 1, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 104201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "チカ", 0, 80001, 1, 73, 2, "私のことを見守ってください\nできることなら、ずっと…", 0, 104201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "クルミ", 0, 80001, 2, 74, 1, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 102101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "クルミ", 0, 80001, 2, 75, 2, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 102101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "シズル", 0, 80001, 0, 76, 1, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 104901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "シズル", 0, 80001, 0, 77, 2, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 104901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "リノ", 0, 80001, 1, 78, 1, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 101101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "リノ", 0, 80001, 1, 79, 2, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 101101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ユイ", 0, 80001, 0, 80, 1, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 100201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ユイ", 0, 80001, 0, 81, 2, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 100201, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ヒヨリ", 0, 80001, 1, 82, 1, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 100101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ヒヨリ", 0, 80001, 1, 83, 2, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 100101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "レイ", 0, 80001, 2, 84, 1, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 100301, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "レイ", 0, 80001, 2, 85, 2, "キミに背中を預けられる日が、\n早くきますように", 0, 100301, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80001, 0, 86, 1, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80001, 0, 87, 2, "これからもずっと、覚えていて\nくださいね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80001, 1, 88, 1, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80001, 1, 89, 2, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80001, 2, 90, 1, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80001, 2, 91, 2, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "スズメ", 1, 60001, 1, 92, 1, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0, 112101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 93, 0, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 94, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 95, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 96, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60001, 2, 97, 0, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 98, 0, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 99, 0, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 100, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 101, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60001, 2, 102, 0, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 103, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 104, 0, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 105, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 106, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 107, 0, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 108, 0, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60001, 2, 109, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 110, 0, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 111, 0, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 112, 0, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 113, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 114, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60001, 2, 115, 0, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60001, 2, 116, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "スズメ", 0, 60001, 3, 117, 2, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 112101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "スズメ", 0, 60001, 3, 118, 3, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 112101, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80003, 0, 119, 1, "ヒューヒュー♪\nドンドンパフパフー♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80003, 1, 120, 1, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80003, 1, 121, 2, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "キャル", 0, 80003, 2, 122, 1, "え？　ちょっと\nいきなりなにっ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "キャル", 0, 80003, 2, 123, 2, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80003, 0, 124, 2, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80003, 0, 125, 3, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80003, 1, 126, 3, "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "キャル", 0, 80003, 2, 127, 3, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80003, 1, 128, 4, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80003, 0, 129, 4, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "キャル", 0, 80003, 2, 130, 4, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80003, 1, 131, 5, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 132, 5, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "カリン", 0, 80003, 1, 133, 1, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 134, 6, "げっ…カリンじゃない\nなんでもないわよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "カリン", 0, 80003, 1, 135, 2, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 136, 7, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "カリン", 0, 80003, 1, 137, 3, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 138, 8, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 139, 9, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "カリン", 0, 80003, 1, 140, 4, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 141, 10, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "カリン", 0, 80003, 1, 142, 5, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80003, 0, 143, 11, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80003, 0, 144, 6, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80003, 1, 145, 5, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80003, 0, 146, 7, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80003, 0, 147, 8, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80003, 1, 148, 6, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80003, 1, 149, 7, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80003, 0, 150, 9, "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80003, 1, 151, 8, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80003, 0, 152, 10, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80003, 1, 153, 9, "えへへ、ありがとうございます\nコッコロちゃん！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80003, 0, 154, 11, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80003, 1, 155, 10, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80003, 0, 156, 11, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80003, 1, 157, 12, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80003, 0, 158, 12, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80003, 1, 159, 13, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80003, 0, 160, 13, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80003, 1, 161, 14, "…積極的？\nなんの話よ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80003, 0, 162, 14, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80003, 0, 163, 15, "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80003, 1, 164, 15, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80003, 0, 165, 16, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80003, 1, 166, 16, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80003, 1, 167, 17, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80003, 0, 168, 18, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "コッコロ", 0, 80003, 1, 169, 12, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80003, 0, 170, 19, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "コッコロ", 0, 80003, 1, 171, 13, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80003, 0, 172, 20, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "コッコロ", 0, 80003, 1, 173, 14, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80003, 0, 174, 21, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "コッコロ", 0, 80003, 1, 175, 15, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80003, 0, 176, 22, "かくし芸？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "コッコロ", 0, 80003, 1, 177, 16, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80003, 0, 178, 23, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ペコリーヌ", 0, 80003, 0, 179, 17, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80003, 2, 180, 24, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "コッコロ", 0, 80003, 1, 181, 17, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ペコリーヌ", 0, 80003, 0, 182, 18, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80003, 2, 183, 25, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "コッコロ", 0, 80003, 1, 184, 18, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80003, 2, 185, 26, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80003, 2, 186, 27, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80003, 2, 187, 28, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ペコリーヌ", 0, 80003, 0, 188, 19, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "コッコロ", 0, 80003, 1, 189, 19, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80003, 2, 190, 29, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 191, 30, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 192, 31, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 193, 32, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 194, 33, "あー\nあー\n…声、これで大丈夫かしら？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 195, 34, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 196, 35, "オイッスー☆", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 197, 36, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 198, 37, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80003, 0, 199, 38, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 200, 20, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 201, 21, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 202, 22, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 203, 23, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 204, 24, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 205, 25, "オイッスー☆", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 206, 26, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 207, 27, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80003, 0, 208, 28, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 209, 20, "オイッスー☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 210, 21, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 211, 22, "あと６日で2.5周年ですか…", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 212, 23, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 213, 24, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 214, 25, "オイッスー☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 215, 26, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 216, 27, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 217, 28, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 218, 29, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "ペコリーヌ", 0, 80003, 0, 219, 30, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ペコリーヌ", 0, 80003, 0, 220, 31, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80003, 1, 221, 29, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ペコリーヌ", 0, 80003, 0, 222, 32, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "キャル", 0, 80003, 2, 223, 39, "…………", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80003, 1, 224, 30, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "キャル", 0, 80003, 2, 225, 40, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ペコリーヌ", 0, 80003, 0, 226, 33, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "キャル", 0, 80003, 2, 227, 41, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ペコリーヌ", 0, 80003, 0, 228, 34, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ペコリーヌ", 0, 80003, 0, 229, 35, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "キャル", 0, 80003, 2, 230, 42, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80003, 1, 231, 31, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "キャル", 0, 80003, 2, 232, 43, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "ペコリーヌ", 0, 80003, 0, 233, 36, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 234, 6, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 235, 7, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 236, 8, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 237, 9, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 238, 10, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 239, 11, "次にコッコロさんですが\nこちらは発声練習をしています", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 240, 12, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 241, 13, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 242, 14, "あれ？\nもしかして緊張で気を失って…？", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 243, 15, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 244, 16, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "カリン", 0, 80003, 0, 245, 17, "それでは\nまた明日お会いしましょう♪", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "キャル", 0, 80003, 0, 246, 44, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "ペコリーヌ", 0, 80003, 2, 247, 37, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "コッコロ", 0, 80003, 1, 248, 32, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "キャル", 0, 80003, 0, 249, 45, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "ペコリーヌ", 0, 80003, 2, 250, 38, "ひどいですよぉキャルちゃ～ん！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "コッコロ", 0, 80003, 1, 251, 33, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "ペコリーヌ", 0, 80003, 2, 252, 39, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "キャル", 0, 80003, 0, 253, 46, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "コッコロ", 0, 80003, 1, 254, 34, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "ペコリーヌ", 0, 80003, 2, 255, 40, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80003, 0, 256, 41, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 257, 47, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80003, 0, 258, 42, "忘れてきちゃいました☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 259, 48, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "コッコロ", 0, 80003, 1, 260, 35, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 261, 49, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80003, 0, 262, 43, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 263, 50, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "コッコロ", 0, 80003, 1, 264, 36, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 265, 51, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "コッコロ", 0, 80003, 1, 266, 37, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 267, 52, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80003, 0, 268, 44, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "コッコロ", 0, 80003, 1, 269, 38, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80003, 0, 270, 45, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80003, 2, 271, 53, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80003, 0, 272, 46, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 273, 18, "こんにちは\n【ギルド管理協会】のカリンです", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 274, 19, "昨日の生放送は\nいかがだったでしょうか？", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 275, 20, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 276, 21, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 277, 22, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 278, 23, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 279, 24, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 280, 25, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 281, 26, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "カリン", 0, 80003, 0, 282, 27, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 283, 47, "準備はいいですかっ？\n…いっきますよー！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 284, 54, "祝！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80003, 1, 285, 39, "2.5周年！", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 286, 48, "おめでとうございま～す☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 287, 55, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80003, 1, 288, 40, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 289, 49, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 290, 56, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80003, 1, 291, 41, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 292, 57, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 293, 50, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 294, 58, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 295, 51, "記念日だから\n平気平気♪\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 296, 59, "意味分かんないわよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80003, 1, 297, 42, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80003, 1, 298, 43, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 299, 52, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80003, 2, 300, 60, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80003, 1, 301, 44, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80003, 0, 302, 53, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "105901,45,106001,61", "ペコリーヌ・コッコロ・キャル", 0, 80003, 0, 303, 54, "2.5周年\nありがとうございます！", 0, 105801, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "カリン", 1, 60002, 1, 304, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 305, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 306, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 307, 0, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 308, 0, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60002, 2, 309, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 310, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 311, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 312, 0, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 313, 0, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60002, 2, 314, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 315, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 316, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 317, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 318, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 319, 0, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 320, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60002, 2, 321, 0, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 322, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 323, 0, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 324, 0, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 325, 0, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 326, 0, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60002, 2, 327, 0, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60002, 2, 328, 0, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 4, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "カリン", 0, 60002, 3, 329, 2, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "カリン", 0, 60002, 3, 330, 3, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80005, 4, 331, 1, "ささ、シェフィさまはこちらへ…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "シェフィ", 0, 80005, 0, 332, 1, "コッコロさん、どこへ行くの…？\n…え？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80005, 3, 333, 1, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "コッコロ", 0, 80005, 4, 334, 2, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "シェフィ", 0, 80005, 0, 335, 2, "ええと…\n二人とも何をしているの？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "キャル", 0, 80005, 1, 336, 1, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "シェフィ", 0, 80005, 0, 337, 3, "あの日…？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80005, 3, 338, 2, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80005, 3, 339, 3, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "カリン", 0, 80005, 2, 340, 1, "やばいですね☆", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "キャル", 0, 80005, 1, 341, 2, "って、なにしれっと\n参加してんのよカリン！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "カリン", 0, 80005, 2, 342, 2, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "カリン", 0, 80005, 2, 343, 3, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "ペコリーヌ", 0, 80005, 3, 344, 4, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (1, "", "シェフィ", 0, 80005, 0, 345, 4, "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ペコリーヌ", 0, 80005, 3, 346, 5, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "コッコロ", 0, 80005, 1, 347, 3, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "コッコロ", 0, 80005, 1, 348, 4, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ペコリーヌ", 0, 80005, 3, 349, 6, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "コッコロ", 0, 80005, 1, 350, 5, "さすがでございます\nペコリーヌさま", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80005, 0, 351, 3, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "シェフィ", 0, 80005, 2, 352, 5, "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "ペコリーヌ", 0, 80005, 3, 353, 7, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "コッコロ", 0, 80005, 1, 354, 6, "さ、次はキャルさまの\n番でございますよ", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80005, 0, 355, 4, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "シェフィ", 0, 80005, 2, 356, 6, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80005, 0, 357, 5, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80005, 0, 358, 6, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "コッコロ", 0, 80005, 1, 359, 7, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (2, "", "キャル", 0, 80005, 0, 360, 7, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80005, 1, 361, 8, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80005, 3, 362, 8, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80005, 1, 363, 9, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "シェフィ", 0, 80005, 2, 364, 7, "聖学祭…って、なにかしら？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "コッコロ", 0, 80005, 1, 365, 10, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "キャル", 0, 80005, 0, 366, 8, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "キャル", 0, 80005, 0, 367, 9, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80005, 3, 368, 9, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "キャル", 0, 80005, 0, 369, 10, "いや、そのまんますぎでしょ…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "シェフィ", 0, 80005, 2, 370, 8, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "ペコリーヌ", 0, 80005, 3, 371, 10, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (3, "", "シェフィ", 0, 80005, 2, 372, 9, "えっ？　ええ…\nぎゅーっ…？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80005, 0, 373, 11, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "シェフィ", 0, 80005, 2, 374, 10, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80005, 0, 375, 12, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "コッコロ", 0, 80005, 1, 376, 11, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80005, 0, 377, 13, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80005, 3, 378, 11, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80005, 0, 379, 14, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80005, 0, 380, 15, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "シェフィ", 0, 80005, 2, 381, 11, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "ペコリーヌ", 0, 80005, 3, 382, 12, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "コッコロ", 0, 80005, 1, 383, 12, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "キャル", 0, 80005, 0, 384, 16, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (4, "", "シェフィ", 0, 80005, 2, 385, 12, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 386, 13, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 387, 14, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 388, 15, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80005, 0, 389, 17, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80005, 0, 390, 18, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "シェフィ", 0, 80005, 2, 391, 13, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "コッコロ", 0, 80005, 1, 392, 13, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 393, 16, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "キャル", 0, 80005, 0, 394, 19, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 395, 17, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 396, 18, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (5, "", "ペコリーヌ", 0, 80005, 3, 397, 19, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "カリン", 0, 80005, 2, 398, 4, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "カリン", 0, 80005, 2, 399, 5, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80005, 1, 400, 20, "…えっ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ペコリーヌ", 0, 80005, 3, 401, 20, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "シェフィ", 0, 80005, 0, 402, 14, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "コッコロ", 0, 80005, 4, 403, 14, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "シェフィ", 0, 80005, 0, 404, 15, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80005, 1, 405, 21, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "ペコリーヌ", 0, 80005, 3, 406, 21, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "キャル", 0, 80005, 1, 407, 22, "ぶっ殺すぞ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (6, "", "カリン", 0, 80005, 2, 408, 6, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "コッコロ", 0, 80005, 1, 409, 15, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80005, 0, 410, 23, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "シェフィ", 0, 80005, 2, 411, 16, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "ペコリーヌ", 0, 80005, 3, 412, 22, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80005, 0, 413, 24, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "シェフィ", 0, 80005, 2, 414, 17, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "コッコロ", 0, 80005, 1, 415, 16, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "シェフィ", 0, 80005, 2, 416, 18, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "コッコロ", 0, 80005, 1, 417, 17, "もちろんでございます\n仲良くお勉強いたしましょう", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80005, 0, 418, 25, "しれっと教育ママが\n生まれてる…？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "ペコリーヌ", 0, 80005, 3, 419, 23, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (7, "", "キャル", 0, 80005, 0, 420, 26, "え～～～～！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "キャル", 0, 80005, 0, 421, 27, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "シェフィ", 0, 80005, 2, 422, 19, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80005, 1, 423, 18, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "ペコリーヌ", 0, 80005, 3, 424, 24, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "シェフィ", 0, 80005, 2, 425, 20, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "キャル", 0, 80005, 0, 426, 28, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "コッコロ", 0, 80005, 1, 427, 19, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "シェフィ", 0, 80005, 2, 428, 21, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "キャル", 0, 80005, 0, 429, 29, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "キャル", 0, 80005, 0, 430, 30, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "ペコリーヌ", 0, 80005, 3, 431, 25, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (8, "", "キャル", 0, 80005, 0, 432, 31, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 433, 22, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 434, 23, "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 435, 24, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 436, 25, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 437, 26, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 438, 27, "それから、それから…！\nぅ、ぅぅっ…！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 439, 28, "―びえぇぇ～んっ！！！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "コッコロ", 0, 80005, 1, 440, 20, "どうされましたか、シェフィさま", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 441, 29, "ううっ…ひっく…\nママッ…！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "コッコロ", 0, 80005, 1, 442, 21, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "コッコロ", 0, 80005, 1, 443, 22, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (9, "", "シェフィ", 0, 80005, 0, 444, 30, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 445, 23, "主さま\n3.5周年まであと５日\nでございます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 446, 24, "ですがその前に\n生放送がありますね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 447, 25, "…生放送に向けた\n意気込みでございますか？", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 448, 26, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 449, 27, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 450, 28, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 451, 29, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 452, 30, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (10, "", "コッコロ", 0, 80005, 0, 453, 31, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 454, 26, "3.5周年まであと４日！\nやばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "キャル", 0, 80005, 1, 455, 32, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 456, 27, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 457, 28, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 458, 29, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "キャル", 0, 80005, 1, 459, 33, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 460, 30, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 461, 31, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 462, 32, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "キャル", 0, 80005, 1, 463, 34, "だからあたしはやるなんて一言も…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "ペコリーヌ", 0, 80005, 0, 464, 33, "オイッスー☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (11, "", "キャル", 0, 80005, 1, 465, 35, "聞きなさいっての！！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "シェフィ", 0, 80005, 2, 466, 31, "3.5周年まであと３日…\nそして生放送は、明日…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "シェフィ", 0, 80005, 2, 467, 32, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "シェフィ", 0, 80005, 2, 468, 33, "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "シェフィ", 0, 80005, 2, 469, 34, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "シェフィ", 0, 80005, 2, 470, 35, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "ペコリーヌ", 0, 80005, 3, 471, 34, "やってますねシェフィちゃん！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "キャル", 0, 80005, 0, 472, 36, "なによ、考えることは\nみんな同じってこと？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "コッコロ", 0, 80005, 1, 473, 32, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "シェフィ", 0, 80005, 2, 474, 36, "みんな…もちろんよ！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (12, "", "ペコリーヌ", 0, 80005, 3, 475, 35, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "カリン", 0, 80005, 2, 476, 7, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "カリン", 0, 80005, 2, 477, 8, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80005, 3, 478, 36, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "カリン", 0, 80005, 2, 479, 9, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "コッコロ", 0, 80005, 4, 480, 33, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "カリン", 0, 80005, 2, 481, 10, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80005, 1, 482, 37, "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "カリン", 0, 80005, 2, 483, 11, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "シェフィ", 0, 80005, 0, 484, 37, "あうー？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "105901,34,106001,38,190801,12", "みんな", 0, 80005, 4, 485, 37, "あ…", 0, 105801, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "キャル", 0, 80005, 1, 486, 39, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "コッコロ", 0, 80005, 4, 487, 35, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "ペコリーヌ", 0, 80005, 3, 488, 38, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (13, "", "カリン", 0, 80005, 2, 489, 13, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "シェフィ", 0, 80005, 2, 490, 38, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ペコリーヌ", 0, 80005, 3, 491, 39, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "キャル", 0, 80005, 0, 492, 40, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "コッコロ", 0, 80005, 1, 493, 36, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "シェフィ", 0, 80005, 2, 494, 39, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ペコリーヌ", 0, 80005, 3, 495, 40, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "コッコロ", 0, 80005, 1, 496, 37, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "キャル", 0, 80005, 0, 497, 41, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "シェフィ", 0, 80005, 2, 498, 40, "へ、へぇ…", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "ペコリーヌ", 0, 80005, 3, 499, 41, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "シェフィ", 0, 80005, 2, 500, 41, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (14, "", "シェフィ", 0, 80005, 2, 501, 42, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80005, 3, 502, 42, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80005, 1, 503, 42, "祝！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80005, 4, 504, 38, "3.5周年！", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "シェフィ", 0, 80005, 0, 505, 43, "おめでとうございます！", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "カリン", 0, 80005, 2, 506, 14, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "カリン", 0, 80005, 2, 507, 15, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 190801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80005, 3, 508, 43, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80005, 3, 509, 44, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80005, 1, 510, 43, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "キャル", 0, 80005, 1, 511, 44, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 106001, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "シェフィ", 0, 80005, 0, 512, 44, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "シェフィ", 0, 80005, 0, 513, 45, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 106401, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80005, 4, 514, 39, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80005, 4, 515, 40, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "コッコロ", 0, 80005, 4, 516, 41, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 105901, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "", "ペコリーヌ", 0, 80005, 3, 517, 45, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 105801, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (15, "105901,42,106001,45,106401,46,190801,16", "みんな", 0, 80005, 4, 518, 46, "これからも\nよろしくおねがいします！", 0, 105801, 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "カリン", 1, 60003, 1, 519, 1, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0, 190804, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 520, 0, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 521, 0, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 522, 0, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 523, 0, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60003, 2, 524, 0, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 1, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 525, 0, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 526, 0, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 527, 0, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 528, 0, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60003, 2, 529, 0, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 530, 0, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 531, 0, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 532, 0, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 533, 0, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 534, 0, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 535, 0, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 2, 60003, 2, 536, 0, "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 3, 0, 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, "", "", 1, 60003, 2, 537, 0, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 4, 0, 0);
