INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 1, "", 0, 1, 0, "カリン", 1, 1, 60000, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 2, 0, "", 0, 1, 60000, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 3, 0, "", 0, 1, 60000, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 4, 0, "", 0, 2, 60000, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 5, 0, "", 0, 1, 60000, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 6, 0, "", 0, 1, 60000, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 7, 0, "", 0, 2, 60000, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 8, 0, "", 0, 1, 60000, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 9, 0, "", 0, 1, 60000, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 10, 0, "", 0, 1, 60000, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 11, 0, "", 0, 1, 60000, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 12, 0, "", 0, 2, 60000, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 13, 0, "", 0, 1, 60000, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 14, 0, "", 0, 1, 60000, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 15, 0, "", 0, 1, 60000, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 16, 0, "", 0, 1, 60000, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 17, 0, "", 0, 2, 60000, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 3, "", 0, 18, 0, "カリン", 2, 0, 60000, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 3, "", 0, 19, 0, "カリン", 3, 0, 60000, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101201, 0, "", 0, 20, 0, "ハツネ", 1, 0, 80001, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101201, 0, "", 0, 21, 0, "ハツネ", 2, 0, 80001, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103801, 1, "", 0, 22, 0, "シオリ", 1, 0, 80001, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103801, 1, "", 0, 23, 0, "シオリ", 2, 0, 80001, "来年も、\n一緒にいられたら…\nうれしいな", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103601, 0, "", 0, 24, 0, "キョウカ", 1, 0, 80001, "あなたと一緒にいるようになって、\nもう1年ですか…", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103601, 0, "", 0, 25, 0, "キョウカ", 2, 0, 80001, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102001, 1, "", 0, 26, 0, "ミミ", 1, 0, 80001, "おにいちゃんとあってから、\n1年たったよ～！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102001, 1, "", 0, 27, 0, "ミミ", 2, 0, 80001, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100401, 2, "", 0, 28, 0, "ミソギ", 1, 0, 80001, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100401, 2, "", 0, 29, 0, "ミソギ", 2, 0, 80001, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101001, 0, "", 0, 30, 0, "マホ", 1, 0, 80001, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101001, 0, "", 0, 31, 0, "マホ", 2, 0, 80001, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104401, 1, "", 0, 32, 0, "イリヤ", 1, 0, 80001, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104401, 1, "", 0, 33, 0, "イリヤ", 2, 0, 80001, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101701, 2, "", 0, 34, 0, "カオリ", 1, 0, 80001, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101701, 2, "", 0, 35, 0, "カオリ", 2, 0, 80001, "これからも、どうぞよろしく\nお願いするさ～！", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102801, 0, "", 0, 36, 0, "サレン", 1, 0, 80001, "あんたと出会ってからもうそんなに\nたつのね～", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102801, 0, "", 0, 37, 0, "サレン", 2, 0, 80001, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102501, 1, "", 0, 38, 0, "スズメ", 1, 0, 80001, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102501, 1, "", 0, 39, 0, "スズメ", 2, 0, 80001, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104601, 0, "", 0, 40, 0, "タマキ", 1, 0, 80001, "振り返れば、今までいろんなことが\nあったにゃあ…", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104601, 0, "", 0, 41, 0, "タマキ", 2, 0, 80001, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104801, 1, "", 0, 42, 0, "ミフユ", 1, 0, 80001, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104801, 1, "", 0, 43, 0, "ミフユ", 2, 0, 80001, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103201, 0, "", 0, 44, 0, "アキノ", 1, 0, 80001, "あなたさまと出会ってもう1年…", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103201, 0, "", 0, 45, 0, "アキノ", 2, 0, 80001, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103401, 1, "", 0, 46, 0, "ユカリ", 1, 0, 80001, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103401, 1, "", 0, 47, 0, "ユカリ", 2, 0, 80001, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (107101, 0, "", 0, 48, 0, "クリスティーナ", 1, 0, 80001, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (107101, 0, "", 0, 49, 0, "クリスティーナ", 2, 0, 80001, "ともかくこれからも\n愉しませてくれよ☆", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104701, 1, "", 0, 50, 0, "ジュン", 1, 0, 80001, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104701, 1, "", 0, 51, 0, "ジュン", 2, 0, 80001, "見えないから分からないって？\nそれもそうだな…", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103701, 0, "", 0, 52, 0, "トモ", 1, 0, 80001, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103701, 0, "", 0, 53, 0, "トモ", 2, 0, 80001, "これからもよろしくね！", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100501, 1, "", 0, 54, 0, "マツリ", 1, 0, 80001, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100501, 1, "", 0, 55, 0, "マツリ", 2, 0, 80001, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100701, 0, "", 0, 56, 0, "ミヤコ", 1, 0, 80001, "オマエと会って1年なの！\n全然成仏もできなかったし、", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100701, 0, "", 0, 57, 0, "ミヤコ", 2, 0, 80001, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103101, 1, "", 0, 58, 0, "シノブ", 1, 0, 80001, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (103101, 1, "", 0, 59, 0, "シノブ", 2, 0, 80001, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100901, 0, "", 0, 60, 0, "アンナ", 1, 0, 80001, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100901, 0, "", 0, 61, 0, "アンナ", 2, 0, 80001, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101301, 1, "", 0, 62, 0, "ナナカ", 1, 0, 80001, "君と出会ってもう1年だって！\n早いねー！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101301, 1, "", 0, 63, 0, "ナナカ", 2, 0, 80001, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105101, 0, "", 0, 64, 0, "ミツキ", 1, 0, 80001, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105101, 0, "", 0, 65, 0, "ミツキ", 2, 0, 80001, "これからも、いろんなところを\n調べさせてね？", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105601, 1, "", 0, 66, 0, "ルカ", 1, 0, 80001, "お前さんと出会ってもう1年か\n助太刀するつもりが", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105601, 1, "", 0, 67, 0, "ルカ", 2, 0, 80001, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102701, 2, "", 0, 68, 0, "エリコ", 1, 0, 80001, "早いものですわね、あなた様と\nお会いして、もう1年…", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102701, 2, "", 0, 69, 0, "エリコ", 2, 0, 80001, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102301, 0, "", 0, 70, 0, "アヤネ", 1, 0, 80001, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102301, 0, "", 0, 71, 0, "アヤネ", 2, 0, 80001, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104201, 1, "", 0, 72, 0, "チカ", 1, 0, 80001, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104201, 1, "", 0, 73, 0, "チカ", 2, 0, 80001, "私のことを見守ってください\nできることなら、ずっと…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102101, 2, "", 0, 74, 0, "クルミ", 1, 0, 80001, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (102101, 2, "", 0, 75, 0, "クルミ", 2, 0, 80001, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104901, 0, "", 0, 76, 0, "シズル", 1, 0, 80001, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (104901, 0, "", 0, 77, 0, "シズル", 2, 0, 80001, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101101, 1, "", 0, 78, 0, "リノ", 1, 0, 80001, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (101101, 1, "", 0, 79, 0, "リノ", 2, 0, 80001, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100201, 0, "", 0, 80, 0, "ユイ", 1, 0, 80001, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100201, 0, "", 0, 81, 0, "ユイ", 2, 0, 80001, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100101, 1, "", 0, 82, 0, "ヒヨリ", 1, 0, 80001, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100101, 1, "", 0, 83, 0, "ヒヨリ", 2, 0, 80001, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100301, 2, "", 0, 84, 0, "レイ", 1, 0, 80001, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (100301, 2, "", 0, 85, 0, "レイ", 2, 0, 80001, "キミに背中を預けられる日が、\n早くきますように", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 86, 0, "ペコリーヌ", 1, 0, 80001, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 87, 0, "ペコリーヌ", 2, 0, 80001, "これからもずっと、覚えていて\nくださいね！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 88, 0, "コッコロ", 1, 0, 80001, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 89, 0, "コッコロ", 2, 0, 80001, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 90, 0, "キャル", 1, 0, 80001, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 91, 0, "キャル", 2, 0, 80001, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (112101, 1, "", 0, 92, 0, "スズメ", 1, 1, 60001, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 93, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 94, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 95, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 96, 0, "", 0, 1, 60001, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 97, 0, "", 0, 2, 60001, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 98, 0, "", 0, 1, 60001, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 99, 0, "", 0, 1, 60001, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 100, 0, "", 0, 1, 60001, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 101, 0, "", 0, 1, 60001, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 102, 0, "", 0, 2, 60001, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 103, 0, "", 0, 1, 60001, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 104, 0, "", 0, 1, 60001, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 105, 0, "", 0, 1, 60001, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 106, 0, "", 0, 1, 60001, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 107, 0, "", 0, 1, 60001, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 108, 0, "", 0, 1, 60001, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 109, 0, "", 0, 2, 60001, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 110, 0, "", 0, 1, 60001, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 111, 0, "", 0, 1, 60001, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 112, 0, "", 0, 1, 60001, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 113, 0, "", 0, 1, 60001, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 114, 0, "", 0, 1, 60001, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 115, 0, "", 0, 1, 60001, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 116, 0, "", 0, 2, 60001, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (112101, 3, "", 0, 117, 0, "スズメ", 2, 0, 60001, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (112101, 3, "", 0, 118, 0, "スズメ", 3, 0, 60001, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 119, 0, "ペコリーヌ", 1, 0, 80003, "ヒューヒュー♪\nドンドンパフパフー♪", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 120, 0, "コッコロ", 1, 0, 80003, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 121, 0, "コッコロ", 2, 0, 80003, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 122, 0, "キャル", 1, 0, 80003, "え？　ちょっと\nいきなりなにっ？", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 123, 0, "キャル", 2, 0, 80003, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 124, 0, "ペコリーヌ", 2, 0, 80003, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 125, 0, "ペコリーヌ", 3, 0, 80003, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 126, 0, "コッコロ", 3, 0, 80003, "ですからその日に向けて\n今から気分を盛り上げているのです", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 127, 0, "キャル", 3, 0, 80003, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 128, 0, "コッコロ", 4, 0, 80003, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 129, 0, "ペコリーヌ", 4, 0, 80003, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 130, 0, "キャル", 4, 0, 80003, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 131, 0, "コッコロ", 5, 0, 80003, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 132, 0, "キャル", 5, 0, 80003, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 1, "", 0, 133, 0, "カリン", 1, 0, 80003, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 134, 0, "キャル", 6, 0, 80003, "げっ…カリンじゃない\nなんでもないわよ！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 1, "", 0, 135, 0, "カリン", 2, 0, 80003, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 136, 0, "キャル", 7, 0, 80003, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 1, "", 0, 137, 0, "カリン", 3, 0, 80003, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 138, 0, "キャル", 8, 0, 80003, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 139, 0, "キャル", 9, 0, 80003, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 1, "", 0, 140, 0, "カリン", 4, 0, 80003, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 141, 0, "キャル", 10, 0, 80003, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 1, "", 0, 142, 0, "カリン", 5, 0, 80003, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 143, 0, "キャル", 11, 0, 80003, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 144, 0, "コッコロ", 6, 0, 80003, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 1, "", 0, 145, 0, "ペコリーヌ", 5, 0, 80003, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 146, 0, "コッコロ", 7, 0, 80003, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 147, 0, "コッコロ", 8, 0, 80003, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 1, "", 0, 148, 0, "ペコリーヌ", 6, 0, 80003, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 1, "", 0, 149, 0, "ペコリーヌ", 7, 0, 80003, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 150, 0, "コッコロ", 9, 0, 80003, "あとは、牧場に素敵な温泉が\n出来たとか…", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 1, "", 0, 151, 0, "ペコリーヌ", 8, 0, 80003, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 152, 0, "コッコロ", 10, 0, 80003, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 1, "", 0, 153, 0, "ペコリーヌ", 9, 0, 80003, "えへへ、ありがとうございます\nコッコロちゃん！", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 154, 0, "コッコロ", 11, 0, 80003, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 1, "", 0, 155, 0, "ペコリーヌ", 10, 0, 80003, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 156, 0, "ペコリーヌ", 11, 0, 80003, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 157, 0, "キャル", 12, 0, 80003, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 158, 0, "ペコリーヌ", 12, 0, 80003, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 159, 0, "キャル", 13, 0, 80003, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 160, 0, "ペコリーヌ", 13, 0, 80003, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 161, 0, "キャル", 14, 0, 80003, "…積極的？\nなんの話よ？", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 162, 0, "ペコリーヌ", 14, 0, 80003, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 163, 0, "ペコリーヌ", 15, 0, 80003, "ムーサの町で\nきゃるきゃる～ん☆　って！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 164, 0, "キャル", 15, 0, 80003, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 165, 0, "ペコリーヌ", 16, 0, 80003, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 166, 0, "キャル", 16, 0, 80003, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 167, 0, "キャル", 17, 0, 80003, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 168, 0, "キャル", 18, 0, 80003, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 169, 0, "コッコロ", 12, 0, 80003, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 170, 0, "キャル", 19, 0, 80003, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 171, 0, "コッコロ", 13, 0, 80003, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 172, 0, "キャル", 20, 0, 80003, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 173, 0, "コッコロ", 14, 0, 80003, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 174, 0, "キャル", 21, 0, 80003, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 175, 0, "コッコロ", 15, 0, 80003, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 176, 0, "キャル", 22, 0, 80003, "かくし芸？", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 177, 0, "コッコロ", 16, 0, 80003, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 178, 0, "キャル", 23, 0, 80003, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 179, 0, "ペコリーヌ", 17, 0, 80003, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 180, 0, "キャル", 24, 0, 80003, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 181, 0, "コッコロ", 17, 0, 80003, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 182, 0, "ペコリーヌ", 18, 0, 80003, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 183, 0, "キャル", 25, 0, 80003, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 184, 0, "コッコロ", 18, 0, 80003, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 185, 0, "キャル", 26, 0, 80003, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 186, 0, "キャル", 27, 0, 80003, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 187, 0, "キャル", 28, 0, 80003, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 188, 0, "ペコリーヌ", 19, 0, 80003, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 189, 0, "コッコロ", 19, 0, 80003, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 190, 0, "キャル", 29, 0, 80003, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 191, 0, "キャル", 30, 0, 80003, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 192, 0, "キャル", 31, 0, 80003, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 193, 0, "キャル", 32, 0, 80003, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 194, 0, "キャル", 33, 0, 80003, "あー\nあー\n…声、これで大丈夫かしら？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 195, 0, "キャル", 34, 0, 80003, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 196, 0, "キャル", 35, 0, 80003, "オイッスー☆", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 197, 0, "キャル", 36, 0, 80003, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 198, 0, "キャル", 37, 0, 80003, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 199, 0, "キャル", 38, 0, 80003, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 200, 0, "コッコロ", 20, 0, 80003, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 201, 0, "コッコロ", 21, 0, 80003, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 202, 0, "コッコロ", 22, 0, 80003, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 203, 0, "コッコロ", 23, 0, 80003, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 204, 0, "コッコロ", 24, 0, 80003, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 205, 0, "コッコロ", 25, 0, 80003, "オイッスー☆", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 206, 0, "コッコロ", 26, 0, 80003, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 207, 0, "コッコロ", 27, 0, 80003, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 208, 0, "コッコロ", 28, 0, 80003, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 209, 0, "ペコリーヌ", 20, 0, 80003, "オイッスー☆", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 210, 0, "ペコリーヌ", 21, 0, 80003, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 211, 0, "ペコリーヌ", 22, 0, 80003, "あと６日で2.5周年ですか…", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 212, 0, "ペコリーヌ", 23, 0, 80003, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 213, 0, "ペコリーヌ", 24, 0, 80003, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 214, 0, "ペコリーヌ", 25, 0, 80003, "オイッスー☆", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 215, 0, "ペコリーヌ", 26, 0, 80003, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 216, 0, "ペコリーヌ", 27, 0, 80003, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 217, 0, "ペコリーヌ", 28, 0, 80003, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 218, 0, "ペコリーヌ", 29, 0, 80003, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 219, 0, "ペコリーヌ", 30, 0, 80003, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 220, 0, "ペコリーヌ", 31, 0, 80003, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 221, 0, "コッコロ", 29, 0, 80003, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 222, 0, "ペコリーヌ", 32, 0, 80003, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 223, 0, "キャル", 39, 0, 80003, "…………", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 224, 0, "コッコロ", 30, 0, 80003, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 225, 0, "キャル", 40, 0, 80003, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 226, 0, "ペコリーヌ", 33, 0, 80003, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 227, 0, "キャル", 41, 0, 80003, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 228, 0, "ペコリーヌ", 34, 0, 80003, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 229, 0, "ペコリーヌ", 35, 0, 80003, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 230, 0, "キャル", 42, 0, 80003, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 231, 0, "コッコロ", 31, 0, 80003, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 232, 0, "キャル", 43, 0, 80003, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 233, 0, "ペコリーヌ", 36, 0, 80003, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 234, 0, "カリン", 6, 0, 80003, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 235, 0, "カリン", 7, 0, 80003, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 236, 0, "カリン", 8, 0, 80003, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 237, 0, "カリン", 9, 0, 80003, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 238, 0, "カリン", 10, 0, 80003, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 239, 0, "カリン", 11, 0, 80003, "次にコッコロさんですが\nこちらは発声練習をしています", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 240, 0, "カリン", 12, 0, 80003, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 241, 0, "カリン", 13, 0, 80003, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 242, 0, "カリン", 14, 0, 80003, "あれ？\nもしかして緊張で気を失って…？", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 243, 0, "カリン", 15, 0, 80003, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 244, 0, "カリン", 16, 0, 80003, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 245, 0, "カリン", 17, 0, 80003, "それでは\nまた明日お会いしましょう♪", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 246, 0, "キャル", 44, 0, 80003, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 2, "", 0, 247, 0, "ペコリーヌ", 37, 0, 80003, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 248, 0, "コッコロ", 32, 0, 80003, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 249, 0, "キャル", 45, 0, 80003, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 2, "", 0, 250, 0, "ペコリーヌ", 38, 0, 80003, "ひどいですよぉキャルちゃ～ん！", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 251, 0, "コッコロ", 33, 0, 80003, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 2, "", 0, 252, 0, "ペコリーヌ", 39, 0, 80003, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 253, 0, "キャル", 46, 0, 80003, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 254, 0, "コッコロ", 34, 0, 80003, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 2, "", 0, 255, 0, "ペコリーヌ", 40, 0, 80003, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 256, 0, "ペコリーヌ", 41, 0, 80003, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 257, 0, "キャル", 47, 0, 80003, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 258, 0, "ペコリーヌ", 42, 0, 80003, "忘れてきちゃいました☆", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 259, 0, "キャル", 48, 0, 80003, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 260, 0, "コッコロ", 35, 0, 80003, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 261, 0, "キャル", 49, 0, 80003, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 262, 0, "ペコリーヌ", 43, 0, 80003, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 263, 0, "キャル", 50, 0, 80003, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 264, 0, "コッコロ", 36, 0, 80003, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 265, 0, "キャル", 51, 0, 80003, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 266, 0, "コッコロ", 37, 0, 80003, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 267, 0, "キャル", 52, 0, 80003, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 268, 0, "ペコリーヌ", 44, 0, 80003, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 269, 0, "コッコロ", 38, 0, 80003, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 270, 0, "ペコリーヌ", 45, 0, 80003, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 271, 0, "キャル", 53, 0, 80003, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 272, 0, "ペコリーヌ", 46, 0, 80003, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 273, 0, "カリン", 18, 0, 80003, "こんにちは\n【ギルド管理協会】のカリンです", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 274, 0, "カリン", 19, 0, 80003, "昨日の生放送は\nいかがだったでしょうか？", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 275, 0, "カリン", 20, 0, 80003, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 276, 0, "カリン", 21, 0, 80003, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 277, 0, "カリン", 22, 0, 80003, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 278, 0, "カリン", 23, 0, 80003, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 279, 0, "カリン", 24, 0, 80003, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 280, 0, "カリン", 25, 0, 80003, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 281, 0, "カリン", 26, 0, 80003, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 0, "", 0, 282, 0, "カリン", 27, 0, 80003, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 283, 0, "ペコリーヌ", 47, 0, 80003, "準備はいいですかっ？\n…いっきますよー！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 284, 0, "キャル", 54, 0, 80003, "祝！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 285, 0, "コッコロ", 39, 0, 80003, "2.5周年！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 286, 0, "ペコリーヌ", 48, 0, 80003, "おめでとうございま～す☆", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 287, 0, "キャル", 55, 0, 80003, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 288, 0, "コッコロ", 40, 0, 80003, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 289, 0, "ペコリーヌ", 49, 0, 80003, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 290, 0, "キャル", 56, 0, 80003, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 291, 0, "コッコロ", 41, 0, 80003, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 292, 0, "キャル", 57, 0, 80003, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 293, 0, "ペコリーヌ", 50, 0, 80003, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 294, 0, "キャル", 58, 0, 80003, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 295, 0, "ペコリーヌ", 51, 0, 80003, "記念日だから\n平気平気♪\nやばいですね☆", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 296, 0, "キャル", 59, 0, 80003, "意味分かんないわよ！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 297, 0, "コッコロ", 42, 0, 80003, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 298, 0, "コッコロ", 43, 0, 80003, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 299, 0, "ペコリーヌ", 52, 0, 80003, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 2, "", 0, 300, 0, "キャル", 60, 0, 80003, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 301, 0, "コッコロ", 44, 0, 80003, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 302, 0, "ペコリーヌ", 53, 0, 80003, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "105901,45,106001,61", 0, 303, 1, "ペコリーヌ・コッコロ・キャル", 54, 0, 80003, "2.5周年\nありがとうございます！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 1, "", 0, 304, 0, "カリン", 1, 1, 60002, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 305, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 306, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 307, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 308, 0, "", 0, 1, 60002, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 309, 0, "", 0, 2, 60002, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 310, 0, "", 0, 1, 60002, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 311, 0, "", 0, 1, 60002, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 312, 0, "", 0, 1, 60002, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 313, 0, "", 0, 1, 60002, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 314, 0, "", 0, 2, 60002, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 315, 0, "", 0, 1, 60002, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 316, 0, "", 0, 1, 60002, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 317, 0, "", 0, 1, 60002, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 318, 0, "", 0, 1, 60002, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 319, 0, "", 0, 1, 60002, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 320, 0, "", 0, 1, 60002, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 321, 0, "", 0, 2, 60002, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 322, 0, "", 0, 1, 60002, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 323, 0, "", 0, 1, 60002, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 324, 0, "", 0, 1, 60002, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 325, 0, "", 0, 1, 60002, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 326, 0, "", 0, 1, 60002, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 327, 0, "", 0, 1, 60002, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 4, 328, 0, "", 0, 2, 60002, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 3, "", 0, 329, 0, "カリン", 2, 0, 60002, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 3, "", 0, 330, 0, "カリン", 3, 0, 60002, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 331, 0, "コッコロ", 1, 0, 80005, "ささ、シェフィさまはこちらへ…", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 332, 0, "シェフィ", 1, 0, 80005, "コッコロさん、どこへ行くの…？\n…え？", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 333, 0, "ペコリーヌ", 1, 0, 80005, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 334, 0, "コッコロ", 2, 0, 80005, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 335, 0, "シェフィ", 2, 0, 80005, "ええと…\n二人とも何をしているの？", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 336, 0, "キャル", 1, 0, 80005, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 337, 0, "シェフィ", 3, 0, 80005, "あの日…？", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 338, 0, "ペコリーヌ", 2, 0, 80005, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 339, 0, "ペコリーヌ", 3, 0, 80005, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 340, 0, "カリン", 1, 0, 80005, "やばいですね☆", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 341, 0, "キャル", 2, 0, 80005, "って、なにしれっと\n参加してんのよカリン！", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 342, 0, "カリン", 2, 0, 80005, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 343, 0, "カリン", 3, 0, 80005, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 344, 0, "ペコリーヌ", 4, 0, 80005, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 345, 0, "シェフィ", 4, 0, 80005, "えっ！？　ええとええと…！\nあ、あと14日…！", 1);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 346, 0, "ペコリーヌ", 5, 0, 80005, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 347, 0, "コッコロ", 3, 0, 80005, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 348, 0, "コッコロ", 4, 0, 80005, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 349, 0, "ペコリーヌ", 6, 0, 80005, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 350, 0, "コッコロ", 5, 0, 80005, "さすがでございます\nペコリーヌさま", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 351, 0, "キャル", 3, 0, 80005, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 352, 0, "シェフィ", 5, 0, 80005, "しゃなり、しゃなり…\nふふ、案外できるものね", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 353, 0, "ペコリーヌ", 7, 0, 80005, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 354, 0, "コッコロ", 6, 0, 80005, "さ、次はキャルさまの\n番でございますよ", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 355, 0, "キャル", 4, 0, 80005, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 356, 0, "シェフィ", 6, 0, 80005, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 357, 0, "キャル", 5, 0, 80005, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 358, 0, "キャル", 6, 0, 80005, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 359, 0, "コッコロ", 7, 0, 80005, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 360, 0, "キャル", 7, 0, 80005, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 2);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 361, 0, "コッコロ", 8, 0, 80005, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 362, 0, "ペコリーヌ", 8, 0, 80005, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 363, 0, "コッコロ", 9, 0, 80005, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 364, 0, "シェフィ", 7, 0, 80005, "聖学祭…って、なにかしら？", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 365, 0, "コッコロ", 10, 0, 80005, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 366, 0, "キャル", 8, 0, 80005, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 367, 0, "キャル", 9, 0, 80005, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 368, 0, "ペコリーヌ", 9, 0, 80005, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 369, 0, "キャル", 10, 0, 80005, "いや、そのまんますぎでしょ…", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 370, 0, "シェフィ", 8, 0, 80005, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 371, 0, "ペコリーヌ", 10, 0, 80005, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 372, 0, "シェフィ", 9, 0, 80005, "えっ？　ええ…\nぎゅーっ…？", 3);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 373, 0, "キャル", 11, 0, 80005, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 374, 0, "シェフィ", 10, 0, 80005, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 375, 0, "キャル", 12, 0, 80005, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 376, 0, "コッコロ", 11, 0, 80005, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 377, 0, "キャル", 13, 0, 80005, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 378, 0, "ペコリーヌ", 11, 0, 80005, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 379, 0, "キャル", 14, 0, 80005, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 380, 0, "キャル", 15, 0, 80005, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 381, 0, "シェフィ", 11, 0, 80005, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 382, 0, "ペコリーヌ", 12, 0, 80005, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 383, 0, "コッコロ", 12, 0, 80005, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 384, 0, "キャル", 16, 0, 80005, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 385, 0, "シェフィ", 12, 0, 80005, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 4);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 386, 0, "ペコリーヌ", 13, 0, 80005, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 387, 0, "ペコリーヌ", 14, 0, 80005, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 388, 0, "ペコリーヌ", 15, 0, 80005, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 389, 0, "キャル", 17, 0, 80005, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 390, 0, "キャル", 18, 0, 80005, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 391, 0, "シェフィ", 13, 0, 80005, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 392, 0, "コッコロ", 13, 0, 80005, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 393, 0, "ペコリーヌ", 16, 0, 80005, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 394, 0, "キャル", 19, 0, 80005, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 395, 0, "ペコリーヌ", 17, 0, 80005, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 396, 0, "ペコリーヌ", 18, 0, 80005, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 397, 0, "ペコリーヌ", 19, 0, 80005, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 5);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 398, 0, "カリン", 4, 0, 80005, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 399, 0, "カリン", 5, 0, 80005, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 400, 0, "キャル", 20, 0, 80005, "…えっ！？", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 401, 0, "ペコリーヌ", 20, 0, 80005, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 402, 0, "シェフィ", 14, 0, 80005, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 403, 0, "コッコロ", 14, 0, 80005, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 404, 0, "シェフィ", 15, 0, 80005, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 405, 0, "キャル", 21, 0, 80005, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 406, 0, "ペコリーヌ", 21, 0, 80005, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 407, 0, "キャル", 22, 0, 80005, "ぶっ殺すぞ！？", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 408, 0, "カリン", 6, 0, 80005, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 6);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 409, 0, "コッコロ", 15, 0, 80005, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 410, 0, "キャル", 23, 0, 80005, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 411, 0, "シェフィ", 16, 0, 80005, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 412, 0, "ペコリーヌ", 22, 0, 80005, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 413, 0, "キャル", 24, 0, 80005, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 414, 0, "シェフィ", 17, 0, 80005, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 415, 0, "コッコロ", 16, 0, 80005, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 416, 0, "シェフィ", 18, 0, 80005, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 417, 0, "コッコロ", 17, 0, 80005, "もちろんでございます\n仲良くお勉強いたしましょう", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 418, 0, "キャル", 25, 0, 80005, "しれっと教育ママが\n生まれてる…？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 419, 0, "ペコリーヌ", 23, 0, 80005, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 420, 0, "キャル", 26, 0, 80005, "え～～～～！？", 7);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 421, 0, "キャル", 27, 0, 80005, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 422, 0, "シェフィ", 19, 0, 80005, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 423, 0, "コッコロ", 18, 0, 80005, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 424, 0, "ペコリーヌ", 24, 0, 80005, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 425, 0, "シェフィ", 20, 0, 80005, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 426, 0, "キャル", 28, 0, 80005, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 427, 0, "コッコロ", 19, 0, 80005, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 428, 0, "シェフィ", 21, 0, 80005, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 429, 0, "キャル", 29, 0, 80005, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 430, 0, "キャル", 30, 0, 80005, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 431, 0, "ペコリーヌ", 25, 0, 80005, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 432, 0, "キャル", 31, 0, 80005, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 8);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 433, 0, "シェフィ", 22, 0, 80005, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 434, 0, "シェフィ", 23, 0, 80005, "…えっ？\n生放送の意気込み！？\n突然なに！？", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 435, 0, "シェフィ", 24, 0, 80005, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 436, 0, "シェフィ", 25, 0, 80005, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 437, 0, "シェフィ", 26, 0, 80005, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 438, 0, "シェフィ", 27, 0, 80005, "それから、それから…！\nぅ、ぅぅっ…！", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 439, 0, "シェフィ", 28, 0, 80005, "―びえぇぇ～んっ！！！", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 440, 0, "コッコロ", 20, 0, 80005, "どうされましたか、シェフィさま", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 441, 0, "シェフィ", 29, 0, 80005, "ううっ…ひっく…\nママッ…！", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 442, 0, "コッコロ", 21, 0, 80005, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 443, 0, "コッコロ", 22, 0, 80005, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 444, 0, "シェフィ", 30, 0, 80005, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 9);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 445, 0, "コッコロ", 23, 0, 80005, "主さま\n3.5周年まであと５日\nでございます", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 446, 0, "コッコロ", 24, 0, 80005, "ですがその前に\n生放送がありますね", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 447, 0, "コッコロ", 25, 0, 80005, "…生放送に向けた\n意気込みでございますか？", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 448, 0, "コッコロ", 26, 0, 80005, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 449, 0, "コッコロ", 27, 0, 80005, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 450, 0, "コッコロ", 28, 0, 80005, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 451, 0, "コッコロ", 29, 0, 80005, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 452, 0, "コッコロ", 30, 0, 80005, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 0, "", 0, 453, 0, "コッコロ", 31, 0, 80005, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 10);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 454, 0, "ペコリーヌ", 26, 0, 80005, "3.5周年まであと４日！\nやばいですね☆", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 455, 0, "キャル", 32, 0, 80005, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 456, 0, "ペコリーヌ", 27, 0, 80005, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 457, 0, "ペコリーヌ", 28, 0, 80005, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 458, 0, "ペコリーヌ", 29, 0, 80005, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 459, 0, "キャル", 33, 0, 80005, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 460, 0, "ペコリーヌ", 30, 0, 80005, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 461, 0, "ペコリーヌ", 31, 0, 80005, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 462, 0, "ペコリーヌ", 32, 0, 80005, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 463, 0, "キャル", 34, 0, 80005, "だからあたしはやるなんて一言も…", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 0, "", 0, 464, 0, "ペコリーヌ", 33, 0, 80005, "オイッスー☆", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 465, 0, "キャル", 35, 0, 80005, "聞きなさいっての！！", 11);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 466, 0, "シェフィ", 31, 0, 80005, "3.5周年まであと３日…\nそして生放送は、明日…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 467, 0, "シェフィ", 32, 0, 80005, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 468, 0, "シェフィ", 33, 0, 80005, "おいっす！\n…こうじゃなかったわね\nええと確か…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 469, 0, "シェフィ", 34, 0, 80005, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 470, 0, "シェフィ", 35, 0, 80005, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 471, 0, "ペコリーヌ", 34, 0, 80005, "やってますねシェフィちゃん！", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 472, 0, "キャル", 36, 0, 80005, "なによ、考えることは\nみんな同じってこと？", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 473, 0, "コッコロ", 32, 0, 80005, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 474, 0, "シェフィ", 36, 0, 80005, "みんな…もちろんよ！", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 475, 0, "ペコリーヌ", 35, 0, 80005, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 12);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 476, 0, "カリン", 7, 0, 80005, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 477, 0, "カリン", 8, 0, 80005, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 478, 0, "ペコリーヌ", 36, 0, 80005, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 479, 0, "カリン", 9, 0, 80005, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 480, 0, "コッコロ", 33, 0, 80005, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 481, 0, "カリン", 10, 0, 80005, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 482, 0, "キャル", 37, 0, 80005, "大丈夫…大丈夫…\nあたしは大丈夫…", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 483, 0, "カリン", 11, 0, 80005, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 484, 0, "シェフィ", 37, 0, 80005, "あうー？", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 4, "105901,34,106001,38,190801,12", 0, 485, 1, "みんな", 37, 0, 80005, "あ…", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 486, 0, "キャル", 39, 0, 80005, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 487, 0, "コッコロ", 35, 0, 80005, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 488, 0, "ペコリーヌ", 38, 0, 80005, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 489, 0, "カリン", 13, 0, 80005, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 13);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 490, 0, "シェフィ", 38, 0, 80005, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 491, 0, "ペコリーヌ", 39, 0, 80005, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 492, 0, "キャル", 40, 0, 80005, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 493, 0, "コッコロ", 36, 0, 80005, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 494, 0, "シェフィ", 39, 0, 80005, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 495, 0, "ペコリーヌ", 40, 0, 80005, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 1, "", 0, 496, 0, "コッコロ", 37, 0, 80005, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 0, "", 0, 497, 0, "キャル", 41, 0, 80005, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 498, 0, "シェフィ", 40, 0, 80005, "へ、へぇ…", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 499, 0, "ペコリーヌ", 41, 0, 80005, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 500, 0, "シェフィ", 41, 0, 80005, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 2, "", 0, 501, 0, "シェフィ", 42, 0, 80005, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 14);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 502, 0, "ペコリーヌ", 42, 0, 80005, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 503, 0, "キャル", 42, 0, 80005, "祝！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 504, 0, "コッコロ", 38, 0, 80005, "3.5周年！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 505, 0, "シェフィ", 43, 0, 80005, "おめでとうございます！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 506, 0, "カリン", 14, 0, 80005, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190801, 2, "", 0, 507, 0, "カリン", 15, 0, 80005, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 508, 0, "ペコリーヌ", 43, 0, 80005, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 509, 0, "ペコリーヌ", 44, 0, 80005, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 510, 0, "キャル", 43, 0, 80005, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106001, 1, "", 0, 511, 0, "キャル", 44, 0, 80005, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 512, 0, "シェフィ", 44, 0, 80005, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (106401, 0, "", 0, 513, 0, "シェフィ", 45, 0, 80005, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 514, 0, "コッコロ", 39, 0, 80005, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 515, 0, "コッコロ", 40, 0, 80005, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105901, 4, "", 0, 516, 0, "コッコロ", 41, 0, 80005, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 3, "", 0, 517, 0, "ペコリーヌ", 45, 0, 80005, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (105801, 4, "105901,42,106001,45,106401,46,190801,16", 0, 518, 1, "みんな", 46, 0, 80005, "これからも\nよろしくおねがいします！", 15);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (190804, 1, "", 0, 519, 0, "カリン", 1, 1, 60003, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 520, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 521, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 522, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 523, 0, "", 0, 1, 60003, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 1, 524, 0, "", 0, 2, 60003, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 525, 0, "", 0, 1, 60003, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 526, 0, "", 0, 1, 60003, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 527, 0, "", 0, 1, 60003, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 528, 0, "", 0, 1, 60003, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 2, 529, 0, "", 0, 2, 60003, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 530, 0, "", 0, 1, 60003, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 531, 0, "", 0, 1, 60003, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 532, 0, "", 0, 1, 60003, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 533, 0, "", 0, 1, 60003, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 534, 0, "", 0, 1, 60003, "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 0);
INSERT INTO `v1_ae8e010fb5840b47b4666508ddea4789fc373742387a3dde244818da545eb7d6` VALUES (0, 2, "", 3, 535, 0, "", 0, 1, 60003, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
