INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 102301, 2, "アヤネ", 0.0, 401, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 2, 0.0, 24001, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 102301, 2, "アヤネ", 0.0, 402, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 1, 2.21, 24002, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 102301, 2, "アヤネ", 0.0, 403, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 2, 0.0, 24003, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192201, 1, "アサヒ", 0.0, 701, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 2, 1.99, 24004, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192201, 2, "アサヒ", 0.0, 702, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 1, 2.21, 24005, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192201, 1, "アサヒ", 0.0, 703, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 2, 3.33, 24006, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192301, 2, "部員３", 0.0, 801, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 2, 0.0, 24007, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192301, 2, "部員３", 0.0, 802, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 1, 2.68, 24008, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192301, 2, "部員３", 0.0, 803, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 2, 0.0, 24009, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192501, 1, "", 0.0, 501, "やった！", 1, 0.0, 24010, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192501, 1, "", 0.0, 502, "勝ったよ！", 1, 0.0, 24011, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192501, 1, "", 0.0, 503, "いい調子！", 1, 0.0, 24012, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", 0.0, 111, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 6, 5.33, 24013, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", 0.0, 112, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 1, 6.85, 24014, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", 0.0, 113, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 1, 3.0, 24015, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192401, 2, "ダイチ", 0.0, 611, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 1, 6.11, 24016, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192401, 2, "ダイチ", 0.0, 612, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 1, 6.75, 24017, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192401, 2, "ダイチ", 0.0, 613, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 2, 0.0, 24018, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113401, 1, "ハツネ", 0.0, 211, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 2, 4.52, 24019, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113401, 1, "ハツネ", 0.0, 212, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 2, 3.89, 24020, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113401, 2, "ハツネ", 0.0, 213, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 1, 13.49, 24021, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 106501, 2, "カヤ", 0.0, 311, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 1, 3.17, 24022, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 106501, 2, "カヤ", 0.0, 312, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 1, 4.42, 24023, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 106501, 1, "カヤ", 0.0, 313, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 6, 2.89, 24024, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 102301, 3, "アヤネ", 0.0, 421, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 4, 3.71, 24025, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192201, 1, "アサヒ", 0.0, 721, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 2, 5.1, 24026, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192301, 3, "部員３", 0.0, 821, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 1, 4.34, 24027, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 192501, 1, "", 0.0, 521, "いい勝負だったよ\nまたやろうね", 1, 0.0, 24028, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", -360.0, 101, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 1, 5.62, 24029, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", -360.0, 102, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 1, 9.58, 24030, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", -360.0, 103, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 2, 0.0, 24031, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 1, "ミサト", -360.0, 104, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 2, 9.13, 24032, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, 24, "2030/12/17 14:59:59", "2020/07/31 12:00:00", 113501, 2, "ミサト", -360.0, 105, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 1, 8.27, 24033, 0.0);
