INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (2, 24003, 102301, "アヤネ", 0.0, 0.0, "2030/12/17 14:59:59", 0.0, 403, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", "2020/07/31 12:00:00", 2, 24, 10046);
