INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (2, 24007, 192301, "部員３", 0.0, 0.0, "2030/12/17 14:59:59", 0.0, 801, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", "2020/07/31 12:00:00", 2, 24, 10046);
