INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (6, 24013, 113501, "ミサト", 0.0, 5.33, "2030/12/17 14:59:59", 0.0, 111, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24014, 113501, "ミサト", 0.0, 6.85, "2030/12/17 14:59:59", 0.0, 112, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24015, 113501, "ミサト", 0.0, 3.0, "2030/12/17 14:59:59", 0.0, 113, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24016, 192401, "ダイチ", 0.0, 6.11, "2030/12/17 14:59:59", 0.0, 611, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24017, 192401, "ダイチ", 0.0, 6.75, "2030/12/17 14:59:59", 0.0, 612, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (2, 24018, 192401, "ダイチ", 0.0, 0.0, "2030/12/17 14:59:59", 0.0, 613, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (2, 24019, 113401, "ハツネ", 0.0, 4.52, "2030/12/17 14:59:59", 0.0, 211, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", "2020/07/31 12:00:00", 1, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (2, 24020, 113401, "ハツネ", 0.0, 3.89, "2030/12/17 14:59:59", 0.0, 212, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", "2020/07/31 12:00:00", 1, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24021, 113401, "ハツネ", 0.0, 13.49, "2030/12/17 14:59:59", 0.0, 213, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24022, 106501, "カヤ", 0.0, 3.17, "2030/12/17 14:59:59", 0.0, 311, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", "2020/07/31 12:00:00", 2, 24, 10046);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (1, 24023, 106501, "カヤ", 0.0, 4.42, "2030/12/17 14:59:59", 0.0, 312, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", "2020/07/31 12:00:00", 2, 24, 10046);
