INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アヤネ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 10046, 2, 24, 24001, 0.0, 0.0, 102301, 401);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アヤネ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 10046, 2, 24, 24002, 0.0, 2.21, 102301, 402);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アヤネ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 10046, 2, 24, 24003, 0.0, 0.0, 102301, 403);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アサヒ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 10046, 1, 24, 24004, 0.0, 1.99, 192201, 701);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アサヒ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 10046, 2, 24, 24005, 0.0, 2.21, 192201, 702);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アサヒ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 10046, 1, 24, 24006, 0.0, 3.33, 192201, 703);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("部員３", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 10046, 2, 24, 24007, 0.0, 0.0, 192301, 801);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("部員３", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 10046, 2, 24, 24008, 0.0, 2.68, 192301, 802);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("部員３", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 10046, 2, 24, 24009, 0.0, 0.0, 192301, 803);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "やった！", 10046, 1, 24, 24010, 0.0, 0.0, 192501, 501);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "勝ったよ！", 10046, 1, 24, 24011, 0.0, 0.0, 192501, 502);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "いい調子！", 10046, 1, 24, 24012, 0.0, 0.0, 192501, 503);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 6, "2020/07/31 12:00:00", "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 10046, 2, 24, 24013, 0.0, 5.33, 113501, 111);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 10046, 2, 24, 24014, 0.0, 6.85, 113501, 112);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 10046, 2, 24, 24015, 0.0, 3.0, 113501, 113);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ダイチ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 10046, 2, 24, 24016, 0.0, 6.11, 192401, 611);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ダイチ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 10046, 2, 24, 24017, 0.0, 6.75, 192401, 612);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ダイチ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 10046, 2, 24, 24018, 0.0, 0.0, 192401, 613);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ハツネ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 10046, 1, 24, 24019, 0.0, 4.52, 113401, 211);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ハツネ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 10046, 1, 24, 24020, 0.0, 3.89, 113401, 212);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ハツネ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 10046, 2, 24, 24021, 0.0, 13.49, 113401, 213);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("カヤ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 10046, 2, 24, 24022, 0.0, 3.17, 106501, 311);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("カヤ", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 10046, 2, 24, 24023, 0.0, 4.42, 106501, 312);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("カヤ", 0.0, "2030/12/17 14:59:59", 6, "2020/07/31 12:00:00", "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 10046, 1, 24, 24024, 0.0, 2.89, 106501, 313);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アヤネ", 0.0, "2030/12/17 14:59:59", 4, "2020/07/31 12:00:00", "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 10046, 3, 24, 24025, 0.0, 3.71, 102301, 421);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("アサヒ", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 10046, 1, 24, 24026, 0.0, 5.1, 192201, 721);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("部員３", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 10046, 3, 24, 24027, 0.0, 4.34, 192301, 821);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "いい勝負だったよ\nまたやろうね", 10046, 1, 24, 24028, 0.0, 0.0, 192501, 521);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 10046, 2, 24, 24029, -360.0, 5.62, 113501, 101);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 10046, 2, 24, 24030, -360.0, 9.58, 113501, 102);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 10046, 2, 24, 24031, -360.0, 0.0, 113501, 103);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 2, "2020/07/31 12:00:00", "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 10046, 1, 24, 24032, -360.0, 9.13, 113501, 104);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES ("ミサト", 0.0, "2030/12/17 14:59:59", 1, "2020/07/31 12:00:00", "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 10046, 2, 24, 24033, -360.0, 8.27, 113501, 105);
