INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24001, 2, "2030/12/17 14:59:59", 10046, 24, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 401, "アヤネ", 0.0, 102301, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24002, 2, "2030/12/17 14:59:59", 10046, 24, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 402, "アヤネ", 2.21, 102301, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24003, 2, "2030/12/17 14:59:59", 10046, 24, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 403, "アヤネ", 0.0, 102301, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24004, 1, "2030/12/17 14:59:59", 10046, 24, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 701, "アサヒ", 1.99, 192201, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24005, 2, "2030/12/17 14:59:59", 10046, 24, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 702, "アサヒ", 2.21, 192201, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24006, 1, "2030/12/17 14:59:59", 10046, 24, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 703, "アサヒ", 3.33, 192201, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24007, 2, "2030/12/17 14:59:59", 10046, 24, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 801, "部員３", 0.0, 192301, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24008, 2, "2030/12/17 14:59:59", 10046, 24, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 802, "部員３", 2.68, 192301, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24009, 2, "2030/12/17 14:59:59", 10046, 24, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 803, "部員３", 0.0, 192301, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24010, 1, "2030/12/17 14:59:59", 10046, 24, "やった！", 501, "", 0.0, 192501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24011, 1, "2030/12/17 14:59:59", 10046, 24, "勝ったよ！", 502, "", 0.0, 192501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24012, 1, "2030/12/17 14:59:59", 10046, 24, "いい調子！", 503, "", 0.0, 192501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24013, 2, "2030/12/17 14:59:59", 10046, 24, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 111, "ミサト", 5.33, 113501, "2020/07/31 12:00:00", 0.0, 6);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24014, 2, "2030/12/17 14:59:59", 10046, 24, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 112, "ミサト", 6.85, 113501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24015, 2, "2030/12/17 14:59:59", 10046, 24, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 113, "ミサト", 3.0, 113501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24016, 2, "2030/12/17 14:59:59", 10046, 24, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 611, "ダイチ", 6.11, 192401, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24017, 2, "2030/12/17 14:59:59", 10046, 24, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 612, "ダイチ", 6.75, 192401, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24018, 2, "2030/12/17 14:59:59", 10046, 24, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 613, "ダイチ", 0.0, 192401, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24019, 1, "2030/12/17 14:59:59", 10046, 24, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 211, "ハツネ", 4.52, 113401, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24020, 1, "2030/12/17 14:59:59", 10046, 24, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 212, "ハツネ", 3.89, 113401, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24021, 2, "2030/12/17 14:59:59", 10046, 24, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 213, "ハツネ", 13.49, 113401, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24022, 2, "2030/12/17 14:59:59", 10046, 24, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 311, "カヤ", 3.17, 106501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24023, 2, "2030/12/17 14:59:59", 10046, 24, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 312, "カヤ", 4.42, 106501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24024, 1, "2030/12/17 14:59:59", 10046, 24, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 313, "カヤ", 2.89, 106501, "2020/07/31 12:00:00", 0.0, 6);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24025, 3, "2030/12/17 14:59:59", 10046, 24, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 421, "アヤネ", 3.71, 102301, "2020/07/31 12:00:00", 0.0, 4);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24026, 1, "2030/12/17 14:59:59", 10046, 24, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 721, "アサヒ", 5.1, 192201, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24027, 3, "2030/12/17 14:59:59", 10046, 24, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 821, "部員３", 4.34, 192301, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (0.0, 24028, 1, "2030/12/17 14:59:59", 10046, 24, "いい勝負だったよ\nまたやろうね", 521, "", 0.0, 192501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (-360.0, 24029, 2, "2030/12/17 14:59:59", 10046, 24, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 101, "ミサト", 5.62, 113501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (-360.0, 24030, 2, "2030/12/17 14:59:59", 10046, 24, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 102, "ミサト", 9.58, 113501, "2020/07/31 12:00:00", 0.0, 1);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (-360.0, 24031, 2, "2030/12/17 14:59:59", 10046, 24, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 103, "ミサト", 0.0, 113501, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (-360.0, 24032, 1, "2030/12/17 14:59:59", 10046, 24, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 104, "ミサト", 9.13, 113501, "2020/07/31 12:00:00", 0.0, 2);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (-360.0, 24033, 2, "2030/12/17 14:59:59", 10046, 24, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 105, "ミサト", 8.27, 113501, "2020/07/31 12:00:00", 0.0, 1);
