INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アヤネ", 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 24, 24001, 2, 102301, "2020/07/31 12:00:00", 401, 0.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アヤネ", 0.0, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 24, 24002, 1, 102301, "2020/07/31 12:00:00", 402, 2.21, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アヤネ", 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 24, 24003, 2, 102301, "2020/07/31 12:00:00", 403, 0.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アサヒ", 0.0, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 24, 24004, 2, 192201, "2020/07/31 12:00:00", 701, 1.99, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アサヒ", 0.0, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 24, 24005, 1, 192201, "2020/07/31 12:00:00", 702, 2.21, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アサヒ", 0.0, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 24, 24006, 2, 192201, "2020/07/31 12:00:00", 703, 3.33, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "部員３", 0.0, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 24, 24007, 2, 192301, "2020/07/31 12:00:00", 801, 0.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "部員３", 0.0, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 24, 24008, 1, 192301, "2020/07/31 12:00:00", 802, 2.68, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "部員３", 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 24, 24009, 2, 192301, "2020/07/31 12:00:00", 803, 0.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "", 0.0, "やった！", 24, 24010, 1, 192501, "2020/07/31 12:00:00", 501, 0.0, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "", 0.0, "勝ったよ！", 24, 24011, 1, 192501, "2020/07/31 12:00:00", 502, 0.0, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "", 0.0, "いい調子！", 24, 24012, 1, 192501, "2020/07/31 12:00:00", 503, 0.0, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", 0.0, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 24, 24013, 6, 113501, "2020/07/31 12:00:00", 111, 5.33, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", 0.0, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 24, 24014, 1, 113501, "2020/07/31 12:00:00", 112, 6.85, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", 0.0, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 24, 24015, 1, 113501, "2020/07/31 12:00:00", 113, 3.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ダイチ", 0.0, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 24, 24016, 1, 192401, "2020/07/31 12:00:00", 611, 6.11, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ダイチ", 0.0, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 24, 24017, 1, 192401, "2020/07/31 12:00:00", 612, 6.75, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ダイチ", 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 24, 24018, 2, 192401, "2020/07/31 12:00:00", 613, 0.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ハツネ", 0.0, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 24, 24019, 2, 113401, "2020/07/31 12:00:00", 211, 4.52, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ハツネ", 0.0, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 24, 24020, 2, 113401, "2020/07/31 12:00:00", 212, 3.89, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ハツネ", 0.0, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 24, 24021, 1, 113401, "2020/07/31 12:00:00", 213, 13.49, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "カヤ", 0.0, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 24, 24022, 1, 106501, "2020/07/31 12:00:00", 311, 3.17, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "カヤ", 0.0, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 24, 24023, 1, 106501, "2020/07/31 12:00:00", 312, 4.42, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "カヤ", 0.0, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 24, 24024, 6, 106501, "2020/07/31 12:00:00", 313, 2.89, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アヤネ", 0.0, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 24, 24025, 4, 102301, "2020/07/31 12:00:00", 421, 3.71, "2030/12/17 14:59:59", 3, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "アサヒ", 0.0, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 24, 24026, 2, 192201, "2020/07/31 12:00:00", 721, 5.1, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "部員３", 0.0, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 24, 24027, 1, 192301, "2020/07/31 12:00:00", 821, 4.34, "2030/12/17 14:59:59", 3, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "", 0.0, "いい勝負だったよ\nまたやろうね", 24, 24028, 1, 192501, "2020/07/31 12:00:00", 521, 0.0, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", -360.0, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 24, 24029, 1, 113501, "2020/07/31 12:00:00", 101, 5.62, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", -360.0, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 24, 24030, 1, 113501, "2020/07/31 12:00:00", 102, 9.58, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", -360.0, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 24, 24031, 2, 113501, "2020/07/31 12:00:00", 103, 0.0, "2030/12/17 14:59:59", 2, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", -360.0, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 24, 24032, 2, 113501, "2020/07/31 12:00:00", 104, 9.13, "2030/12/17 14:59:59", 1, 0.0);
INSERT INTO `v1_af0ffb8fef7d91701db82840d1fc5276592fbc6fd9ae0c602f7734a943dabed2` VALUES (10046, "ミサト", -360.0, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 24, 24033, 1, 113501, "2020/07/31 12:00:00", 105, 8.27, "2030/12/17 14:59:59", 2, 0.0);
