INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("リンの\nナッツ入りぐうたらあんパン", 10, "リンのナッツ入りぐうたらあんパン", 30003, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", "なし", 0, 52028, 9, 3, 51028, 1, 1, 500, 0, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 2);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("シェフィの\n氷晶煌めく手作りアイス", 0, "シェフィの氷晶煌めく手作りアイス", 30004, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", "なし", 0, 52029, 0, 2, 51029, 1, 1, 500, 0, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 3);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ミソラの\n気まぐれ味噌ラーメン★", 0, "ミソラの気まぐれ味噌ラーメン★", 1, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", "なし", 0, 52030, 0, 1, 51030, 3, 3, 500, 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 12);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("イロハの\n最強クワガタゼリー（人間用）", 0, "イロハの最強クワガタゼリー（人間用）", 20009, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", "なし", 0, 52031, 0, 3, 51031, 1, 1, 500, 0, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 2);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("グレイスの\n目ん玉飛び出るカップケーキ", 11, "グレイスの目ん玉飛び出るカップケーキ", 4, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", "次にダイスを振った際、3マス多く進める。", 0, 52032, 3, 3, 51032, 2, 2, 500, 0, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 9);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ソノの今日は特別ご褒美オムライス", 0, "ソノの今日は特別ご褒美オムライス", 3, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", "なし", 0, 52033, 0, 1, 51033, 0, 3, 500, 0, "RANK3のマイルショップを開く。", 8);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ジータの\nグリルド・グラッジチャンク", 10, "ジータのグリルド・グラッジチャンク", 1, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", "平凡な未鑑定トレジャーを1個獲得する。", 0, 52034, 1, 1, 51034, 2, 2, 500, 0, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 9);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ルイズマリーの\n成功しすぎてるフライ", 10, "ルイズマリーの成功しすぎてるフライ", 1, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", "貴重な未鑑定トレジャーを1個獲得する。", 0, 52035, 10, 1, 51035, 0, 3, 500, 0, "ランダムなイベントを発生させる。", 4);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ホウオウの\n食べる権利をあげます！饅頭", 0, "ホウオウの食べる権利をあげます！饅頭", 1, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", "なし", 0, 52036, 0, 3, 51036, 3, 1, 500, 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 7);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("魔法少女\nナナカちゃんウエハース", 10, "魔法少女ナナカちゃんウエハース", 1, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", "平凡な未鑑定トレジャーを3個獲得する。", 1, 52037, 11, 3, 51037, 0, 2, 500, 0, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 6);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("アリサがお届け♪\nリノセウスの包み焼き", 10, "アリサがお届け♪リノセウスの包み焼き", 2, "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。", "福引券を1枚獲得する。", 0, 52038, 3, 1, 51038, 1, 1, 500, 0, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("イリヤの†ブラッディ†\nトマトジュース", 0, "イリヤの†ブラッディ†トマトジュース", 2, "イリヤの愛飲する赤く鮮やかなトマトジュース。\n伝説の吸血鬼に相応しいクオリティを、と求めた\n末に辿り着いた究極の逸品。しかしお値段も相応\nに張るため、特別な時にしか飲めないらしい。", "なし", 0, 52039, 0, 2, 51039, 2, 3, 500, 0, "2ターンの間、すべての「マイルマス」をランク3の「ショップマス」に変化させる。", 12);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("マツリの活力源\n～騎士団遠征プレート～", 11, "マツリの活力源～騎士団遠征プレート～", 2, "【王宮騎士団】遠征時の定番メニューにマツリが\nアレンジを加え調理したプレート。苦手な野菜を\nなくしてお肉倍増を目論んだのが配膳前にバレ、\nジュンの指導できちんと野菜も入れさせられた。", "次にダイスを振った際、2マス多く進める。", 0, 52040, 2, 1, 51040, 3, 2, 500, 0, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 9);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ヴァンピィちゃんの\nさいきょースープ", 10, "ヴァンピィちゃんのさいきょースープ", 1, "ヴァンピィがよく振舞ってくれる特製スープ。\nどうやら家庭料理のようで、素朴な味わいが\n人々を郷愁へと誘う。昔誰かと並んで一緒に\n肉団子を作ったような…とはヴァンピィの言葉。", "平凡な未鑑定トレジャーを1個獲得する。", 0, 52041, 1, 1, 51041, 2, 1, 500, 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「7」に、次のターンに振るダイスの目を必ず「2」にする。", 7);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("アイラのみんなで\n飲みたい赤麦しゅわ", 0, "アイラのみんなで飲みたい赤麦しゅわ", 20002, "アイラが愛する赤麦しゅわ。彼女の熱い要望で\n酩酊感ゼロの子供向けも生産開始した。曰くこの\n美味しさを広めるためとのことだが、本当は単に\n老若男女皆で楽しく飲みたいだけかもしれない。", "なし", 0, 52042, 0, 2, 51042, 2, 1, 500, 0, "ダイスを2回振るまでの間、出る目が「4・5・6」のみになる", 2);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("レイの釣りたて\n鮮魚七輪塩焼き", 10, "レイの釣りたて鮮魚七輪塩焼き", 1, "レイが釣った魚の塩焼き。試行錯誤の末に理想の\n塩と火の加減にたどり着いた。調理は真剣勝負で\n釣りと同様、己の心との戦いだという。レイも\n時々、集中しすぎて仲間に心配されているとか。", "なし", 0, 52043, 12, 1, 51043, 2, 1, 500, 1, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で貴重な未鑑定トレジャーを3個獲得する。", 2);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES ("ユキノ製ごろっと果実の\nかちこち氷菓子", 10, "ユキノ製ごろっと果実のかちこち氷菓子", 30002, "ユキノが偶然生み出した、青果実のアイスバー。\n元は誤って凍らせたものだが爽やかな味が好評で\nなんと久遠氷河を代表する手土産に。伸び続ける\n売り上げに本人が一番溶けそうになっている。", "福引券を1枚獲得する。", 0, 52044, 3, 2, 51044, 1, 1, 500, 0, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 3);
