INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, 0, "ペコリーヌの満腹ツインおにぎり", 52001, 10, 1, 500, "ペコリーヌの\n満腹ツインおにぎり", 2, 2, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", "500マイルを獲得する。", 0, 1, 1, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 51001);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, 0, "コッコロのゆるりと休憩サラダ", 52002, 10, 1, 500, "コッコロの\nゆるりと休憩サラダ", 30001, 2, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", "500マイルを獲得する。", 0, 2, 1, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", 51002);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "キャルの絶品五つ星バーガー", 52003, 10, 1, 500, "キャルの\n絶品五つ星バーガー", 20004, 1, "このターンに振るダイスの目が必ず「5」になる。", "平凡な未鑑定トレジャーを1個獲得する。", 0, 1, 1, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 51003);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "リリのコツコツまい進かしわ餅", 52004, 10, 1, 500, "リリのコツコツ\nまい進かしわ餅", 20001, 4, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", "800マイルを獲得する。", 0, 3, 3, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", 51004);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (4, 0, "クリアの彩り華やかバラエティ弁当", 52005, 11, 3, 500, "クリアの彩り華やか\nバラエティ弁当", 1, 1, "ランダムなイベントを発生させる。", "次にダイスを振った際、1マス多く進める。", 0, 3, 0, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", 51005);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "プレシアの食いしん坊ぶーぶー肉まん", 52006, 10, 1, 500, "プレシアの食いしん坊\nぶーぶー肉まん", 20002, 4, "このターンに振るダイスの目が「4・5・6」のみになる。", "800マイルを獲得する。", 0, 1, 1, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", 51006);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (7, 0, "カヤとイノリの食欲倍増ケバブサンド", 52007, 10, 1, 500, "カヤとイノリの\n食欲倍増ケバブサンド", 1, 3, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", "福引券を1枚獲得する。", 0, 1, 2, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", 51007);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (5, 1, "タマキの金運招くレインボーたい焼き", 52008, 10, 2, 500, "タマキの金運招く\nレインボーたい焼き", 200, 2, "次にマイルマスに止まった際の獲得マイルが200%アップする。", "500マイルを獲得する。", 0, 2, 0, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 51008);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (7, 0, "クローチェのフルパワーポテトサラダ", 52009, 10, 1, 500, "クローチェの\nフルパワーポテトサラダ", 1, 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", "平凡な未鑑定トレジャーを1個獲得する。", 1, 2, 2, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", 51009);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (6, 2, "アヤネとクルミの青空ピクニックセット", 52010, 10, 2, 500, "アヤネとクルミの\n青空ピクニックセット", 1, 2, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", "500マイルを獲得する。", 0, 3, 0, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", 51010);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (7, 0, "リマの不思議なスイートアップル", 52011, 10, 1, 500, "リマの不思議な\nスイートアップル", 1, 3, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", "福引券を1枚獲得する。", 0, 3, 2, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", 51011);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, 0, "ニノンのオリジナル一二三寿司", 52012, 10, 1, 500, "ニノンのオリジナル\n一二三寿司", 30002, 1, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", "平凡な未鑑定トレジャーを1個獲得する。", 0, 2, 1, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", 51012);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (8, 0, "クレジッタの開運お菓子盛り合わせ", 52013, 10, 3, 500, "クレジッタの開運\nお菓子盛り合わせ", 2, 4, "RANK2のマイルショップを開く。", "800マイルを獲得する。", 0, 3, 0, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", 51013);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "リノの「時は金時」かき氷", 52014, 11, 1, 500, "リノの\n「時は金時」かき氷", 1, 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "次にダイスを振った際、1マス多く進める。", 0, 2, 2, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 51014);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, 0, "シズルの愛情盛り盛りハンバーグ", 52015, 11, 1, 500, "シズルの愛情\n盛り盛りハンバーグ", 2, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", "次にダイスを振った際、1マス多く進める。", 0, 1, 1, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 51015);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "アオイの疾走サンドイッチ", 52016, 0, 1, 500, "アオイの疾走\nサンドイッチ", 20005, 0, "このターンに振るダイスの目が必ず「6」になる。", "なし", 0, 3, 1, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 51016);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "クウカの緊縛触手活け造り", 52017, 10, 1, 500, "クウカの緊縛\n触手活け造り", 1, 5, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", "なし", 1, 3, 2, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 51017);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "ランファの世話焼き三昧肉じゃが", 52018, 10, 1, 500, "ランファの世話焼き三昧肉じゃが", 20006, 6, "このターンに振るダイスの目が必ず「3」になる。", "1000マイルを獲得する。", 0, 1, 1, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 51018);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "ワカナの節約腹八分目カレー", 52019, 0, 1, 500, "ワカナの節約腹八分目カレー", 20007, 0, "このターンに振るダイスの目が必ず「8」になる。", "なし", 0, 1, 1, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 0);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "リノの「時は金時」かき氷", 52020, 11, 2, 500, "リノの\n「時は金時」かき氷", 1, 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "次にダイスを振った際、1マス多く進める。", 0, 2, 2, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 51014);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "カスミの真実見抜くボンボン・ショコラ", 52021, 10, 2, 500, "カスミの真実見抜く\nボンボン・ショコラ", 2, 7, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", "	平凡な未鑑定トレジャーを2個獲得する。", 0, 3, 3, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", 51021);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "アキノのゲヘニアンチキンプレート", 52022, 0, 1, 500, "アキノのゲヘニアン\nチキンプレート", 20008, 0, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", "なし", 0, 1, 3, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", 51022);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (7, 0, "ミミのコロコロお手玉おにぎり", 52023, 10, 1, 500, "ミミのコロコロ\nお手玉おにぎり", 1, 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", "平凡な未鑑定トレジャーを1個獲得する。", 1, 1, 2, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 51023);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (8, 0, "アユミの隠密ガトーインビジブル", 52024, 11, 3, 500, "アユミの隠密\nガトーインビジブル", 2, 3, "RANK2のマイルショップを開く。", "次にダイスを振った際、3マス多く進める。", 0, 3, 0, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 51024);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "アネモネの花爛漫ステーキサラダ", 52025, 0, 2, 500, "アネモネの\n花爛漫ステーキサラダ", 3, 0, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", "なし", 0, 3, 1, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 51025);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (4, 0, "アンナの煉獄蕃茄煮混沌卵", 52026, 10, 3, 500, "アンナの\n煉獄蕃茄煮混沌卵", 1, 8, "ランダムなイベントを発生させる。", "福引券を2枚獲得する。", 0, 1, 0, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 51026);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "ユイの栄養満点まごころ弁当", 52027, 0, 1, 500, "ユイの\n栄養満点まごころ弁当", 20004, 0, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", "なし", 0, 3, 2, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 51027);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "リンのナッツ入りぐうたらあんパン", 52028, 10, 1, 500, "リンの\nナッツ入りぐうたらあんパン", 30003, 9, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", "なし", 0, 3, 1, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 51028);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, 0, "シェフィの氷晶煌めく手作りアイス", 52029, 0, 1, 500, "シェフィの\n氷晶煌めく手作りアイス", 30004, 0, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", "なし", 0, 2, 1, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 51029);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (12, 0, "ミソラの気まぐれ味噌ラーメン★", 52030, 0, 3, 500, "ミソラの\n気まぐれ味噌ラーメン★", 1, 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", "なし", 0, 1, 3, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 51030);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "イロハの最強クワガタゼリー（人間用）", 52031, 0, 1, 500, "イロハの\n最強クワガタゼリー（人間用）", 20009, 0, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", "なし", 0, 3, 1, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 51031);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "グレイスの目ん玉飛び出るカップケーキ", 52032, 11, 2, 500, "グレイスの\n目ん玉飛び出るカップケーキ", 4, 3, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", "次にダイスを振った際、3マス多く進める。", 0, 3, 2, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 51032);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (8, 0, "ソノの今日は特別ご褒美オムライス", 52033, 0, 3, 500, "ソノの今日は特別ご褒美オムライス", 3, 0, "RANK3のマイルショップを開く。", "なし", 0, 1, 0, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 51033);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "ジータのグリルド・グラッジチャンク", 52034, 10, 2, 500, "ジータの\nグリルド・グラッジチャンク", 1, 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "平凡な未鑑定トレジャーを1個獲得する。", 0, 1, 2, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 51034);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (4, 0, "ルイズマリーの成功しすぎてるフライ", 52035, 10, 3, 500, "ルイズマリーの\n成功しすぎてるフライ", 1, 10, "ランダムなイベントを発生させる。", "貴重な未鑑定トレジャーを1個獲得する。", 0, 1, 0, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", 51035);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (7, 0, "ホウオウの食べる権利をあげます！饅頭", 52036, 0, 1, 500, "ホウオウの\n食べる権利をあげます！饅頭", 1, 0, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", "なし", 1, 3, 3, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", 51036);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (6, 1, "魔法少女ナナカちゃんウエハース", 52037, 10, 2, 500, "魔法少女\nナナカちゃんウエハース", 1, 11, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", "平凡な未鑑定トレジャーを3個獲得する。", 0, 3, 0, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", 51037);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, 0, "アリサがお届け♪リノセウスの包み焼き", 52038, 10, 1, 500, "アリサがお届け♪\nリノセウスの包み焼き", 2, 3, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", "福引券を1枚獲得する。", 0, 1, 1, "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。", 51038);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (12, 0, "イリヤの†ブラッディ†トマトジュース", 52039, 0, 3, 500, "イリヤの†ブラッディ†\nトマトジュース", 2, 0, "2ターンの間、すべての「マイルマス」をランク3の「ショップマス」に変化させる。", "なし", 0, 2, 2, "イリヤの愛飲する赤く鮮やかなトマトジュース。\n伝説の吸血鬼に相応しいクオリティを、と求めた\n末に辿り着いた究極の逸品。しかしお値段も相応\nに張るため、特別な時にしか飲めないらしい。", 51039);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "マツリの活力源～騎士団遠征プレート～", 52040, 11, 2, 500, "マツリの活力源\n～騎士団遠征プレート～", 2, 2, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", "次にダイスを振った際、2マス多く進める。", 0, 1, 3, "【王宮騎士団】遠征時の定番メニューにマツリが\nアレンジを加え調理したプレート。苦手な野菜を\nなくしてお肉倍増を目論んだのが配膳前にバレ、\nジュンの指導できちんと野菜も入れさせられた。", 51040);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (7, 0, "ヴァンピィちゃんのさいきょースープ", 52041, 10, 1, 500, "ヴァンピィちゃんの\nさいきょースープ", 1, 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「7」に、次のターンに振るダイスの目を必ず「2」にする。", "平凡な未鑑定トレジャーを1個獲得する。", 1, 1, 2, "ヴァンピィがよく振舞ってくれる特製スープ。\nどうやら家庭料理のようで、素朴な味わいが\n人々を郷愁へと誘う。昔誰かと並んで一緒に\n肉団子を作ったような…とはヴァンピィの言葉。", 51041);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "アイラのみんなで飲みたい赤麦しゅわ", 52042, 0, 1, 500, "アイラのみんなで\n飲みたい赤麦しゅわ", 20002, 0, "ダイスを2回振るまでの間、出る目が「4・5・6」のみになる", "なし", 0, 2, 2, "アイラが愛する赤麦しゅわ。彼女の熱い要望で\n酩酊感ゼロの子供向けも生産開始した。曰くこの\n美味しさを広めるためとのことだが、本当は単に\n老若男女皆で楽しく飲みたいだけかもしれない。", 51042);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, 0, "レイの釣りたて鮮魚七輪塩焼き", 52043, 10, 1, 500, "レイの釣りたて\n鮮魚七輪塩焼き", 1, 12, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で貴重な未鑑定トレジャーを3個獲得する。", "なし", 1, 1, 2, "レイが釣った魚の塩焼き。試行錯誤の末に理想の\n塩と火の加減にたどり着いた。調理は真剣勝負で\n釣りと同様、己の心との戦いだという。レイも\n時々、集中しすぎて仲間に心配されているとか。", 51043);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, 0, "ユキノ製ごろっと果実のかちこち氷菓子", 52044, 10, 1, 500, "ユキノ製ごろっと果実の\nかちこち氷菓子", 30002, 3, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", "福引券を1枚獲得する。", 0, 2, 1, "ユキノが偶然生み出した、青果実のアイスバー。\n元は誤って凍らせたものだが爽やかな味が好評で\nなんと久遠氷河を代表する手土産に。伸び続ける\n売り上げに本人が一番溶けそうになっている。", 51044);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (9, 0, "サレンの思い出ぎっしり焼きそばパン", 52045, 10, 2, 500, "サレンの思い出ぎっしり\n焼きそばパン", 5, 4, "「ショップマス」「ミニゲームマス」「イベントマス」をスキップして移動できる。効果は3ターンの間継続する。", "800マイルを獲得する。", 0, 3, 3, "サレンが作った思い出の焼きそばパン。どっしり\nしたボリュームある炭水化物×炭水化物の信頼感\nで皆を笑顔に変える。専用の皿に盛り付けたら見\n栄えも十分。カロリーは気にしないのがお作法。", 51045);
