INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "なし", 3, 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", "アネモネの花爛漫ステーキサラダ", 0, 52025, 1, "アネモネの\n花爛漫ステーキサラダ", "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 2, 0, 9, 51025, 0, 500);
