INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "なし", 20004, 0, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", "ユイの栄養満点まごころ弁当", 0, 52027, 2, "ユイの\n栄養満点まごころ弁当", "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 1, 0, 2, 51027, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "なし", 30003, 9, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", "リンのナッツ入りぐうたらあんパン", 0, 52028, 1, "リンの\nナッツ入りぐうたらあんパン", "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 1, 10, 2, 51028, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, "なし", 30004, 0, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", "シェフィの氷晶煌めく手作りアイス", 0, 52029, 1, "シェフィの\n氷晶煌めく手作りアイス", "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 1, 0, 3, 51029, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "なし", 1, 0, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", "ミソラの気まぐれ味噌ラーメン★", 0, 52030, 3, "ミソラの\n気まぐれ味噌ラーメン★", "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 3, 0, 12, 51030, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "なし", 20009, 0, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", "イロハの最強クワガタゼリー（人間用）", 0, 52031, 1, "イロハの\n最強クワガタゼリー（人間用）", "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 1, 0, 2, 51031, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "次にダイスを振った際、3マス多く進める。", 4, 3, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", "グレイスの目ん玉飛び出るカップケーキ", 0, 52032, 2, "グレイスの\n目ん玉飛び出るカップケーキ", "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 2, 11, 9, 51032, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "なし", 3, 0, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", "ソノの今日は特別ご褒美オムライス", 0, 52033, 0, "ソノの今日は特別ご褒美オムライス", "RANK3のマイルショップを開く。", 3, 0, 8, 51033, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "平凡な未鑑定トレジャーを1個獲得する。", 1, 1, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", "ジータのグリルド・グラッジチャンク", 0, 52034, 2, "ジータの\nグリルド・グラッジチャンク", "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 10, 9, 51034, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "貴重な未鑑定トレジャーを1個獲得する。", 1, 10, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", "ルイズマリーの成功しすぎてるフライ", 0, 52035, 0, "ルイズマリーの\n成功しすぎてるフライ", "ランダムなイベントを発生させる。", 3, 10, 4, 51035, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "なし", 1, 0, "ジオ・ゲヘナの空の覇者にして煉華のホウオウが\n己への信仰心を集めるべく作らせたお菓子。中の\n餡はしっとり甘く、上品な味わい。ジオ・ゲヘナ\n銘菓の名を恣にする日も近い、かもしれない。", "ホウオウの食べる権利をあげます！饅頭", 0, 52036, 3, "ホウオウの\n食べる権利をあげます！饅頭", "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「12」に、次とさらにその次のターンに振るダイスの目を必ず「1」にする。", 1, 0, 7, 51036, 1, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (3, "平凡な未鑑定トレジャーを3個獲得する。", 1, 11, "ナナカ考案＆監修のウエハース。おまけシールの\nコレクション性を高くした結果ランドソル中で\n大ブーム…だが肝心の菓子が棄てられる事態に。\n解決のため、王女が自ら口を開いたのだった。", "魔法少女ナナカちゃんウエハース", 1, 52037, 0, "魔法少女\nナナカちゃんウエハース", "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計1回止まるまで継続する。", 2, 10, 6, 51037, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "福引券を1枚獲得する。", 2, 3, "アリサ特製の包み焼き。伝承の怪物とよく似た\n魔物を材料にしているため、いつしかその怪物と\n同じ名で呼ばれるようになったという。鋭い鎌と\n火傷に気を付けながら、熱々を召し上がれ。", "アリサがお届け♪リノセウスの包み焼き", 0, 52038, 1, "アリサがお届け♪\nリノセウスの包み焼き", "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 10, 1, 51038, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, "なし", 2, 0, "イリヤの愛飲する赤く鮮やかなトマトジュース。\n伝説の吸血鬼に相応しいクオリティを、と求めた\n末に辿り着いた究極の逸品。しかしお値段も相応\nに張るため、特別な時にしか飲めないらしい。", "イリヤの†ブラッディ†トマトジュース", 0, 52039, 2, "イリヤの†ブラッディ†\nトマトジュース", "2ターンの間、すべての「マイルマス」をランク3の「ショップマス」に変化させる。", 3, 0, 12, 51039, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "次にダイスを振った際、2マス多く進める。", 2, 2, "【王宮騎士団】遠征時の定番メニューにマツリが\nアレンジを加え調理したプレート。苦手な野菜を\nなくしてお肉倍増を目論んだのが配膳前にバレ、\nジュンの指導できちんと野菜も入れさせられた。", "マツリの活力源～騎士団遠征プレート～", 0, 52040, 3, "マツリの活力源\n～騎士団遠征プレート～", "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, 11, 9, 51040, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "平凡な未鑑定トレジャーを1個獲得する。", 1, 1, "ヴァンピィがよく振舞ってくれる特製スープ。\nどうやら家庭料理のようで、素朴な味わいが\n人々を郷愁へと誘う。昔誰かと並んで一緒に\n肉団子を作ったような…とはヴァンピィの言葉。", "ヴァンピィちゃんのさいきょースープ", 0, 52041, 2, "ヴァンピィちゃんの\nさいきょースープ", "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「7」に、次のターンに振るダイスの目を必ず「2」にする。", 1, 10, 7, 51041, 1, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, "なし", 20002, 0, "アイラが愛する赤麦しゅわ。彼女の熱い要望で\n酩酊感ゼロの子供向けも生産開始した。曰くこの\n美味しさを広めるためとのことだが、本当は単に\n老若男女皆で楽しく飲みたいだけかもしれない。", "アイラのみんなで飲みたい赤麦しゅわ", 0, 52042, 2, "アイラのみんなで\n飲みたい赤麦しゅわ", "ダイスを2回振るまでの間、出る目が「4・5・6」のみになる", 1, 0, 2, 51042, 0, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (1, "なし", 1, 12, "レイが釣った魚の塩焼き。試行錯誤の末に理想の\n塩と火の加減にたどり着いた。調理は真剣勝負で\n釣りと同様、己の心との戦いだという。レイも\n時々、集中しすぎて仲間に心配されているとか。", "レイの釣りたて鮮魚七輪塩焼き", 0, 52043, 2, "レイの釣りたて\n鮮魚七輪塩焼き", "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で貴重な未鑑定トレジャーを3個獲得する。", 1, 10, 2, 51043, 1, 500);
INSERT INTO `v1_afc88fba8fd4cac1ef103c4468a1c2ef585eaaa47641adf62fe737bd58c677e0` VALUES (2, "福引券を1枚獲得する。", 30002, 3, "ユキノが偶然生み出した、青果実のアイスバー。\n元は誤って凍らせたものだが爽やかな味が好評で\nなんと久遠氷河を代表する手土産に。伸び続ける\n売り上げに本人が一番溶けそうになっている。", "ユキノ製ごろっと果実のかちこち氷菓子", 0, 52044, 1, "ユキノ製ごろっと果実の\nかちこち氷菓子", "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 1, 10, 3, 51044, 0, 500);
