INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 100, 100, "2024/03/31 12:00:00", 7, 1, "エリコ", "2030/12/17 14:59:59", 10142, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, 10.85);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 101, 101, "2024/03/31 12:00:00", 2, 1, "エリコ", "2030/12/17 14:59:59", 10142, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, 11.15);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 102, 102, "2024/03/31 12:00:00", 5, 7, "エリコ", "2030/12/17 14:59:59", 10142, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, 6.4);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 103, 103, "2024/03/31 12:00:00", 3, 3, "エリコ", "2030/12/17 14:59:59", 10142, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 104, 104, "2024/03/31 12:00:00", 8, 2, "エリコ", "2030/12/17 14:59:59", 10142, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, 2.43);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 105, 105, "2024/03/31 12:00:00", 3, 4, "エリコ", "2030/12/17 14:59:59", 10142, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, 9.08);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 106, 106, "2024/03/31 12:00:00", 6, 6, "エリコ", "2030/12/17 14:59:59", 10142, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 107, 107, "2024/03/31 12:00:00", 8, 3, "エリコ", "2030/12/17 14:59:59", 10142, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, 10.6);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 108, 108, "2024/03/31 12:00:00", 4, 6, "エリコ", "2030/12/17 14:59:59", 10142, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, 7.54);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 109, 109, "2024/03/31 12:00:00", 7, 2, "エリコ", "2030/12/17 14:59:59", 10142, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, 11.5);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 110, 110, "2024/03/31 12:00:00", 1, 2, "エリコ", "2030/12/17 14:59:59", 10142, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, 8.83);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 111, 111, "2024/03/31 12:00:00", 2, 1, "エリコ", "2030/12/17 14:59:59", 10142, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, 6.68);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 112, 112, "2024/03/31 12:00:00", 2, 6, "エリコ", "2030/12/17 14:59:59", 10142, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, 8.8);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 113, 113, "2024/03/31 12:00:00", 1, 2, "エリコ", "2030/12/17 14:59:59", 10142, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, 8.9);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 114, 114, "2024/03/31 12:00:00", 5, 2, "エリコ", "2030/12/17 14:59:59", 10142, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, 3.7);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 200, 200, "2024/03/31 12:00:00", 2, 1, "マコト", "2030/12/17 14:59:59", 10142, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, 7.6);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 201, 201, "2024/03/31 12:00:00", 1, 2, "マコト", "2030/12/17 14:59:59", 10142, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, 2.25);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 202, 202, "2024/03/31 12:00:00", 1, 2, "マコト", "2030/12/17 14:59:59", 10142, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, 3.28);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 203, 203, "2024/03/31 12:00:00", 1, 4, "マコト", "2030/12/17 14:59:59", 10142, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, 2.98);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 204, 204, "2024/03/31 12:00:00", 1, 4, "マコト", "2030/12/17 14:59:59", 10142, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, 3.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 205, 205, "2024/03/31 12:00:00", 2, 1, "マコト", "2030/12/17 14:59:59", 10142, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, 10.3);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 206, 206, "2024/03/31 12:00:00", 1, 6, "マコト", "2030/12/17 14:59:59", 10142, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, 2.93);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 207, 207, "2024/03/31 12:00:00", 1, 3, "マコト", "2030/12/17 14:59:59", 10142, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, 9.3);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 208, 208, "2024/03/31 12:00:00", 1, 4, "マコト", "2030/12/17 14:59:59", 10142, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, 6.9);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 209, 209, "2024/03/31 12:00:00", 2, 2, "マコト", "2030/12/17 14:59:59", 10142, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 210, 210, "2024/03/31 12:00:00", 2, 1, "マコト", "2030/12/17 14:59:59", 10142, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, 5.95);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 211, 211, "2024/03/31 12:00:00", 1, 2, "マコト", "2030/12/17 14:59:59", 10142, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, 6.73);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 212, 212, "2024/03/31 12:00:00", 2, 2, "マコト", "2030/12/17 14:59:59", 10142, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 213, 213, "2024/03/31 12:00:00", 2, 1, "マコト", "2030/12/17 14:59:59", 10142, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, 6.65);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (24, 0.0, 0.0, 214, 214, "2024/03/31 12:00:00", 1, 6, "マコト", "2030/12/17 14:59:59", 10142, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, 3.05);
