INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (100, 10142, 7, 100, "2030/12/17 14:59:59", 1, 0.0, 129901, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 10.85);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (101, 10142, 2, 101, "2030/12/17 14:59:59", 1, 0.0, 129901, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 11.15);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (102, 10142, 5, 102, "2030/12/17 14:59:59", 7, 0.0, 129901, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 6.4);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (103, 10142, 3, 103, "2030/12/17 14:59:59", 3, 0.0, 129901, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (104, 10142, 8, 104, "2030/12/17 14:59:59", 2, 0.0, 129901, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 2.43);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (105, 10142, 3, 105, "2030/12/17 14:59:59", 4, 0.0, 129901, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 9.08);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (106, 10142, 6, 106, "2030/12/17 14:59:59", 6, 0.0, 129901, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (107, 10142, 8, 107, "2030/12/17 14:59:59", 3, 0.0, 129901, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 10.6);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (108, 10142, 4, 108, "2030/12/17 14:59:59", 6, 0.0, 129901, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 7.54);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (109, 10142, 7, 109, "2030/12/17 14:59:59", 2, 0.0, 129901, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 11.5);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (110, 10142, 1, 110, "2030/12/17 14:59:59", 2, 0.0, 129901, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 8.83);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (111, 10142, 2, 111, "2030/12/17 14:59:59", 1, 0.0, 129901, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 6.68);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (112, 10142, 2, 112, "2030/12/17 14:59:59", 6, 0.0, 129901, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 8.8);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (113, 10142, 1, 113, "2030/12/17 14:59:59", 2, 0.0, 129901, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 8.9);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (114, 10142, 5, 114, "2030/12/17 14:59:59", 2, 0.0, 129901, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, "2024/03/31 12:00:00", 24, "エリコ", 3.7);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (200, 10142, 2, 200, "2030/12/17 14:59:59", 1, 0.0, 129801, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, "2024/03/31 12:00:00", 24, "マコト", 7.6);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (201, 10142, 1, 201, "2030/12/17 14:59:59", 2, 0.0, 129801, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, "2024/03/31 12:00:00", 24, "マコト", 2.25);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (202, 10142, 1, 202, "2030/12/17 14:59:59", 2, 0.0, 129801, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, "2024/03/31 12:00:00", 24, "マコト", 3.28);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (203, 10142, 1, 203, "2030/12/17 14:59:59", 4, 0.0, 129801, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 2.98);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (204, 10142, 1, 204, "2030/12/17 14:59:59", 4, 0.0, 129801, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 3.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (205, 10142, 2, 205, "2030/12/17 14:59:59", 1, 0.0, 129801, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 10.3);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (206, 10142, 1, 206, "2030/12/17 14:59:59", 6, 0.0, 129801, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 2.93);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (207, 10142, 1, 207, "2030/12/17 14:59:59", 3, 0.0, 129801, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 9.3);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (208, 10142, 1, 208, "2030/12/17 14:59:59", 4, 0.0, 129801, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, "2024/03/31 12:00:00", 24, "マコト", 6.9);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (209, 10142, 2, 209, "2030/12/17 14:59:59", 2, 0.0, 129801, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (210, 10142, 2, 210, "2030/12/17 14:59:59", 1, 0.0, 129801, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, "2024/03/31 12:00:00", 24, "マコト", 5.95);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (211, 10142, 1, 211, "2030/12/17 14:59:59", 2, 0.0, 129801, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, "2024/03/31 12:00:00", 24, "マコト", 6.73);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (212, 10142, 2, 212, "2030/12/17 14:59:59", 2, 0.0, 129801, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 0.0);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (213, 10142, 2, 213, "2030/12/17 14:59:59", 1, 0.0, 129801, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, "2024/03/31 12:00:00", 24, "マコト", 6.65);
INSERT INTO `v1_b1301dbcdb6fb7d610735dcc0d30c6efaefb66c8c7070853658e575c4b913ea3` VALUES (214, 10142, 1, 214, "2030/12/17 14:59:59", 6, 0.0, 129801, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, "2024/03/31 12:00:00", 24, "マコト", 3.05);
