INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "カリン", 190804, 1, 1, 0, 60000, 0, "", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 2, 1, 0, 60000, 1, "", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 3, 1, 0, 60000, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 4, 2, 0, 60000, 1, "", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 5, 1, 0, 60000, 2, "", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 6, 1, 0, 60000, 2, "", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 7, 2, 0, 60000, 2, "", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 8, 1, 0, 60000, 3, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 9, 1, 0, 60000, 3, "", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 10, 1, 0, 60000, 3, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 11, 1, 0, 60000, 3, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 12, 2, 0, 60000, 3, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 13, 1, 0, 60000, 4, "", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 14, 1, 0, 60000, 4, "", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 15, 1, 0, 60000, 4, "", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 16, 1, 0, 60000, 4, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 17, 2, 0, 60000, 4, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 2, "カリン", 190804, 18, 0, 0, 60000, 0, "", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 3, "カリン", 190804, 19, 0, 7, 60000, 0, "", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "ハツネ", 101201, 20, 0, 1, 80001, 0, "", "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "ハツネ", 101201, 21, 0, 1, 80001, 0, "", "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "シオリ", 103801, 22, 0, 1, 80001, 0, "", "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "シオリ", 103801, 23, 0, 1, 80001, 0, "", "来年も、\n一緒にいられたら…\nうれしいな", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "キョウカ", 103601, 24, 0, 2, 80001, 0, "", "あなたと一緒にいるようになって、\nもう1年ですか…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "キョウカ", 103601, 25, 0, 2, 80001, 0, "", "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ミミ", 102001, 26, 0, 2, 80001, 0, "", "おにいちゃんとあってから、\n1年たったよ～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ミミ", 102001, 27, 0, 2, 80001, 0, "", "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "ミソギ", 100401, 28, 0, 2, 80001, 0, "", "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "ミソギ", 100401, 29, 0, 2, 80001, 0, "", "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "マホ", 101001, 30, 0, 3, 80001, 0, "", "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "マホ", 101001, 31, 0, 3, 80001, 0, "", "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "イリヤ", 104401, 32, 0, 3, 80001, 0, "", "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "イリヤ", 104401, 33, 0, 3, 80001, 0, "", "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "カオリ", 101701, 34, 0, 3, 80001, 0, "", "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "カオリ", 101701, 35, 0, 3, 80001, 0, "", "これからも、どうぞよろしく\nお願いするさ～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "サレン", 102801, 36, 0, 4, 80001, 0, "", "あんたと出会ってからもうそんなに\nたつのね～", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "サレン", 102801, 37, 0, 4, 80001, 0, "", "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "スズメ", 102501, 38, 0, 4, 80001, 0, "", "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "スズメ", 102501, 39, 0, 4, 80001, 0, "", "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "タマキ", 104601, 40, 0, 5, 80001, 0, "", "振り返れば、今までいろんなことが\nあったにゃあ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "タマキ", 104601, 41, 0, 5, 80001, 0, "", "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ミフユ", 104801, 42, 0, 5, 80001, 0, "", "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ミフユ", 104801, 43, 0, 5, 80001, 0, "", "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "アキノ", 103201, 44, 0, 6, 80001, 0, "", "あなたさまと出会ってもう1年…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "アキノ", 103201, 45, 0, 6, 80001, 0, "", "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ユカリ", 103401, 46, 0, 6, 80001, 0, "", "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ユカリ", 103401, 47, 0, 6, 80001, 0, "", "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "クリスティーナ", 107101, 48, 0, 7, 80001, 0, "", "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "クリスティーナ", 107101, 49, 0, 7, 80001, 0, "", "ともかくこれからも\n愉しませてくれよ☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ジュン", 104701, 50, 0, 7, 80001, 0, "", "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ジュン", 104701, 51, 0, 7, 80001, 0, "", "見えないから分からないって？\nそれもそうだな…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "トモ", 103701, 52, 0, 8, 80001, 0, "", "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "トモ", 103701, 53, 0, 8, 80001, 0, "", "これからもよろしくね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "マツリ", 100501, 54, 0, 8, 80001, 0, "", "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "マツリ", 100501, 55, 0, 8, 80001, 0, "", "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "ミヤコ", 100701, 56, 0, 9, 80001, 0, "", "オマエと会って1年なの！\n全然成仏もできなかったし、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "ミヤコ", 100701, 57, 0, 9, 80001, 0, "", "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "シノブ", 103101, 58, 0, 9, 80001, 0, "", "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "シノブ", 103101, 59, 0, 9, 80001, 0, "", "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "アンナ", 100901, 60, 0, 10, 80001, 0, "", "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "アンナ", 100901, 61, 0, 10, 80001, 0, "", "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ナナカ", 101301, 62, 0, 10, 80001, 0, "", "君と出会ってもう1年だって！\n早いねー！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ナナカ", 101301, 63, 0, 10, 80001, 0, "", "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "ミツキ", 105101, 64, 0, 11, 80001, 0, "", "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "ミツキ", 105101, 65, 0, 11, 80001, 0, "", "これからも、いろんなところを\n調べさせてね？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ルカ", 105601, 66, 0, 11, 80001, 0, "", "お前さんと出会ってもう1年か\n助太刀するつもりが", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ルカ", 105601, 67, 0, 11, 80001, 0, "", "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "エリコ", 102701, 68, 0, 11, 80001, 0, "", "早いものですわね、あなた様と\nお会いして、もう1年…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "エリコ", 102701, 69, 0, 11, 80001, 0, "", "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "アヤネ", 102301, 70, 0, 12, 80001, 0, "", "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "アヤネ", 102301, 71, 0, 12, 80001, 0, "", "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "チカ", 104201, 72, 0, 12, 80001, 0, "", "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "チカ", 104201, 73, 0, 12, 80001, 0, "", "私のことを見守ってください\nできることなら、ずっと…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "クルミ", 102101, 74, 0, 12, 80001, 0, "", "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "クルミ", 102101, 75, 0, 12, 80001, 0, "", "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "シズル", 104901, 76, 0, 13, 80001, 0, "", "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "シズル", 104901, 77, 0, 13, 80001, 0, "", "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "リノ", 101101, 78, 0, 13, 80001, 0, "", "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "リノ", 101101, 79, 0, 13, 80001, 0, "", "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "ユイ", 100201, 80, 0, 14, 80001, 0, "", "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "ユイ", 100201, 81, 0, 14, 80001, 0, "", "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "ヒヨリ", 100101, 82, 0, 14, 80001, 0, "", "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "ヒヨリ", 100101, 83, 0, 14, 80001, 0, "", "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "レイ", 100301, 84, 0, 14, 80001, 0, "", "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "レイ", 100301, 85, 0, 14, 80001, 0, "", "キミに背中を預けられる日が、\n早くきますように", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "ペコリーヌ", 105801, 86, 0, 15, 80001, 0, "", "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "ペコリーヌ", 105801, 87, 0, 15, 80001, 0, "", "これからもずっと、覚えていて\nくださいね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "コッコロ", 105901, 88, 0, 15, 80001, 0, "", "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "コッコロ", 105901, 89, 0, 15, 80001, 0, "", "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "キャル", 106001, 90, 0, 15, 80001, 0, "", "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "キャル", 106001, 91, 0, 15, 80001, 0, "", "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "スズメ", 112101, 92, 1, 0, 60001, 0, "", "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 93, 1, 0, 60001, 1, "", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 94, 1, 0, 60001, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 95, 1, 0, 60001, 1, "", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 96, 1, 0, 60001, 1, "", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 97, 2, 0, 60001, 1, "", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 98, 1, 0, 60001, 2, "", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 99, 1, 0, 60001, 2, "", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 100, 1, 0, 60001, 2, "", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 101, 1, 0, 60001, 2, "", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 102, 2, 0, 60001, 2, "", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 103, 1, 0, 60001, 3, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 104, 1, 0, 60001, 3, "", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 105, 1, 0, 60001, 3, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 106, 1, 0, 60001, 3, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 107, 1, 0, 60001, 3, "", "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 108, 1, 0, 60001, 3, "", "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 109, 2, 0, 60001, 3, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 110, 1, 0, 60001, 4, "", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 111, 1, 0, 60001, 4, "", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 112, 1, 0, 60001, 4, "", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 113, 1, 0, 60001, 4, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 114, 1, 0, 60001, 4, "", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 115, 1, 0, 60001, 4, "", "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 116, 2, 0, 60001, 4, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 2, "スズメ", 112101, 117, 0, 0, 60001, 0, "", "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 3, "スズメ", 112101, 118, 0, 7, 60001, 0, "", "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "ペコリーヌ", 105801, 119, 0, 1, 80003, 0, "", "ヒューヒュー♪\nドンドンパフパフー♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "コッコロ", 105901, 120, 0, 1, 80003, 0, "", "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "コッコロ", 105901, 121, 0, 1, 80003, 0, "", "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "キャル", 106001, 122, 0, 1, 80003, 0, "", "え？　ちょっと\nいきなりなにっ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "キャル", 106001, 123, 0, 1, 80003, 0, "", "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "ペコリーヌ", 105801, 124, 0, 1, 80003, 0, "", "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 3, "ペコリーヌ", 105801, 125, 0, 1, 80003, 0, "", "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 3, "コッコロ", 105901, 126, 0, 1, 80003, 0, "", "ですからその日に向けて\n今から気分を盛り上げているのです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 3, "キャル", 106001, 127, 0, 1, 80003, 0, "", "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 4, "コッコロ", 105901, 128, 0, 1, 80003, 0, "", "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 4, "ペコリーヌ", 105801, 129, 0, 1, 80003, 0, "", "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 4, "キャル", 106001, 130, 0, 1, 80003, 0, "", "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 5, "コッコロ", 105901, 131, 0, 1, 80003, 0, "", "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 5, "キャル", 106001, 132, 0, 2, 80003, 0, "", "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "カリン", 190801, 133, 0, 2, 80003, 0, "", "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 6, "キャル", 106001, 134, 0, 2, 80003, 0, "", "げっ…カリンじゃない\nなんでもないわよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "カリン", 190801, 135, 0, 2, 80003, 0, "", "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 7, "キャル", 106001, 136, 0, 2, 80003, 0, "", "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 3, "カリン", 190801, 137, 0, 2, 80003, 0, "", "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 8, "キャル", 106001, 138, 0, 2, 80003, 0, "", "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 9, "キャル", 106001, 139, 0, 2, 80003, 0, "", "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 4, "カリン", 190801, 140, 0, 2, 80003, 0, "", "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 10, "キャル", 106001, 141, 0, 2, 80003, 0, "", "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 5, "カリン", 190801, 142, 0, 2, 80003, 0, "", "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 11, "キャル", 106001, 143, 0, 2, 80003, 0, "", "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 6, "コッコロ", 105901, 144, 0, 3, 80003, 0, "", "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 5, "ペコリーヌ", 105801, 145, 0, 3, 80003, 0, "", "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 7, "コッコロ", 105901, 146, 0, 3, 80003, 0, "", "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 8, "コッコロ", 105901, 147, 0, 3, 80003, 0, "", "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 6, "ペコリーヌ", 105801, 148, 0, 3, 80003, 0, "", "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 7, "ペコリーヌ", 105801, 149, 0, 3, 80003, 0, "", "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 9, "コッコロ", 105901, 150, 0, 3, 80003, 0, "", "あとは、牧場に素敵な温泉が\n出来たとか…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 8, "ペコリーヌ", 105801, 151, 0, 3, 80003, 0, "", "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 10, "コッコロ", 105901, 152, 0, 3, 80003, 0, "", "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 9, "ペコリーヌ", 105801, 153, 0, 3, 80003, 0, "", "えへへ、ありがとうございます\nコッコロちゃん！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 11, "コッコロ", 105901, 154, 0, 3, 80003, 0, "", "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 10, "ペコリーヌ", 105801, 155, 0, 3, 80003, 0, "", "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 11, "ペコリーヌ", 105801, 156, 0, 4, 80003, 0, "", "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 12, "キャル", 106001, 157, 0, 4, 80003, 0, "", "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 12, "ペコリーヌ", 105801, 158, 0, 4, 80003, 0, "", "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 13, "キャル", 106001, 159, 0, 4, 80003, 0, "", "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 13, "ペコリーヌ", 105801, 160, 0, 4, 80003, 0, "", "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 14, "キャル", 106001, 161, 0, 4, 80003, 0, "", "…積極的？\nなんの話よ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 14, "ペコリーヌ", 105801, 162, 0, 4, 80003, 0, "", "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 15, "ペコリーヌ", 105801, 163, 0, 4, 80003, 0, "", "ムーサの町で\nきゃるきゃる～ん☆　って！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 15, "キャル", 106001, 164, 0, 4, 80003, 0, "", "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 16, "ペコリーヌ", 105801, 165, 0, 4, 80003, 0, "", "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 16, "キャル", 106001, 166, 0, 4, 80003, 0, "", "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 17, "キャル", 106001, 167, 0, 4, 80003, 0, "", "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 18, "キャル", 106001, 168, 0, 5, 80003, 0, "", "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 12, "コッコロ", 105901, 169, 0, 5, 80003, 0, "", "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 19, "キャル", 106001, 170, 0, 5, 80003, 0, "", "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 13, "コッコロ", 105901, 171, 0, 5, 80003, 0, "", "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 20, "キャル", 106001, 172, 0, 5, 80003, 0, "", "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 14, "コッコロ", 105901, 173, 0, 5, 80003, 0, "", "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 21, "キャル", 106001, 174, 0, 5, 80003, 0, "", "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 15, "コッコロ", 105901, 175, 0, 5, 80003, 0, "", "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 22, "キャル", 106001, 176, 0, 5, 80003, 0, "", "かくし芸？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 16, "コッコロ", 105901, 177, 0, 5, 80003, 0, "", "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "キャル", 106001, 178, 0, 5, 80003, 0, "", "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 17, "ペコリーヌ", 105801, 179, 0, 6, 80003, 0, "", "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 24, "キャル", 106001, 180, 0, 6, 80003, 0, "", "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 17, "コッコロ", 105901, 181, 0, 6, 80003, 0, "", "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 18, "ペコリーヌ", 105801, 182, 0, 6, 80003, 0, "", "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 25, "キャル", 106001, 183, 0, 6, 80003, 0, "", "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 18, "コッコロ", 105901, 184, 0, 6, 80003, 0, "", "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 26, "キャル", 106001, 185, 0, 6, 80003, 0, "", "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 27, "キャル", 106001, 186, 0, 6, 80003, 0, "", "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 28, "キャル", 106001, 187, 0, 6, 80003, 0, "", "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 19, "ペコリーヌ", 105801, 188, 0, 6, 80003, 0, "", "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 19, "コッコロ", 105901, 189, 0, 6, 80003, 0, "", "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 29, "キャル", 106001, 190, 0, 6, 80003, 0, "", "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 30, "キャル", 106001, 191, 0, 7, 80003, 0, "", "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 31, "キャル", 106001, 192, 0, 7, 80003, 0, "", "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 32, "キャル", 106001, 193, 0, 7, 80003, 0, "", "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 33, "キャル", 106001, 194, 0, 7, 80003, 0, "", "あー\nあー\n…声、これで大丈夫かしら？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 34, "キャル", 106001, 195, 0, 7, 80003, 0, "", "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 35, "キャル", 106001, 196, 0, 7, 80003, 0, "", "オイッスー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 36, "キャル", 106001, 197, 0, 7, 80003, 0, "", "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 37, "キャル", 106001, 198, 0, 7, 80003, 0, "", "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 38, "キャル", 106001, 199, 0, 7, 80003, 0, "", "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 20, "コッコロ", 105901, 200, 0, 8, 80003, 0, "", "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 21, "コッコロ", 105901, 201, 0, 8, 80003, 0, "", "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 22, "コッコロ", 105901, 202, 0, 8, 80003, 0, "", "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "コッコロ", 105901, 203, 0, 8, 80003, 0, "", "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 24, "コッコロ", 105901, 204, 0, 8, 80003, 0, "", "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 25, "コッコロ", 105901, 205, 0, 8, 80003, 0, "", "オイッスー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "コッコロ", 105901, 206, 0, 8, 80003, 0, "", "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "コッコロ", 105901, 207, 0, 8, 80003, 0, "", "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 28, "コッコロ", 105901, 208, 0, 8, 80003, 0, "", "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 20, "ペコリーヌ", 105801, 209, 0, 9, 80003, 0, "", "オイッスー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 21, "ペコリーヌ", 105801, 210, 0, 9, 80003, 0, "", "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 22, "ペコリーヌ", 105801, 211, 0, 9, 80003, 0, "", "あと６日で2.5周年ですか…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "ペコリーヌ", 105801, 212, 0, 9, 80003, 0, "", "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 24, "ペコリーヌ", 105801, 213, 0, 9, 80003, 0, "", "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 25, "ペコリーヌ", 105801, 214, 0, 9, 80003, 0, "", "オイッスー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "ペコリーヌ", 105801, 215, 0, 9, 80003, 0, "", "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "ペコリーヌ", 105801, 216, 0, 9, 80003, 0, "", "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 28, "ペコリーヌ", 105801, 217, 0, 9, 80003, 0, "", "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 29, "ペコリーヌ", 105801, 218, 0, 9, 80003, 0, "", "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 30, "ペコリーヌ", 105801, 219, 0, 9, 80003, 0, "", "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 31, "ペコリーヌ", 105801, 220, 0, 10, 80003, 0, "", "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 29, "コッコロ", 105901, 221, 0, 10, 80003, 0, "", "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 32, "ペコリーヌ", 105801, 222, 0, 10, 80003, 0, "", "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 39, "キャル", 106001, 223, 0, 10, 80003, 0, "", "…………", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 30, "コッコロ", 105901, 224, 0, 10, 80003, 0, "", "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 40, "キャル", 106001, 225, 0, 10, 80003, 0, "", "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 33, "ペコリーヌ", 105801, 226, 0, 10, 80003, 0, "", "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 41, "キャル", 106001, 227, 0, 10, 80003, 0, "", "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 34, "ペコリーヌ", 105801, 228, 0, 10, 80003, 0, "", "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 35, "ペコリーヌ", 105801, 229, 0, 10, 80003, 0, "", "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 42, "キャル", 106001, 230, 0, 10, 80003, 0, "", "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 31, "コッコロ", 105901, 231, 0, 10, 80003, 0, "", "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 43, "キャル", 106001, 232, 0, 10, 80003, 0, "", "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 36, "ペコリーヌ", 105801, 233, 0, 10, 80003, 0, "", "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 6, "カリン", 190801, 234, 0, 11, 80003, 0, "", "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 7, "カリン", 190801, 235, 0, 11, 80003, 0, "", "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 8, "カリン", 190801, 236, 0, 11, 80003, 0, "", "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 9, "カリン", 190801, 237, 0, 11, 80003, 0, "", "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 10, "カリン", 190801, 238, 0, 11, 80003, 0, "", "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 11, "カリン", 190801, 239, 0, 11, 80003, 0, "", "次にコッコロさんですが\nこちらは発声練習をしています", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 12, "カリン", 190801, 240, 0, 11, 80003, 0, "", "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 13, "カリン", 190801, 241, 0, 11, 80003, 0, "", "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 14, "カリン", 190801, 242, 0, 11, 80003, 0, "", "あれ？\nもしかして緊張で気を失って…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 15, "カリン", 190801, 243, 0, 11, 80003, 0, "", "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 16, "カリン", 190801, 244, 0, 11, 80003, 0, "", "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 17, "カリン", 190801, 245, 0, 11, 80003, 0, "", "それでは\nまた明日お会いしましょう♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 44, "キャル", 106001, 246, 0, 12, 80003, 0, "", "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 37, "ペコリーヌ", 105801, 247, 0, 12, 80003, 0, "", "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 32, "コッコロ", 105901, 248, 0, 12, 80003, 0, "", "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 45, "キャル", 106001, 249, 0, 12, 80003, 0, "", "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 38, "ペコリーヌ", 105801, 250, 0, 12, 80003, 0, "", "ひどいですよぉキャルちゃ～ん！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 33, "コッコロ", 105901, 251, 0, 12, 80003, 0, "", "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 39, "ペコリーヌ", 105801, 252, 0, 12, 80003, 0, "", "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 46, "キャル", 106001, 253, 0, 12, 80003, 0, "", "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 34, "コッコロ", 105901, 254, 0, 12, 80003, 0, "", "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 40, "ペコリーヌ", 105801, 255, 0, 12, 80003, 0, "", "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 41, "ペコリーヌ", 105801, 256, 0, 13, 80003, 0, "", "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 47, "キャル", 106001, 257, 0, 13, 80003, 0, "", "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 42, "ペコリーヌ", 105801, 258, 0, 13, 80003, 0, "", "忘れてきちゃいました☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 48, "キャル", 106001, 259, 0, 13, 80003, 0, "", "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 35, "コッコロ", 105901, 260, 0, 13, 80003, 0, "", "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 49, "キャル", 106001, 261, 0, 13, 80003, 0, "", "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 43, "ペコリーヌ", 105801, 262, 0, 13, 80003, 0, "", "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 50, "キャル", 106001, 263, 0, 13, 80003, 0, "", "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 36, "コッコロ", 105901, 264, 0, 13, 80003, 0, "", "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 51, "キャル", 106001, 265, 0, 13, 80003, 0, "", "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 37, "コッコロ", 105901, 266, 0, 13, 80003, 0, "", "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 52, "キャル", 106001, 267, 0, 13, 80003, 0, "", "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 44, "ペコリーヌ", 105801, 268, 0, 13, 80003, 0, "", "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 38, "コッコロ", 105901, 269, 0, 13, 80003, 0, "", "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 45, "ペコリーヌ", 105801, 270, 0, 13, 80003, 0, "", "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 53, "キャル", 106001, 271, 0, 13, 80003, 0, "", "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 46, "ペコリーヌ", 105801, 272, 0, 13, 80003, 0, "", "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 18, "カリン", 190801, 273, 0, 14, 80003, 0, "", "こんにちは\n【ギルド管理協会】のカリンです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 19, "カリン", 190801, 274, 0, 14, 80003, 0, "", "昨日の生放送は\nいかがだったでしょうか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 20, "カリン", 190801, 275, 0, 14, 80003, 0, "", "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 21, "カリン", 190801, 276, 0, 14, 80003, 0, "", "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 22, "カリン", 190801, 277, 0, 14, 80003, 0, "", "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "カリン", 190801, 278, 0, 14, 80003, 0, "", "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 24, "カリン", 190801, 279, 0, 14, 80003, 0, "", "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 25, "カリン", 190801, 280, 0, 14, 80003, 0, "", "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "カリン", 190801, 281, 0, 14, 80003, 0, "", "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "カリン", 190801, 282, 0, 14, 80003, 0, "", "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 47, "ペコリーヌ", 105801, 283, 0, 15, 80003, 0, "", "準備はいいですかっ？\n…いっきますよー！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 54, "キャル", 106001, 284, 0, 15, 80003, 0, "", "祝！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 39, "コッコロ", 105901, 285, 0, 15, 80003, 0, "", "2.5周年！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 48, "ペコリーヌ", 105801, 286, 0, 15, 80003, 0, "", "おめでとうございま～す☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 55, "キャル", 106001, 287, 0, 15, 80003, 0, "", "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 40, "コッコロ", 105901, 288, 0, 15, 80003, 0, "", "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 49, "ペコリーヌ", 105801, 289, 0, 15, 80003, 0, "", "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 56, "キャル", 106001, 290, 0, 15, 80003, 0, "", "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 41, "コッコロ", 105901, 291, 0, 15, 80003, 0, "", "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 57, "キャル", 106001, 292, 0, 15, 80003, 0, "", "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 50, "ペコリーヌ", 105801, 293, 0, 15, 80003, 0, "", "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 58, "キャル", 106001, 294, 0, 15, 80003, 0, "", "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 51, "ペコリーヌ", 105801, 295, 0, 15, 80003, 0, "", "記念日だから\n平気平気♪\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 59, "キャル", 106001, 296, 0, 15, 80003, 0, "", "意味分かんないわよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 42, "コッコロ", 105901, 297, 0, 15, 80003, 0, "", "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 43, "コッコロ", 105901, 298, 0, 15, 80003, 0, "", "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 52, "ペコリーヌ", 105801, 299, 0, 15, 80003, 0, "", "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 60, "キャル", 106001, 300, 0, 15, 80003, 0, "", "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 44, "コッコロ", 105901, 301, 0, 15, 80003, 0, "", "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 53, "ペコリーヌ", 105801, 302, 0, 15, 80003, 0, "", "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 54, "ペコリーヌ・コッコロ・キャル", 105801, 303, 0, 15, 80003, 0, "105901,45,106001,61", "2.5周年\nありがとうございます！", 1);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "カリン", 190804, 304, 1, 0, 60002, 0, "", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 305, 1, 0, 60002, 1, "", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 306, 1, 0, 60002, 1, "", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 307, 1, 0, 60002, 1, "", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 308, 1, 0, 60002, 1, "", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 309, 2, 0, 60002, 1, "", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 310, 1, 0, 60002, 2, "", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 311, 1, 0, 60002, 2, "", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 312, 1, 0, 60002, 2, "", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 313, 1, 0, 60002, 2, "", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 314, 2, 0, 60002, 2, "", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 315, 1, 0, 60002, 3, "", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 316, 1, 0, 60002, 3, "", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 317, 1, 0, 60002, 3, "", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 318, 1, 0, 60002, 3, "", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 319, 1, 0, 60002, 3, "", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 320, 1, 0, 60002, 3, "", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 321, 2, 0, 60002, 3, "", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 322, 1, 0, 60002, 4, "", "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 323, 1, 0, 60002, 4, "", "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 324, 1, 0, 60002, 4, "", "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 325, 1, 0, 60002, 4, "", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 326, 1, 0, 60002, 4, "", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 327, 1, 0, 60002, 4, "", "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 0, "", 0, 328, 2, 0, 60002, 4, "", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 2, "カリン", 190804, 329, 0, 0, 60002, 0, "", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 3, "カリン", 190804, 330, 0, 7, 60002, 0, "", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 1, "コッコロ", 105901, 331, 0, 1, 80005, 0, "", "ささ、シェフィさまはこちらへ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 1, "シェフィ", 106401, 332, 0, 1, 80005, 0, "", "コッコロさん、どこへ行くの…？\n…え？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 1, "ペコリーヌ", 105801, 333, 0, 1, 80005, 0, "", "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 2, "コッコロ", 105901, 334, 0, 1, 80005, 0, "", "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 2, "シェフィ", 106401, 335, 0, 1, 80005, 0, "", "ええと…\n二人とも何をしているの？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 1, "キャル", 106001, 336, 0, 1, 80005, 0, "", "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 3, "シェフィ", 106401, 337, 0, 1, 80005, 0, "", "あの日…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 2, "ペコリーヌ", 105801, 338, 0, 1, 80005, 0, "", "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 3, "ペコリーヌ", 105801, 339, 0, 1, 80005, 0, "", "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 1, "カリン", 190801, 340, 0, 1, 80005, 0, "", "やばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 2, "キャル", 106001, 341, 0, 1, 80005, 0, "", "って、なにしれっと\n参加してんのよカリン！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 2, "カリン", 190801, 342, 0, 1, 80005, 0, "", "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 3, "カリン", 190801, 343, 0, 1, 80005, 0, "", "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 4, "ペコリーヌ", 105801, 344, 0, 1, 80005, 0, "", "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 4, "シェフィ", 106401, 345, 0, 1, 80005, 0, "", "えっ！？　ええとええと…！\nあ、あと14日…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 5, "ペコリーヌ", 105801, 346, 0, 2, 80005, 0, "", "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 3, "コッコロ", 105901, 347, 0, 2, 80005, 0, "", "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 4, "コッコロ", 105901, 348, 0, 2, 80005, 0, "", "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 6, "ペコリーヌ", 105801, 349, 0, 2, 80005, 0, "", "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 5, "コッコロ", 105901, 350, 0, 2, 80005, 0, "", "さすがでございます\nペコリーヌさま", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 3, "キャル", 106001, 351, 0, 2, 80005, 0, "", "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 5, "シェフィ", 106401, 352, 0, 2, 80005, 0, "", "しゃなり、しゃなり…\nふふ、案外できるものね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 7, "ペコリーヌ", 105801, 353, 0, 2, 80005, 0, "", "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 6, "コッコロ", 105901, 354, 0, 2, 80005, 0, "", "さ、次はキャルさまの\n番でございますよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 4, "キャル", 106001, 355, 0, 2, 80005, 0, "", "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 6, "シェフィ", 106401, 356, 0, 2, 80005, 0, "", "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 5, "キャル", 106001, 357, 0, 2, 80005, 0, "", "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 6, "キャル", 106001, 358, 0, 2, 80005, 0, "", "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 7, "コッコロ", 105901, 359, 0, 2, 80005, 0, "", "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 7, "キャル", 106001, 360, 0, 2, 80005, 0, "", "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 8, "コッコロ", 105901, 361, 0, 3, 80005, 0, "", "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 8, "ペコリーヌ", 105801, 362, 0, 3, 80005, 0, "", "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 9, "コッコロ", 105901, 363, 0, 3, 80005, 0, "", "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 7, "シェフィ", 106401, 364, 0, 3, 80005, 0, "", "聖学祭…って、なにかしら？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 10, "コッコロ", 105901, 365, 0, 3, 80005, 0, "", "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 8, "キャル", 106001, 366, 0, 3, 80005, 0, "", "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 9, "キャル", 106001, 367, 0, 3, 80005, 0, "", "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 9, "ペコリーヌ", 105801, 368, 0, 3, 80005, 0, "", "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 10, "キャル", 106001, 369, 0, 3, 80005, 0, "", "いや、そのまんますぎでしょ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 8, "シェフィ", 106401, 370, 0, 3, 80005, 0, "", "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 10, "ペコリーヌ", 105801, 371, 0, 3, 80005, 0, "", "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 9, "シェフィ", 106401, 372, 0, 3, 80005, 0, "", "えっ？　ええ…\nぎゅーっ…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 11, "キャル", 106001, 373, 0, 4, 80005, 0, "", "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 10, "シェフィ", 106401, 374, 0, 4, 80005, 0, "", "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 12, "キャル", 106001, 375, 0, 4, 80005, 0, "", "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 11, "コッコロ", 105901, 376, 0, 4, 80005, 0, "", "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 13, "キャル", 106001, 377, 0, 4, 80005, 0, "", "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 11, "ペコリーヌ", 105801, 378, 0, 4, 80005, 0, "", "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 14, "キャル", 106001, 379, 0, 4, 80005, 0, "", "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 15, "キャル", 106001, 380, 0, 4, 80005, 0, "", "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 11, "シェフィ", 106401, 381, 0, 4, 80005, 0, "", "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 12, "ペコリーヌ", 105801, 382, 0, 4, 80005, 0, "", "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 12, "コッコロ", 105901, 383, 0, 4, 80005, 0, "", "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 16, "キャル", 106001, 384, 0, 4, 80005, 0, "", "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 12, "シェフィ", 106401, 385, 0, 4, 80005, 0, "", "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 13, "ペコリーヌ", 105801, 386, 0, 5, 80005, 0, "", "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 14, "ペコリーヌ", 105801, 387, 0, 5, 80005, 0, "", "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 15, "ペコリーヌ", 105801, 388, 0, 5, 80005, 0, "", "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 17, "キャル", 106001, 389, 0, 5, 80005, 0, "", "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 18, "キャル", 106001, 390, 0, 5, 80005, 0, "", "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 13, "シェフィ", 106401, 391, 0, 5, 80005, 0, "", "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 13, "コッコロ", 105901, 392, 0, 5, 80005, 0, "", "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 16, "ペコリーヌ", 105801, 393, 0, 5, 80005, 0, "", "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 19, "キャル", 106001, 394, 0, 5, 80005, 0, "", "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 17, "ペコリーヌ", 105801, 395, 0, 5, 80005, 0, "", "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 18, "ペコリーヌ", 105801, 396, 0, 5, 80005, 0, "", "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 19, "ペコリーヌ", 105801, 397, 0, 5, 80005, 0, "", "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 4, "カリン", 190801, 398, 0, 6, 80005, 0, "", "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 5, "カリン", 190801, 399, 0, 6, 80005, 0, "", "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 20, "キャル", 106001, 400, 0, 6, 80005, 0, "", "…えっ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 20, "ペコリーヌ", 105801, 401, 0, 6, 80005, 0, "", "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 14, "シェフィ", 106401, 402, 0, 6, 80005, 0, "", "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 14, "コッコロ", 105901, 403, 0, 6, 80005, 0, "", "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 15, "シェフィ", 106401, 404, 0, 6, 80005, 0, "", "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 21, "キャル", 106001, 405, 0, 6, 80005, 0, "", "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 21, "ペコリーヌ", 105801, 406, 0, 6, 80005, 0, "", "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 22, "キャル", 106001, 407, 0, 6, 80005, 0, "", "ぶっ殺すぞ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 6, "カリン", 190801, 408, 0, 6, 80005, 0, "", "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 15, "コッコロ", 105901, 409, 0, 7, 80005, 0, "", "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "キャル", 106001, 410, 0, 7, 80005, 0, "", "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 16, "シェフィ", 106401, 411, 0, 7, 80005, 0, "", "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 22, "ペコリーヌ", 105801, 412, 0, 7, 80005, 0, "", "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 24, "キャル", 106001, 413, 0, 7, 80005, 0, "", "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 17, "シェフィ", 106401, 414, 0, 7, 80005, 0, "", "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 16, "コッコロ", 105901, 415, 0, 7, 80005, 0, "", "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 18, "シェフィ", 106401, 416, 0, 7, 80005, 0, "", "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 17, "コッコロ", 105901, 417, 0, 7, 80005, 0, "", "もちろんでございます\n仲良くお勉強いたしましょう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 25, "キャル", 106001, 418, 0, 7, 80005, 0, "", "しれっと教育ママが\n生まれてる…？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 23, "ペコリーヌ", 105801, 419, 0, 7, 80005, 0, "", "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "キャル", 106001, 420, 0, 7, 80005, 0, "", "え～～～～！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "キャル", 106001, 421, 0, 8, 80005, 0, "", "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 19, "シェフィ", 106401, 422, 0, 8, 80005, 0, "", "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 18, "コッコロ", 105901, 423, 0, 8, 80005, 0, "", "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 24, "ペコリーヌ", 105801, 424, 0, 8, 80005, 0, "", "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 20, "シェフィ", 106401, 425, 0, 8, 80005, 0, "", "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 28, "キャル", 106001, 426, 0, 8, 80005, 0, "", "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 19, "コッコロ", 105901, 427, 0, 8, 80005, 0, "", "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 21, "シェフィ", 106401, 428, 0, 8, 80005, 0, "", "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 29, "キャル", 106001, 429, 0, 8, 80005, 0, "", "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 30, "キャル", 106001, 430, 0, 8, 80005, 0, "", "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 25, "ペコリーヌ", 105801, 431, 0, 8, 80005, 0, "", "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 31, "キャル", 106001, 432, 0, 8, 80005, 0, "", "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 22, "シェフィ", 106401, 433, 0, 9, 80005, 0, "", "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "シェフィ", 106401, 434, 0, 9, 80005, 0, "", "…えっ？\n生放送の意気込み！？\n突然なに！？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 24, "シェフィ", 106401, 435, 0, 9, 80005, 0, "", "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 25, "シェフィ", 106401, 436, 0, 9, 80005, 0, "", "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "シェフィ", 106401, 437, 0, 9, 80005, 0, "", "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "シェフィ", 106401, 438, 0, 9, 80005, 0, "", "それから、それから…！\nぅ、ぅぅっ…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 28, "シェフィ", 106401, 439, 0, 9, 80005, 0, "", "―びえぇぇ～んっ！！！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 20, "コッコロ", 105901, 440, 0, 9, 80005, 0, "", "どうされましたか、シェフィさま", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 29, "シェフィ", 106401, 441, 0, 9, 80005, 0, "", "ううっ…ひっく…\nママッ…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 21, "コッコロ", 105901, 442, 0, 9, 80005, 0, "", "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 22, "コッコロ", 105901, 443, 0, 9, 80005, 0, "", "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 30, "シェフィ", 106401, 444, 0, 9, 80005, 0, "", "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 23, "コッコロ", 105901, 445, 0, 10, 80005, 0, "", "主さま\n3.5周年まであと５日\nでございます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 24, "コッコロ", 105901, 446, 0, 10, 80005, 0, "", "ですがその前に\n生放送がありますね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 25, "コッコロ", 105901, 447, 0, 10, 80005, 0, "", "…生放送に向けた\n意気込みでございますか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "コッコロ", 105901, 448, 0, 10, 80005, 0, "", "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "コッコロ", 105901, 449, 0, 10, 80005, 0, "", "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 28, "コッコロ", 105901, 450, 0, 10, 80005, 0, "", "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 29, "コッコロ", 105901, 451, 0, 10, 80005, 0, "", "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 30, "コッコロ", 105901, 452, 0, 10, 80005, 0, "", "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 31, "コッコロ", 105901, 453, 0, 10, 80005, 0, "", "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 26, "ペコリーヌ", 105801, 454, 0, 11, 80005, 0, "", "3.5周年まであと４日！\nやばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 32, "キャル", 106001, 455, 0, 11, 80005, 0, "", "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 27, "ペコリーヌ", 105801, 456, 0, 11, 80005, 0, "", "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 28, "ペコリーヌ", 105801, 457, 0, 11, 80005, 0, "", "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 29, "ペコリーヌ", 105801, 458, 0, 11, 80005, 0, "", "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 33, "キャル", 106001, 459, 0, 11, 80005, 0, "", "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 30, "ペコリーヌ", 105801, 460, 0, 11, 80005, 0, "", "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 31, "ペコリーヌ", 105801, 461, 0, 11, 80005, 0, "", "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 32, "ペコリーヌ", 105801, 462, 0, 11, 80005, 0, "", "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 34, "キャル", 106001, 463, 0, 11, 80005, 0, "", "だからあたしはやるなんて一言も…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 33, "ペコリーヌ", 105801, 464, 0, 11, 80005, 0, "", "オイッスー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 35, "キャル", 106001, 465, 0, 11, 80005, 0, "", "聞きなさいっての！！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 31, "シェフィ", 106401, 466, 0, 12, 80005, 0, "", "3.5周年まであと３日…\nそして生放送は、明日…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 32, "シェフィ", 106401, 467, 0, 12, 80005, 0, "", "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 33, "シェフィ", 106401, 468, 0, 12, 80005, 0, "", "おいっす！\n…こうじゃなかったわね\nええと確か…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 34, "シェフィ", 106401, 469, 0, 12, 80005, 0, "", "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 35, "シェフィ", 106401, 470, 0, 12, 80005, 0, "", "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 34, "ペコリーヌ", 105801, 471, 0, 12, 80005, 0, "", "やってますねシェフィちゃん！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 36, "キャル", 106001, 472, 0, 12, 80005, 0, "", "なによ、考えることは\nみんな同じってこと？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 32, "コッコロ", 105901, 473, 0, 12, 80005, 0, "", "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 36, "シェフィ", 106401, 474, 0, 12, 80005, 0, "", "みんな…もちろんよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 35, "ペコリーヌ", 105801, 475, 0, 12, 80005, 0, "", "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 7, "カリン", 190801, 476, 0, 13, 80005, 0, "", "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 8, "カリン", 190801, 477, 0, 13, 80005, 0, "", "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 36, "ペコリーヌ", 105801, 478, 0, 13, 80005, 0, "", "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 9, "カリン", 190801, 479, 0, 13, 80005, 0, "", "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 33, "コッコロ", 105901, 480, 0, 13, 80005, 0, "", "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 10, "カリン", 190801, 481, 0, 13, 80005, 0, "", "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 37, "キャル", 106001, 482, 0, 13, 80005, 0, "", "大丈夫…大丈夫…\nあたしは大丈夫…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 11, "カリン", 190801, 483, 0, 13, 80005, 0, "", "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 37, "シェフィ", 106401, 484, 0, 13, 80005, 0, "", "あうー？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 37, "みんな", 105801, 485, 0, 13, 80005, 0, "105901,34,106001,38,190801,12", "あ…", 1);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 39, "キャル", 106001, 486, 0, 13, 80005, 0, "", "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 35, "コッコロ", 105901, 487, 0, 13, 80005, 0, "", "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 38, "ペコリーヌ", 105801, 488, 0, 13, 80005, 0, "", "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 13, "カリン", 190801, 489, 0, 13, 80005, 0, "", "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 38, "シェフィ", 106401, 490, 0, 14, 80005, 0, "", "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 39, "ペコリーヌ", 105801, 491, 0, 14, 80005, 0, "", "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 40, "キャル", 106001, 492, 0, 14, 80005, 0, "", "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 36, "コッコロ", 105901, 493, 0, 14, 80005, 0, "", "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 39, "シェフィ", 106401, 494, 0, 14, 80005, 0, "", "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 40, "ペコリーヌ", 105801, 495, 0, 14, 80005, 0, "", "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 37, "コッコロ", 105901, 496, 0, 14, 80005, 0, "", "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 41, "キャル", 106001, 497, 0, 14, 80005, 0, "", "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 40, "シェフィ", 106401, 498, 0, 14, 80005, 0, "", "へ、へぇ…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 41, "ペコリーヌ", 105801, 499, 0, 14, 80005, 0, "", "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 41, "シェフィ", 106401, 500, 0, 14, 80005, 0, "", "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 42, "シェフィ", 106401, 501, 0, 14, 80005, 0, "", "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 42, "ペコリーヌ", 105801, 502, 0, 15, 80005, 0, "", "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 42, "キャル", 106001, 503, 0, 15, 80005, 0, "", "祝！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, 38, "コッコロ", 105901, 504, 0, 15, 80005, 0, "", "3.5周年！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 43, "シェフィ", 106401, 505, 0, 15, 80005, 0, "", "おめでとうございます！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 14, "カリン", 190801, 506, 0, 15, 80005, 0, "", "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, 15, "カリン", 190801, 507, 0, 15, 80005, 0, "", "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 43, "ペコリーヌ", 105801, 508, 0, 15, 80005, 0, "", "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, 44, "ペコリーヌ", 105801, 509, 0, 15, 80005, 0, "", "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 43, "キャル", 106001, 510, 0, 15, 80005, 0, "", "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, 44, "キャル", 106001, 511, 0, 15, 80005, 0, "", "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, 44, "シェフィ", 106401, 512, 0, 15, 80005, 0, "", "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0);
