INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", "ペコリーヌ", 0, 0, 1, 129, 80003, 105801, 4, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", "キャル", 2, 0, 1, 130, 80003, 106001, 4, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", "コッコロ", 1, 0, 1, 131, 80003, 105901, 5, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", "キャル", 0, 0, 2, 132, 80003, 106001, 5, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", "カリン", 1, 0, 2, 133, 80003, 190801, 1, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("げっ…カリンじゃない\nなんでもないわよ！", "キャル", 0, 0, 2, 134, 80003, 106001, 6, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", "カリン", 1, 0, 2, 135, 80003, 190801, 2, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", "キャル", 0, 0, 2, 136, 80003, 106001, 7, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", "カリン", 1, 0, 2, 137, 80003, 190801, 3, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", "キャル", 0, 0, 2, 138, 80003, 106001, 8, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", "キャル", 0, 0, 2, 139, 80003, 106001, 9, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", "カリン", 1, 0, 2, 140, 80003, 190801, 4, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", "キャル", 0, 0, 2, 141, 80003, 106001, 10, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", "カリン", 1, 0, 2, 142, 80003, 190801, 5, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", "キャル", 0, 0, 2, 143, 80003, 106001, 11, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", "コッコロ", 0, 0, 3, 144, 80003, 105901, 6, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", "ペコリーヌ", 1, 0, 3, 145, 80003, 105801, 5, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", "コッコロ", 0, 0, 3, 146, 80003, 105901, 7, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", "コッコロ", 0, 0, 3, 147, 80003, 105901, 8, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", "ペコリーヌ", 1, 0, 3, 148, 80003, 105801, 6, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", "ペコリーヌ", 1, 0, 3, 149, 80003, 105801, 7, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あとは、牧場に素敵な温泉が\n出来たとか…", "コッコロ", 0, 0, 3, 150, 80003, 105901, 9, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", "ペコリーヌ", 1, 0, 3, 151, 80003, 105801, 8, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", "コッコロ", 0, 0, 3, 152, 80003, 105901, 10, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えへへ、ありがとうございます\nコッコロちゃん！", "ペコリーヌ", 1, 0, 3, 153, 80003, 105801, 9, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", "コッコロ", 0, 0, 3, 154, 80003, 105901, 11, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", "ペコリーヌ", 1, 0, 3, 155, 80003, 105801, 10, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", "ペコリーヌ", 0, 0, 4, 156, 80003, 105801, 11, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", "キャル", 1, 0, 4, 157, 80003, 106001, 12, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", "ペコリーヌ", 0, 0, 4, 158, 80003, 105801, 12, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", "キャル", 1, 0, 4, 159, 80003, 106001, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", "ペコリーヌ", 0, 0, 4, 160, 80003, 105801, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…積極的？\nなんの話よ？", "キャル", 1, 0, 4, 161, 80003, 106001, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", "ペコリーヌ", 0, 0, 4, 162, 80003, 105801, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ムーサの町で\nきゃるきゃる～ん☆　って！", "ペコリーヌ", 0, 0, 4, 163, 80003, 105801, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", "キャル", 1, 0, 4, 164, 80003, 106001, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", "ペコリーヌ", 0, 0, 4, 165, 80003, 105801, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", "キャル", 1, 0, 4, 166, 80003, 106001, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", "キャル", 1, 0, 4, 167, 80003, 106001, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", "キャル", 0, 0, 5, 168, 80003, 106001, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", "コッコロ", 1, 0, 5, 169, 80003, 105901, 12, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", "キャル", 0, 0, 5, 170, 80003, 106001, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", "コッコロ", 1, 0, 5, 171, 80003, 105901, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", "キャル", 0, 0, 5, 172, 80003, 106001, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", "コッコロ", 1, 0, 5, 173, 80003, 105901, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", "キャル", 0, 0, 5, 174, 80003, 106001, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", "コッコロ", 1, 0, 5, 175, 80003, 105901, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("かくし芸？", "キャル", 0, 0, 5, 176, 80003, 106001, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", "コッコロ", 1, 0, 5, 177, 80003, 105901, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", "キャル", 0, 0, 5, 178, 80003, 106001, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", "ペコリーヌ", 0, 0, 6, 179, 80003, 105801, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", "キャル", 2, 0, 6, 180, 80003, 106001, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", "コッコロ", 1, 0, 6, 181, 80003, 105901, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", "ペコリーヌ", 0, 0, 6, 182, 80003, 105801, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", "キャル", 2, 0, 6, 183, 80003, 106001, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", "コッコロ", 1, 0, 6, 184, 80003, 105901, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", "キャル", 2, 0, 6, 185, 80003, 106001, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", "キャル", 2, 0, 6, 186, 80003, 106001, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", "キャル", 2, 0, 6, 187, 80003, 106001, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", "ペコリーヌ", 0, 0, 6, 188, 80003, 105801, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", "コッコロ", 1, 0, 6, 189, 80003, 105901, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", "キャル", 2, 0, 6, 190, 80003, 106001, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", "キャル", 0, 0, 7, 191, 80003, 106001, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", "キャル", 0, 0, 7, 192, 80003, 106001, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", "キャル", 0, 0, 7, 193, 80003, 106001, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あー\nあー\n…声、これで大丈夫かしら？", "キャル", 0, 0, 7, 194, 80003, 106001, 33, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", "キャル", 0, 0, 7, 195, 80003, 106001, 34, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("オイッスー☆", "キャル", 0, 0, 7, 196, 80003, 106001, 35, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("……\nいや、ないわ\nいつもどおりが一番ね、うん", "キャル", 0, 0, 7, 197, 80003, 106001, 36, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", "キャル", 0, 0, 7, 198, 80003, 106001, 37, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ど、どど、どうしよう！？\n緊張してきちゃった…！！", "キャル", 0, 0, 7, 199, 80003, 106001, 38, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", "コッコロ", 0, 0, 8, 200, 80003, 105901, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", "コッコロ", 0, 0, 8, 201, 80003, 105901, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", "コッコロ", 0, 0, 8, 202, 80003, 105901, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", "コッコロ", 0, 0, 8, 203, 80003, 105901, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", "コッコロ", 0, 0, 8, 204, 80003, 105901, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("オイッスー☆", "コッコロ", 0, 0, 8, 205, 80003, 105901, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", "コッコロ", 0, 0, 8, 206, 80003, 105901, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", "コッコロ", 0, 0, 8, 207, 80003, 105901, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", "コッコロ", 0, 0, 8, 208, 80003, 105901, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("オイッスー☆", "ペコリーヌ", 0, 0, 9, 209, 80003, 105801, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", "ペコリーヌ", 0, 0, 9, 210, 80003, 105801, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あと６日で2.5周年ですか…", "ペコリーヌ", 0, 0, 9, 211, 80003, 105801, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", "ペコリーヌ", 0, 0, 9, 212, 80003, 105801, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", "ペコリーヌ", 0, 0, 9, 213, 80003, 105801, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("オイッスー☆", "ペコリーヌ", 0, 0, 9, 214, 80003, 105801, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", "ペコリーヌ", 0, 0, 9, 215, 80003, 105801, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", "ペコリーヌ", 0, 0, 9, 216, 80003, 105801, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", "ペコリーヌ", 0, 0, 9, 217, 80003, 105801, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", "ペコリーヌ", 0, 0, 9, 218, 80003, 105801, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", "ペコリーヌ", 0, 0, 9, 219, 80003, 105801, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", "ペコリーヌ", 0, 0, 10, 220, 80003, 105801, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", "コッコロ", 1, 0, 10, 221, 80003, 105901, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", "ペコリーヌ", 0, 0, 10, 222, 80003, 105801, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…………", "キャル", 2, 0, 10, 223, 80003, 106001, 39, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", "コッコロ", 1, 0, 10, 224, 80003, 105901, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", "キャル", 2, 0, 10, 225, 80003, 106001, 40, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", "ペコリーヌ", 0, 0, 10, 226, 80003, 105801, 33, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", "キャル", 2, 0, 10, 227, 80003, 106001, 41, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", "ペコリーヌ", 0, 0, 10, 228, 80003, 105801, 34, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", "ペコリーヌ", 0, 0, 10, 229, 80003, 105801, 35, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", "キャル", 2, 0, 10, 230, 80003, 106001, 42, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", "コッコロ", 1, 0, 10, 231, 80003, 105901, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", "キャル", 2, 0, 10, 232, 80003, 106001, 43, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", "ペコリーヌ", 0, 0, 10, 233, 80003, 105801, 36, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", "カリン", 0, 0, 11, 234, 80003, 190801, 6, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", "カリン", 0, 0, 11, 235, 80003, 190801, 7, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", "カリン", 0, 0, 11, 236, 80003, 190801, 8, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", "カリン", 0, 0, 11, 237, 80003, 190801, 9, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", "カリン", 0, 0, 11, 238, 80003, 190801, 10, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("次にコッコロさんですが\nこちらは発声練習をしています", "カリン", 0, 0, 11, 239, 80003, 190801, 11, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", "カリン", 0, 0, 11, 240, 80003, 190801, 12, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", "カリン", 0, 0, 11, 241, 80003, 190801, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あれ？\nもしかして緊張で気を失って…？", "カリン", 0, 0, 11, 242, 80003, 190801, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", "カリン", 0, 0, 11, 243, 80003, 190801, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", "カリン", 0, 0, 11, 244, 80003, 190801, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それでは\nまた明日お会いしましょう♪", "カリン", 0, 0, 11, 245, 80003, 190801, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", "キャル", 0, 0, 12, 246, 80003, 106001, 44, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", "ペコリーヌ", 2, 0, 12, 247, 80003, 105801, 37, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", "コッコロ", 1, 0, 12, 248, 80003, 105901, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", "キャル", 0, 0, 12, 249, 80003, 106001, 45, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ひどいですよぉキャルちゃ～ん！", "ペコリーヌ", 2, 0, 12, 250, 80003, 105801, 38, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふ。お二人とも\n普段通りの様子で\n安心いたしました", "コッコロ", 1, 0, 12, 251, 80003, 105901, 33, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", "ペコリーヌ", 2, 0, 12, 252, 80003, 105801, 39, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", "キャル", 0, 0, 12, 253, 80003, 106001, 46, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", "コッコロ", 1, 0, 12, 254, 80003, 105901, 34, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", "ペコリーヌ", 2, 0, 12, 255, 80003, 105801, 40, 0, "", 0);
