INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", "シェフィ", 2, 0, 4, 385, 80005, 106401, 12, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", "ペコリーヌ", 3, 0, 5, 386, 80005, 105801, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", "ペコリーヌ", 3, 0, 5, 387, 80005, 105801, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", "ペコリーヌ", 3, 0, 5, 388, 80005, 105801, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", "キャル", 0, 0, 5, 389, 80005, 106001, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", "キャル", 0, 0, 5, 390, 80005, 106001, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", "シェフィ", 2, 0, 5, 391, 80005, 106401, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", "コッコロ", 1, 0, 5, 392, 80005, 105901, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", "ペコリーヌ", 3, 0, 5, 393, 80005, 105801, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", "キャル", 0, 0, 5, 394, 80005, 106001, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", "ペコリーヌ", 3, 0, 5, 395, 80005, 105801, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", "ペコリーヌ", 3, 0, 5, 396, 80005, 105801, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", "ペコリーヌ", 3, 0, 5, 397, 80005, 105801, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", "カリン", 2, 0, 6, 398, 80005, 190801, 4, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", "カリン", 2, 0, 6, 399, 80005, 190801, 5, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…えっ！？", "キャル", 1, 0, 6, 400, 80005, 106001, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", "ペコリーヌ", 3, 0, 6, 401, 80005, 105801, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", "シェフィ", 0, 0, 6, 402, 80005, 106401, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", "コッコロ", 4, 0, 6, 403, 80005, 105901, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", "シェフィ", 0, 0, 6, 404, 80005, 106401, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", "キャル", 1, 0, 6, 405, 80005, 106001, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", "ペコリーヌ", 3, 0, 6, 406, 80005, 105801, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ぶっ殺すぞ！？", "キャル", 1, 0, 6, 407, 80005, 106001, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", "カリン", 2, 0, 6, 408, 80005, 190801, 6, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", "コッコロ", 1, 0, 7, 409, 80005, 105901, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", "キャル", 0, 0, 7, 410, 80005, 106001, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", "シェフィ", 2, 0, 7, 411, 80005, 106401, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", "ペコリーヌ", 3, 0, 7, 412, 80005, 105801, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", "キャル", 0, 0, 7, 413, 80005, 106001, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", "シェフィ", 2, 0, 7, 414, 80005, 106401, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", "コッコロ", 1, 0, 7, 415, 80005, 105901, 16, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", "シェフィ", 2, 0, 7, 416, 80005, 106401, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("もちろんでございます\n仲良くお勉強いたしましょう", "コッコロ", 1, 0, 7, 417, 80005, 105901, 17, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("しれっと教育ママが\n生まれてる…？", "キャル", 0, 0, 7, 418, 80005, 106001, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", "ペコリーヌ", 3, 0, 7, 419, 80005, 105801, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("え～～～～！？", "キャル", 0, 0, 7, 420, 80005, 106001, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", "キャル", 0, 0, 8, 421, 80005, 106001, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…\nねぇコッコロさん\nキャル、どうしちゃったの？", "シェフィ", 2, 0, 8, 422, 80005, 106401, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", "コッコロ", 1, 0, 8, 423, 80005, 105901, 18, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", "ペコリーヌ", 3, 0, 8, 424, 80005, 105801, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", "シェフィ", 2, 0, 8, 425, 80005, 106401, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", "キャル", 0, 0, 8, 426, 80005, 106001, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", "コッコロ", 1, 0, 8, 427, 80005, 105901, 19, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", "シェフィ", 2, 0, 8, 428, 80005, 106401, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", "キャル", 0, 0, 8, 429, 80005, 106001, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", "キャル", 0, 0, 8, 430, 80005, 106001, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", "ペコリーヌ", 3, 0, 8, 431, 80005, 105801, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", "キャル", 0, 0, 8, 432, 80005, 106001, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", "シェフィ", 0, 0, 9, 433, 80005, 106401, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…えっ？\n生放送の意気込み！？\n突然なに！？", "シェフィ", 0, 0, 9, 434, 80005, 106401, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", "シェフィ", 0, 0, 9, 435, 80005, 106401, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", "シェフィ", 0, 0, 9, 436, 80005, 106401, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("とにかく落ち着いて\nスタッフさんの指示を聞いて…", "シェフィ", 0, 0, 9, 437, 80005, 106401, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それから、それから…！\nぅ、ぅぅっ…！", "シェフィ", 0, 0, 9, 438, 80005, 106401, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("―びえぇぇ～んっ！！！", "シェフィ", 0, 0, 9, 439, 80005, 106401, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("どうされましたか、シェフィさま", "コッコロ", 1, 0, 9, 440, 80005, 105901, 20, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ううっ…ひっく…\nママッ…！", "シェフィ", 0, 0, 9, 441, 80005, 106401, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", "コッコロ", 1, 0, 9, 442, 80005, 105901, 21, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", "コッコロ", 1, 0, 9, 443, 80005, 105901, 22, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…ううっ\n恥ずかしすぎて\nもう生放送出られない…", "シェフィ", 0, 0, 9, 444, 80005, 106401, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("主さま\n3.5周年まであと５日\nでございます", "コッコロ", 0, 0, 10, 445, 80005, 105901, 23, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ですがその前に\n生放送がありますね", "コッコロ", 0, 0, 10, 446, 80005, 105901, 24, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…生放送に向けた\n意気込みでございますか？", "コッコロ", 0, 0, 10, 447, 80005, 105901, 25, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", "コッコロ", 0, 0, 10, 448, 80005, 105901, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", "コッコロ", 0, 0, 10, 449, 80005, 105901, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", "コッコロ", 0, 0, 10, 450, 80005, 105901, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", "コッコロ", 0, 0, 10, 451, 80005, 105901, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", "コッコロ", 0, 0, 10, 452, 80005, 105901, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", "コッコロ", 0, 0, 10, 453, 80005, 105901, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと４日！\nやばいですね☆", "ペコリーヌ", 0, 0, 11, 454, 80005, 105801, 26, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", "キャル", 1, 0, 11, 455, 80005, 106001, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", "ペコリーヌ", 0, 0, 11, 456, 80005, 105801, 27, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", "ペコリーヌ", 0, 0, 11, 457, 80005, 105801, 28, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", "ペコリーヌ", 0, 0, 11, 458, 80005, 105801, 29, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", "キャル", 1, 0, 11, 459, 80005, 106001, 33, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", "ペコリーヌ", 0, 0, 11, 460, 80005, 105801, 30, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", "ペコリーヌ", 0, 0, 11, 461, 80005, 105801, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", "ペコリーヌ", 0, 0, 11, 462, 80005, 105801, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("だからあたしはやるなんて一言も…", "キャル", 1, 0, 11, 463, 80005, 106001, 34, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("オイッスー☆", "ペコリーヌ", 0, 0, 11, 464, 80005, 105801, 33, 0, "", 0);
