INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと３日…\nそして生放送は、明日…", "シェフィ", 2, 0, 12, 466, 80005, 106401, 31, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("もっと台本を\n読み込んでおかないと…\nあと発声練習も…", "シェフィ", 2, 0, 12, 467, 80005, 106401, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("おいっす！\n…こうじゃなかったわね\nええと確か…", "シェフィ", 2, 0, 12, 468, 80005, 106401, 33, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", "シェフィ", 2, 0, 12, 469, 80005, 106401, 34, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", "シェフィ", 2, 0, 12, 470, 80005, 106401, 35, 0, "", 0);
