INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", "コッコロ", 1, 0, 12, 473, 80005, 105901, 32, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("みんな…もちろんよ！", "シェフィ", 2, 0, 12, 474, 80005, 106401, 36, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", "ペコリーヌ", 3, 0, 12, 475, 80005, 105801, 35, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", "カリン", 2, 0, 13, 476, 80005, 190801, 7, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", "カリン", 2, 0, 13, 477, 80005, 190801, 8, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", "ペコリーヌ", 3, 0, 13, 478, 80005, 105801, 36, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", "カリン", 2, 0, 13, 479, 80005, 190801, 9, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", "コッコロ", 4, 0, 13, 480, 80005, 105901, 33, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("さすが【美食殿】のママですね！\nそしてキャルさんは…", "カリン", 2, 0, 13, 481, 80005, 190801, 10, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("大丈夫…大丈夫…\nあたしは大丈夫…", "キャル", 1, 0, 13, 482, 80005, 106001, 37, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", "カリン", 2, 0, 13, 483, 80005, 190801, 11, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あうー？", "シェフィ", 0, 0, 13, 484, 80005, 106401, 37, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("あ…", "みんな", 4, 1, 13, 485, 80005, 105801, 37, 0, "105901,34,106001,38,190801,12", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", "キャル", 1, 0, 13, 486, 80005, 106001, 39, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", "コッコロ", 4, 0, 13, 487, 80005, 105901, 35, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", "ペコリーヌ", 3, 0, 13, 488, 80005, 105801, 38, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", "カリン", 2, 0, 13, 489, 80005, 190801, 13, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", "シェフィ", 2, 0, 14, 490, 80005, 106401, 38, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", "ペコリーヌ", 3, 0, 14, 491, 80005, 105801, 39, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", "キャル", 0, 0, 14, 492, 80005, 106001, 40, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", "コッコロ", 1, 0, 14, 493, 80005, 105901, 36, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", "シェフィ", 2, 0, 14, 494, 80005, 106401, 39, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", "ペコリーヌ", 3, 0, 14, 495, 80005, 105801, 40, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", "コッコロ", 1, 0, 14, 496, 80005, 105901, 37, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", "キャル", 0, 0, 14, 497, 80005, 106001, 41, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("へ、へぇ…", "シェフィ", 2, 0, 14, 498, 80005, 106401, 40, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", "ペコリーヌ", 3, 0, 14, 499, 80005, 105801, 41, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", "シェフィ", 2, 0, 14, 500, 80005, 106401, 41, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", "シェフィ", 2, 0, 14, 501, 80005, 106401, 42, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", "ペコリーヌ", 3, 0, 15, 502, 80005, 105801, 42, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("祝！", "キャル", 1, 0, 15, 503, 80005, 106001, 42, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("3.5周年！", "コッコロ", 4, 0, 15, 504, 80005, 105901, 38, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("おめでとうございます！", "シェフィ", 0, 0, 15, 505, 80005, 106401, 43, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", "カリン", 2, 0, 15, 506, 80005, 190801, 14, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", "カリン", 2, 0, 15, 507, 80005, 190801, 15, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", "ペコリーヌ", 3, 0, 15, 508, 80005, 105801, 43, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", "ペコリーヌ", 3, 0, 15, 509, 80005, 105801, 44, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", "キャル", 1, 0, 15, 510, 80005, 106001, 43, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", "キャル", 1, 0, 15, 511, 80005, 106001, 44, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", "シェフィ", 0, 0, 15, 512, 80005, 106401, 44, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", "シェフィ", 0, 0, 15, 513, 80005, 106401, 45, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", "コッコロ", 4, 0, 15, 514, 80005, 105901, 39, 0, "", 0);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES ("ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", "コッコロ", 4, 0, 15, 515, 80005, 105901, 40, 0, "", 0);
