INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 1, 0, "カリン", 1, 190804, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 2, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 3, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 4, 0, "", 2, 0, "", 2, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 5, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 6, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 7, 0, "", 2, 0, "", 2, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 8, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 9, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 10, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 11, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 12, 0, "", 2, 0, "", 2, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 13, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 14, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 15, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 16, 0, "", 2, 0, "", 1, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 17, 0, "", 2, 0, "", 2, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, 18, 0, "カリン", 3, 190804, "", 0, 0, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 19, 0, "カリン", 3, 190804, "", 0, 7, 60000);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 1, 20, 0, "ハツネ", 0, 101201, "", 0, 1, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 2, 21, 0, "ハツネ", 0, 101201, "", 0, 1, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 1, 22, 0, "シオリ", 1, 103801, "", 0, 1, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "来年も、\n一緒にいられたら…\nうれしいな", 2, 23, 0, "シオリ", 1, 103801, "", 0, 1, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あなたと一緒にいるようになって、\nもう1年ですか…", 1, 24, 0, "キョウカ", 0, 103601, "", 0, 2, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 2, 25, 0, "キョウカ", 0, 103601, "", 0, 2, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おにいちゃんとあってから、\n1年たったよ～！", 1, 26, 0, "ミミ", 1, 102001, "", 0, 2, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 2, 27, 0, "ミミ", 1, 102001, "", 0, 2, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 1, 28, 0, "ミソギ", 2, 100401, "", 0, 2, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 2, 29, 0, "ミソギ", 2, 100401, "", 0, 2, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 1, 30, 0, "マホ", 0, 101001, "", 0, 3, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 2, 31, 0, "マホ", 0, 101001, "", 0, 3, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 1, 32, 0, "イリヤ", 1, 104401, "", 0, 3, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 2, 33, 0, "イリヤ", 1, 104401, "", 0, 3, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 1, 34, 0, "カオリ", 2, 101701, "", 0, 3, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからも、どうぞよろしく\nお願いするさ～！", 2, 35, 0, "カオリ", 2, 101701, "", 0, 3, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あんたと出会ってからもうそんなに\nたつのね～", 1, 36, 0, "サレン", 0, 102801, "", 0, 4, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 2, 37, 0, "サレン", 0, 102801, "", 0, 4, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 1, 38, 0, "スズメ", 1, 102501, "", 0, 4, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 2, 39, 0, "スズメ", 1, 102501, "", 0, 4, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "振り返れば、今までいろんなことが\nあったにゃあ…", 1, 40, 0, "タマキ", 0, 104601, "", 0, 5, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 2, 41, 0, "タマキ", 0, 104601, "", 0, 5, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 1, 42, 0, "ミフユ", 1, 104801, "", 0, 5, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 2, 43, 0, "ミフユ", 1, 104801, "", 0, 5, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あなたさまと出会ってもう1年…", 1, 44, 0, "アキノ", 0, 103201, "", 0, 6, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 2, 45, 0, "アキノ", 0, 103201, "", 0, 6, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 1, 46, 0, "ユカリ", 1, 103401, "", 0, 6, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 2, 47, 0, "ユカリ", 1, 103401, "", 0, 6, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 1, 48, 0, "クリスティーナ", 0, 107101, "", 0, 7, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ともかくこれからも\n愉しませてくれよ☆", 2, 49, 0, "クリスティーナ", 0, 107101, "", 0, 7, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 1, 50, 0, "ジュン", 1, 104701, "", 0, 7, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "見えないから分からないって？\nそれもそうだな…", 2, 51, 0, "ジュン", 1, 104701, "", 0, 7, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 1, 52, 0, "トモ", 0, 103701, "", 0, 8, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからもよろしくね！", 2, 53, 0, "トモ", 0, 103701, "", 0, 8, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 1, 54, 0, "マツリ", 1, 100501, "", 0, 8, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 2, 55, 0, "マツリ", 1, 100501, "", 0, 8, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オマエと会って1年なの！\n全然成仏もできなかったし、", 1, 56, 0, "ミヤコ", 0, 100701, "", 0, 9, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 2, 57, 0, "ミヤコ", 0, 100701, "", 0, 9, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 1, 58, 0, "シノブ", 1, 103101, "", 0, 9, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 2, 59, 0, "シノブ", 1, 103101, "", 0, 9, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 1, 60, 0, "アンナ", 0, 100901, "", 0, 10, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 2, 61, 0, "アンナ", 0, 100901, "", 0, 10, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "君と出会ってもう1年だって！\n早いねー！", 1, 62, 0, "ナナカ", 1, 101301, "", 0, 10, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 2, 63, 0, "ナナカ", 1, 101301, "", 0, 10, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 1, 64, 0, "ミツキ", 0, 105101, "", 0, 11, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからも、いろんなところを\n調べさせてね？", 2, 65, 0, "ミツキ", 0, 105101, "", 0, 11, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お前さんと出会ってもう1年か\n助太刀するつもりが", 1, 66, 0, "ルカ", 1, 105601, "", 0, 11, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 2, 67, 0, "ルカ", 1, 105601, "", 0, 11, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "早いものですわね、あなた様と\nお会いして、もう1年…", 1, 68, 0, "エリコ", 2, 102701, "", 0, 11, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 2, 69, 0, "エリコ", 2, 102701, "", 0, 11, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 1, 70, 0, "アヤネ", 0, 102301, "", 0, 12, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 2, 71, 0, "アヤネ", 0, 102301, "", 0, 12, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 1, 72, 0, "チカ", 1, 104201, "", 0, 12, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "私のことを見守ってください\nできることなら、ずっと…", 2, 73, 0, "チカ", 1, 104201, "", 0, 12, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 1, 74, 0, "クルミ", 2, 102101, "", 0, 12, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "で、でもこれからもずっと…\n一緒にいたいなぁ…", 2, 75, 0, "クルミ", 2, 102101, "", 0, 12, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 1, 76, 0, "シズル", 0, 104901, "", 0, 13, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 2, 77, 0, "シズル", 0, 104901, "", 0, 13, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 1, 78, 0, "リノ", 1, 101101, "", 0, 13, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 2, 79, 0, "リノ", 1, 101101, "", 0, 13, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 1, 80, 0, "ユイ", 0, 100201, "", 0, 14, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 2, 81, 0, "ユイ", 0, 100201, "", 0, 14, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 1, 82, 0, "ヒヨリ", 1, 100101, "", 0, 14, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 2, 83, 0, "ヒヨリ", 1, 100101, "", 0, 14, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 1, 84, 0, "レイ", 2, 100301, "", 0, 14, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キミに背中を預けられる日が、\n早くきますように", 2, 85, 0, "レイ", 2, 100301, "", 0, 14, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 1, 86, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからもずっと、覚えていて\nくださいね！", 2, 87, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 1, 88, 0, "コッコロ", 1, 105901, "", 0, 15, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 2, 89, 0, "コッコロ", 1, 105901, "", 0, 15, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 1, 90, 0, "キャル", 2, 106001, "", 0, 15, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 2, 91, 0, "キャル", 2, 106001, "", 0, 15, 80001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, 92, 0, "スズメ", 1, 112101, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 0, 93, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 94, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 95, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 96, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 0, 97, 0, "", 2, 0, "", 2, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 0, 98, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 0, 99, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 100, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 101, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 0, 102, 0, "", 2, 0, "", 2, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 0, 103, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 0, 104, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 105, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 106, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 0, 107, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 0, 108, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 0, 109, 0, "", 2, 0, "", 2, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 0, 110, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 0, 111, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 0, 112, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 113, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 114, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 0, 115, 0, "", 2, 0, "", 1, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 116, 0, "", 2, 0, "", 2, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 2, 117, 0, "スズメ", 3, 112101, "", 0, 0, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 3, 118, 0, "スズメ", 3, 112101, "", 0, 7, 60001);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ヒューヒュー♪\nドンドンパフパフー♪", 1, 119, 0, "ペコリーヌ", 0, 105801, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 1, 120, 0, "コッコロ", 1, 105901, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 2, 121, 0, "コッコロ", 1, 105901, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "え？　ちょっと\nいきなりなにっ？", 1, 122, 0, "キャル", 2, 106001, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 2, 123, 0, "キャル", 2, 106001, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 2, 124, 0, "ペコリーヌ", 0, 105801, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 3, 125, 0, "ペコリーヌ", 0, 105801, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ですからその日に向けて\n今から気分を盛り上げているのです", 3, 126, 0, "コッコロ", 1, 105901, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 3, 127, 0, "キャル", 2, 106001, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 4, 128, 0, "コッコロ", 1, 105901, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 4, 129, 0, "ペコリーヌ", 0, 105801, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 4, 130, 0, "キャル", 2, 106001, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 5, 131, 0, "コッコロ", 1, 105901, "", 0, 1, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 5, 132, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 1, 133, 0, "カリン", 1, 190801, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "げっ…カリンじゃない\nなんでもないわよ！", 6, 134, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 2, 135, 0, "カリン", 1, 190801, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 7, 136, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 3, 137, 0, "カリン", 1, 190801, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 8, 138, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 9, 139, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 4, 140, 0, "カリン", 1, 190801, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 10, 141, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 5, 142, 0, "カリン", 1, 190801, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 11, 143, 0, "キャル", 0, 106001, "", 0, 2, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 6, 144, 0, "コッコロ", 0, 105901, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 5, 145, 0, "ペコリーヌ", 1, 105801, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 7, 146, 0, "コッコロ", 0, 105901, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 8, 147, 0, "コッコロ", 0, 105901, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 6, 148, 0, "ペコリーヌ", 1, 105801, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 7, 149, 0, "ペコリーヌ", 1, 105801, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あとは、牧場に素敵な温泉が\n出来たとか…", 9, 150, 0, "コッコロ", 0, 105901, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 8, 151, 0, "ペコリーヌ", 1, 105801, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 10, 152, 0, "コッコロ", 0, 105901, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えへへ、ありがとうございます\nコッコロちゃん！", 9, 153, 0, "ペコリーヌ", 1, 105801, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 11, 154, 0, "コッコロ", 0, 105901, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 10, 155, 0, "ペコリーヌ", 1, 105801, "", 0, 3, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 11, 156, 0, "ペコリーヌ", 0, 105801, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 12, 157, 0, "キャル", 1, 106001, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 12, 158, 0, "ペコリーヌ", 0, 105801, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 13, 159, 0, "キャル", 1, 106001, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 13, 160, 0, "ペコリーヌ", 0, 105801, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…積極的？\nなんの話よ？", 14, 161, 0, "キャル", 1, 106001, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 14, 162, 0, "ペコリーヌ", 0, 105801, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ムーサの町で\nきゃるきゃる～ん☆　って！", 15, 163, 0, "ペコリーヌ", 0, 105801, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 15, 164, 0, "キャル", 1, 106001, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 16, 165, 0, "ペコリーヌ", 0, 105801, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 16, 166, 0, "キャル", 1, 106001, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 17, 167, 0, "キャル", 1, 106001, "", 0, 4, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 18, 168, 0, "キャル", 0, 106001, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 12, 169, 0, "コッコロ", 1, 105901, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 19, 170, 0, "キャル", 0, 106001, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 13, 171, 0, "コッコロ", 1, 105901, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 20, 172, 0, "キャル", 0, 106001, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 14, 173, 0, "コッコロ", 1, 105901, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 21, 174, 0, "キャル", 0, 106001, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 15, 175, 0, "コッコロ", 1, 105901, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "かくし芸？", 22, 176, 0, "キャル", 0, 106001, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 16, 177, 0, "コッコロ", 1, 105901, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 23, 178, 0, "キャル", 0, 106001, "", 0, 5, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 17, 179, 0, "ペコリーヌ", 0, 105801, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 24, 180, 0, "キャル", 2, 106001, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 17, 181, 0, "コッコロ", 1, 105901, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 18, 182, 0, "ペコリーヌ", 0, 105801, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 25, 183, 0, "キャル", 2, 106001, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 18, 184, 0, "コッコロ", 1, 105901, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 26, 185, 0, "キャル", 2, 106001, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 27, 186, 0, "キャル", 2, 106001, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 28, 187, 0, "キャル", 2, 106001, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 19, 188, 0, "ペコリーヌ", 0, 105801, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 19, 189, 0, "コッコロ", 1, 105901, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 29, 190, 0, "キャル", 2, 106001, "", 0, 6, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 30, 191, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 31, 192, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 32, 193, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あー\nあー\n…声、これで大丈夫かしら？", 33, 194, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 34, 195, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オイッスー☆", 35, 196, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "……\nいや、ないわ\nいつもどおりが一番ね、うん", 36, 197, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 37, 198, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 38, 199, 0, "キャル", 0, 106001, "", 0, 7, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 20, 200, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 21, 201, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 22, 202, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 23, 203, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 24, 204, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オイッスー☆", 25, 205, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 26, 206, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 27, 207, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 28, 208, 0, "コッコロ", 0, 105901, "", 0, 8, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オイッスー☆", 20, 209, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 21, 210, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あと６日で2.5周年ですか…", 22, 211, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 23, 212, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 24, 213, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オイッスー☆", 25, 214, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 26, 215, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 27, 216, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 28, 217, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 29, 218, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 30, 219, 0, "ペコリーヌ", 0, 105801, "", 0, 9, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 31, 220, 0, "ペコリーヌ", 0, 105801, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 29, 221, 0, "コッコロ", 1, 105901, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 32, 222, 0, "ペコリーヌ", 0, 105801, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…………", 39, 223, 0, "キャル", 2, 106001, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 30, 224, 0, "コッコロ", 1, 105901, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 40, 225, 0, "キャル", 2, 106001, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 33, 226, 0, "ペコリーヌ", 0, 105801, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 41, 227, 0, "キャル", 2, 106001, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 34, 228, 0, "ペコリーヌ", 0, 105801, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 35, 229, 0, "ペコリーヌ", 0, 105801, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 42, 230, 0, "キャル", 2, 106001, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 31, 231, 0, "コッコロ", 1, 105901, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 43, 232, 0, "キャル", 2, 106001, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 36, 233, 0, "ペコリーヌ", 0, 105801, "", 0, 10, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 6, 234, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 7, 235, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 8, 236, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 9, 237, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 10, 238, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "次にコッコロさんですが\nこちらは発声練習をしています", 11, 239, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 12, 240, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 13, 241, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あれ？\nもしかして緊張で気を失って…？", 14, 242, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 15, 243, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 16, 244, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それでは\nまた明日お会いしましょう♪", 17, 245, 0, "カリン", 0, 190801, "", 0, 11, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 44, 246, 0, "キャル", 0, 106001, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 37, 247, 0, "ペコリーヌ", 2, 105801, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 32, 248, 0, "コッコロ", 1, 105901, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 45, 249, 0, "キャル", 0, 106001, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ひどいですよぉキャルちゃ～ん！", 38, 250, 0, "ペコリーヌ", 2, 105801, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 33, 251, 0, "コッコロ", 1, 105901, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 39, 252, 0, "ペコリーヌ", 2, 105801, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 46, 253, 0, "キャル", 0, 106001, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 34, 254, 0, "コッコロ", 1, 105901, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 40, 255, 0, "ペコリーヌ", 2, 105801, "", 0, 12, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 41, 256, 0, "ペコリーヌ", 0, 105801, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 47, 257, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "忘れてきちゃいました☆", 42, 258, 0, "ペコリーヌ", 0, 105801, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 48, 259, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 35, 260, 0, "コッコロ", 1, 105901, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 49, 261, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 43, 262, 0, "ペコリーヌ", 0, 105801, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 50, 263, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 36, 264, 0, "コッコロ", 1, 105901, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 51, 265, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 37, 266, 0, "コッコロ", 1, 105901, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 52, 267, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 44, 268, 0, "ペコリーヌ", 0, 105801, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 38, 269, 0, "コッコロ", 1, 105901, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 45, 270, 0, "ペコリーヌ", 0, 105801, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 53, 271, 0, "キャル", 2, 106001, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 46, 272, 0, "ペコリーヌ", 0, 105801, "", 0, 13, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "こんにちは\n【ギルド管理協会】のカリンです", 18, 273, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "昨日の生放送は\nいかがだったでしょうか？", 19, 274, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 20, 275, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 21, 276, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 22, 277, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 23, 278, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 24, 279, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 25, 280, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 26, 281, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 27, 282, 0, "カリン", 0, 190801, "", 0, 14, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "準備はいいですかっ？\n…いっきますよー！", 47, 283, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "祝！", 54, 284, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "2.5周年！", 39, 285, 0, "コッコロ", 1, 105901, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おめでとうございま～す☆", 48, 286, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 55, 287, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 40, 288, 0, "コッコロ", 1, 105901, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 49, 289, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 56, 290, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 41, 291, 0, "コッコロ", 1, 105901, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 57, 292, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 50, 293, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 58, 294, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "記念日だから\n平気平気♪\nやばいですね☆", 51, 295, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "意味分かんないわよ！", 59, 296, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 42, 297, 0, "コッコロ", 1, 105901, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 43, 298, 0, "コッコロ", 1, 105901, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 52, 299, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 60, 300, 0, "キャル", 2, 106001, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 44, 301, 0, "コッコロ", 1, 105901, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 53, 302, 0, "ペコリーヌ", 0, 105801, "", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "2.5周年\nありがとうございます！", 54, 303, 1, "ペコリーヌ・コッコロ・キャル", 0, 105801, "105901,45,106001,61", 0, 15, 80003);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 304, 0, "カリン", 1, 190804, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 0, 305, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 0, 306, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 0, 307, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 0, 308, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (1, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 0, 309, 0, "", 2, 0, "", 2, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 0, 310, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 0, 311, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 0, 312, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 0, 313, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (2, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 0, 314, 0, "", 2, 0, "", 2, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 0, 315, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 0, 316, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 0, 317, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 0, 318, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 0, 319, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 0, 320, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 0, 321, 0, "", 2, 0, "", 2, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 0, 322, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 0, 323, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 0, 324, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 0, 325, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 0, 326, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 0, 327, 0, "", 2, 0, "", 1, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 0, 328, 0, "", 2, 0, "", 2, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 2, 329, 0, "カリン", 3, 190804, "", 0, 0, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 3, 330, 0, "カリン", 3, 190804, "", 0, 7, 60002);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ささ、シェフィさまはこちらへ…", 1, 331, 0, "コッコロ", 4, 105901, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "コッコロさん、どこへ行くの…？\n…え？", 1, 332, 0, "シェフィ", 0, 106401, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 1, 333, 0, "ペコリーヌ", 3, 105801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 2, 334, 0, "コッコロ", 4, 105901, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ええと…\n二人とも何をしているの？", 2, 335, 0, "シェフィ", 0, 106401, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 1, 336, 0, "キャル", 1, 106001, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あの日…？", 3, 337, 0, "シェフィ", 0, 106401, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 2, 338, 0, "ペコリーヌ", 3, 105801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "『プリンセスコネクト！\nRe:Dive』3.5周年！", 3, 339, 0, "ペコリーヌ", 3, 105801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "やばいですね☆", 1, 340, 0, "カリン", 2, 190801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "って、なにしれっと\n参加してんのよカリン！", 2, 341, 0, "キャル", 1, 106001, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 2, 342, 0, "カリン", 2, 190801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 3, 343, 0, "カリン", 2, 190801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 4, 344, 0, "ペコリーヌ", 3, 105801, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっ！？　ええとええと…！\nあ、あと14日…！", 4, 345, 0, "シェフィ", 0, 106401, "", 0, 1, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 5, 346, 0, "ペコリーヌ", 3, 105801, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 3, 347, 0, "コッコロ", 1, 105901, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 4, 348, 0, "コッコロ", 1, 105901, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 6, 349, 0, "ペコリーヌ", 3, 105801, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "さすがでございます\nペコリーヌさま", 5, 350, 0, "コッコロ", 1, 105901, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 3, 351, 0, "キャル", 0, 106001, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "しゃなり、しゃなり…\nふふ、案外できるものね", 5, 352, 0, "シェフィ", 2, 106401, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 7, 353, 0, "ペコリーヌ", 3, 105801, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "さ、次はキャルさまの\n番でございますよ", 6, 354, 0, "コッコロ", 1, 105901, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 4, 355, 0, "キャル", 0, 106001, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 6, 356, 0, "シェフィ", 2, 106401, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 5, 357, 0, "キャル", 0, 106001, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "いたたた…もぉお～！\nなんであたしがこんな目に～！", 6, 358, 0, "キャル", 0, 106001, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 7, 359, 0, "コッコロ", 1, 105901, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 7, 360, 0, "キャル", 0, 106001, "", 0, 2, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 8, 361, 0, "コッコロ", 1, 105901, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 8, 362, 0, "ペコリーヌ", 3, 105801, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 9, 363, 0, "コッコロ", 1, 105901, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "聖学祭…って、なにかしら？", 7, 364, 0, "シェフィ", 2, 106401, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 10, 365, 0, "コッコロ", 1, 105901, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 8, 366, 0, "キャル", 0, 106001, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 9, 367, 0, "キャル", 0, 106001, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 9, 368, 0, "ペコリーヌ", 3, 105801, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "いや、そのまんますぎでしょ…", 10, 369, 0, "キャル", 0, 106001, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 8, 370, 0, "シェフィ", 2, 106401, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 10, 371, 0, "ペコリーヌ", 3, 105801, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっ？　ええ…\nぎゅーっ…？", 9, 372, 0, "シェフィ", 2, 106401, "", 0, 3, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 11, 373, 0, "キャル", 0, 106001, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 10, 374, 0, "シェフィ", 2, 106401, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 12, 375, 0, "キャル", 0, 106001, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 11, 376, 0, "コッコロ", 1, 105901, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 13, 377, 0, "キャル", 0, 106001, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 11, 378, 0, "ペコリーヌ", 3, 105801, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 14, 379, 0, "キャル", 0, 106001, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 15, 380, 0, "キャル", 0, 106001, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 11, 381, 0, "シェフィ", 2, 106401, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 12, 382, 0, "ペコリーヌ", 3, 105801, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 12, 383, 0, "コッコロ", 1, 105901, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 16, 384, 0, "キャル", 0, 106001, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 12, 385, 0, "シェフィ", 2, 106401, "", 0, 4, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 13, 386, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 14, 387, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 15, 388, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 17, 389, 0, "キャル", 0, 106001, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 18, 390, 0, "キャル", 0, 106001, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 13, 391, 0, "シェフィ", 2, 106401, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 13, 392, 0, "コッコロ", 1, 105901, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 16, 393, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 19, 394, 0, "キャル", 0, 106001, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 17, 395, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 18, 396, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 19, 397, 0, "ペコリーヌ", 3, 105801, "", 0, 5, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 4, 398, 0, "カリン", 2, 190801, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 5, 399, 0, "カリン", 2, 190801, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…えっ！？", 20, 400, 0, "キャル", 1, 106001, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 20, 401, 0, "ペコリーヌ", 3, 105801, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 14, 402, 0, "シェフィ", 0, 106401, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 14, 403, 0, "コッコロ", 4, 105901, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 15, 404, 0, "シェフィ", 0, 106401, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 21, 405, 0, "キャル", 1, 106001, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 21, 406, 0, "ペコリーヌ", 3, 105801, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ぶっ殺すぞ！？", 22, 407, 0, "キャル", 1, 106001, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 6, 408, 0, "カリン", 2, 190801, "", 0, 6, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 15, 409, 0, "コッコロ", 1, 105901, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 23, 410, 0, "キャル", 0, 106001, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 16, 411, 0, "シェフィ", 2, 106401, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 22, 412, 0, "ペコリーヌ", 3, 105801, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 24, 413, 0, "キャル", 0, 106001, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 17, 414, 0, "シェフィ", 2, 106401, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 16, 415, 0, "コッコロ", 1, 105901, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 18, 416, 0, "シェフィ", 2, 106401, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もちろんでございます\n仲良くお勉強いたしましょう", 17, 417, 0, "コッコロ", 1, 105901, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "しれっと教育ママが\n生まれてる…？", 25, 418, 0, "キャル", 0, 106001, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 23, 419, 0, "ペコリーヌ", 3, 105801, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "え～～～～！？", 26, 420, 0, "キャル", 0, 106001, "", 0, 7, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 27, 421, 0, "キャル", 0, 106001, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 19, 422, 0, "シェフィ", 2, 106401, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 18, 423, 0, "コッコロ", 1, 105901, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 24, 424, 0, "ペコリーヌ", 3, 105801, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 20, 425, 0, "シェフィ", 2, 106401, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 28, 426, 0, "キャル", 0, 106001, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 19, 427, 0, "コッコロ", 1, 105901, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 21, 428, 0, "シェフィ", 2, 106401, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 29, 429, 0, "キャル", 0, 106001, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 30, 430, 0, "キャル", 0, 106001, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 25, 431, 0, "ペコリーヌ", 3, 105801, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 31, 432, 0, "キャル", 0, 106001, "", 0, 8, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 22, 433, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…えっ？\n生放送の意気込み！？\n突然なに！？", 23, 434, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 24, 435, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 25, 436, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 26, 437, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それから、それから…！\nぅ、ぅぅっ…！", 27, 438, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "―びえぇぇ～んっ！！！", 28, 439, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "どうされましたか、シェフィさま", 20, 440, 0, "コッコロ", 1, 105901, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ううっ…ひっく…\nママッ…！", 29, 441, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 21, 442, 0, "コッコロ", 1, 105901, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 22, 443, 0, "コッコロ", 1, 105901, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 30, 444, 0, "シェフィ", 0, 106401, "", 0, 9, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "主さま\n3.5周年まであと５日\nでございます", 23, 445, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ですがその前に\n生放送がありますね", 24, 446, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…生放送に向けた\n意気込みでございますか？", 25, 447, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 26, 448, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 27, 449, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 28, 450, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 29, 451, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 30, 452, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 31, 453, 0, "コッコロ", 0, 105901, "", 0, 10, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと４日！\nやばいですね☆", 26, 454, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 32, 455, 0, "キャル", 1, 106001, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 27, 456, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 28, 457, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 29, 458, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 33, 459, 0, "キャル", 1, 106001, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 30, 460, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 31, 461, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 32, 462, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "だからあたしはやるなんて一言も…", 34, 463, 0, "キャル", 1, 106001, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "オイッスー☆", 33, 464, 0, "ペコリーヌ", 0, 105801, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "聞きなさいっての！！", 35, 465, 0, "キャル", 1, 106001, "", 0, 11, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと３日…\nそして生放送は、明日…", 31, 466, 0, "シェフィ", 2, 106401, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 32, 467, 0, "シェフィ", 2, 106401, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おいっす！\n…こうじゃなかったわね\nええと確か…", 33, 468, 0, "シェフィ", 2, 106401, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 34, 469, 0, "シェフィ", 2, 106401, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 35, 470, 0, "シェフィ", 2, 106401, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "やってますねシェフィちゃん！", 34, 471, 0, "ペコリーヌ", 3, 105801, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なによ、考えることは\nみんな同じってこと？", 36, 472, 0, "キャル", 0, 106001, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 32, 473, 0, "コッコロ", 1, 105901, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "みんな…もちろんよ！", 36, 474, 0, "シェフィ", 2, 106401, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 35, 475, 0, "ペコリーヌ", 3, 105801, "", 0, 12, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 7, 476, 0, "カリン", 2, 190801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 8, 477, 0, "カリン", 2, 190801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 36, 478, 0, "ペコリーヌ", 3, 105801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 9, 479, 0, "カリン", 2, 190801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 33, 480, 0, "コッコロ", 4, 105901, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "さすが【美食殿】のママですね！\nそしてキャルさんは…", 10, 481, 0, "カリン", 2, 190801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "大丈夫…大丈夫…\nあたしは大丈夫…", 37, 482, 0, "キャル", 1, 106001, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 11, 483, 0, "カリン", 2, 190801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あうー？", 37, 484, 0, "シェフィ", 0, 106401, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "あ…", 37, 485, 1, "みんな", 4, 105801, "105901,34,106001,38,190801,12", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 39, 486, 0, "キャル", 1, 106001, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 35, 487, 0, "コッコロ", 4, 105901, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 38, 488, 0, "ペコリーヌ", 3, 105801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 13, 489, 0, "カリン", 2, 190801, "", 0, 13, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 38, 490, 0, "シェフィ", 2, 106401, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 39, 491, 0, "ペコリーヌ", 3, 105801, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 40, 492, 0, "キャル", 0, 106001, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 36, 493, 0, "コッコロ", 1, 105901, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 39, 494, 0, "シェフィ", 2, 106401, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 40, 495, 0, "ペコリーヌ", 3, 105801, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 37, 496, 0, "コッコロ", 1, 105901, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 41, 497, 0, "キャル", 0, 106001, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "へ、へぇ…", 40, 498, 0, "シェフィ", 2, 106401, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 41, 499, 0, "ペコリーヌ", 3, 105801, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 41, 500, 0, "シェフィ", 2, 106401, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 42, 501, 0, "シェフィ", 2, 106401, "", 0, 14, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 42, 502, 0, "ペコリーヌ", 3, 105801, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "祝！", 42, 503, 0, "キャル", 1, 106001, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "3.5周年！", 38, 504, 0, "コッコロ", 4, 105901, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "おめでとうございます！", 43, 505, 0, "シェフィ", 0, 106401, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 14, 506, 0, "カリン", 2, 190801, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 15, 507, 0, "カリン", 2, 190801, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 43, 508, 0, "ペコリーヌ", 3, 105801, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 44, 509, 0, "ペコリーヌ", 3, 105801, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 43, 510, 0, "キャル", 1, 106001, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 44, 511, 0, "キャル", 1, 106001, "", 0, 15, 80005);
INSERT INTO `v1_b2df4b898e2fd50beb5df7a37579a56a654ac5842d0d274ec5e2be10a8b21b6e` VALUES (0, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 44, 512, 0, "シェフィ", 0, 106401, "", 0, 15, 80005);
