INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (7, "2024/03/31 12:00:00", 24, 129901, 100, "エリコ", "2030/12/17 14:59:59", "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 100, 1, 0.0, 10142, 10.85);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129901, 101, "エリコ", "2030/12/17 14:59:59", "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 101, 1, 0.0, 10142, 11.15);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (5, "2024/03/31 12:00:00", 24, 129901, 102, "エリコ", "2030/12/17 14:59:59", "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 102, 7, 0.0, 10142, 6.4);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (3, "2024/03/31 12:00:00", 24, 129901, 103, "エリコ", "2030/12/17 14:59:59", "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 103, 3, 0.0, 10142, 0.0);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (8, "2024/03/31 12:00:00", 24, 129901, 104, "エリコ", "2030/12/17 14:59:59", "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 104, 2, 0.0, 10142, 2.43);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (3, "2024/03/31 12:00:00", 24, 129901, 105, "エリコ", "2030/12/17 14:59:59", "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 105, 4, 0.0, 10142, 9.08);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (6, "2024/03/31 12:00:00", 24, 129901, 106, "エリコ", "2030/12/17 14:59:59", "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 106, 6, 0.0, 10142, 0.0);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (8, "2024/03/31 12:00:00", 24, 129901, 107, "エリコ", "2030/12/17 14:59:59", "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 107, 3, 0.0, 10142, 10.6);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (4, "2024/03/31 12:00:00", 24, 129901, 108, "エリコ", "2030/12/17 14:59:59", "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 108, 6, 0.0, 10142, 7.54);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (7, "2024/03/31 12:00:00", 24, 129901, 109, "エリコ", "2030/12/17 14:59:59", "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 109, 2, 0.0, 10142, 11.5);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129901, 110, "エリコ", "2030/12/17 14:59:59", "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 110, 2, 0.0, 10142, 8.83);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129901, 111, "エリコ", "2030/12/17 14:59:59", "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 111, 1, 0.0, 10142, 6.68);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129901, 112, "エリコ", "2030/12/17 14:59:59", "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 112, 6, 0.0, 10142, 8.8);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129901, 113, "エリコ", "2030/12/17 14:59:59", "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 113, 2, 0.0, 10142, 8.9);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (5, "2024/03/31 12:00:00", 24, 129901, 114, "エリコ", "2030/12/17 14:59:59", "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 114, 2, 0.0, 10142, 3.7);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129801, 200, "マコト", "2030/12/17 14:59:59", "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 200, 1, 0.0, 10142, 7.6);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 201, "マコト", "2030/12/17 14:59:59", "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 201, 2, 0.0, 10142, 2.25);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 202, "マコト", "2030/12/17 14:59:59", "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 202, 2, 0.0, 10142, 3.28);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 203, "マコト", "2030/12/17 14:59:59", "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 203, 4, 0.0, 10142, 2.98);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 204, "マコト", "2030/12/17 14:59:59", "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 204, 4, 0.0, 10142, 3.0);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129801, 205, "マコト", "2030/12/17 14:59:59", "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 205, 1, 0.0, 10142, 10.3);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 206, "マコト", "2030/12/17 14:59:59", "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 206, 6, 0.0, 10142, 2.93);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 207, "マコト", "2030/12/17 14:59:59", "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 207, 3, 0.0, 10142, 9.3);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 208, "マコト", "2030/12/17 14:59:59", "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 208, 4, 0.0, 10142, 6.9);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129801, 209, "マコト", "2030/12/17 14:59:59", "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 209, 2, 0.0, 10142, 0.0);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129801, 210, "マコト", "2030/12/17 14:59:59", "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 210, 1, 0.0, 10142, 5.95);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 211, "マコト", "2030/12/17 14:59:59", "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 211, 2, 0.0, 10142, 6.73);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129801, 212, "マコト", "2030/12/17 14:59:59", "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 212, 2, 0.0, 10142, 0.0);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (2, "2024/03/31 12:00:00", 24, 129801, 213, "マコト", "2030/12/17 14:59:59", "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 213, 1, 0.0, 10142, 6.65);
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (1, "2024/03/31 12:00:00", 24, 129801, 214, "マコト", "2030/12/17 14:59:59", "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 214, 6, 0.0, 10142, 3.05);
