INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (100, 0.0, 10142, "2030/12/17 14:59:59", 1, 7, 129901, 10.85, 24, "エリコ", "2024/03/31 12:00:00", 100, 0.0, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (101, 0.0, 10142, "2030/12/17 14:59:59", 1, 2, 129901, 11.15, 24, "エリコ", "2024/03/31 12:00:00", 101, 0.0, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (102, 0.0, 10142, "2030/12/17 14:59:59", 7, 5, 129901, 6.4, 24, "エリコ", "2024/03/31 12:00:00", 102, 0.0, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (103, 0.0, 10142, "2030/12/17 14:59:59", 3, 3, 129901, 0.0, 24, "エリコ", "2024/03/31 12:00:00", 103, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (104, 0.0, 10142, "2030/12/17 14:59:59", 2, 8, 129901, 2.43, 24, "エリコ", "2024/03/31 12:00:00", 104, 0.0, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (105, 0.0, 10142, "2030/12/17 14:59:59", 4, 3, 129901, 9.08, 24, "エリコ", "2024/03/31 12:00:00", 105, 0.0, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (106, 0.0, 10142, "2030/12/17 14:59:59", 6, 6, 129901, 0.0, 24, "エリコ", "2024/03/31 12:00:00", 106, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (107, 0.0, 10142, "2030/12/17 14:59:59", 3, 8, 129901, 10.6, 24, "エリコ", "2024/03/31 12:00:00", 107, 0.0, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (108, 0.0, 10142, "2030/12/17 14:59:59", 6, 4, 129901, 7.54, 24, "エリコ", "2024/03/31 12:00:00", 108, 0.0, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (109, 0.0, 10142, "2030/12/17 14:59:59", 2, 7, 129901, 11.5, 24, "エリコ", "2024/03/31 12:00:00", 109, 0.0, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (110, 0.0, 10142, "2030/12/17 14:59:59", 2, 1, 129901, 8.83, 24, "エリコ", "2024/03/31 12:00:00", 110, 0.0, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (111, 0.0, 10142, "2030/12/17 14:59:59", 1, 2, 129901, 6.68, 24, "エリコ", "2024/03/31 12:00:00", 111, 0.0, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (112, 0.0, 10142, "2030/12/17 14:59:59", 6, 2, 129901, 8.8, 24, "エリコ", "2024/03/31 12:00:00", 112, 0.0, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (113, 0.0, 10142, "2030/12/17 14:59:59", 2, 1, 129901, 8.9, 24, "エリコ", "2024/03/31 12:00:00", 113, 0.0, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (114, 0.0, 10142, "2030/12/17 14:59:59", 2, 5, 129901, 3.7, 24, "エリコ", "2024/03/31 12:00:00", 114, 0.0, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (200, 0.0, 10142, "2030/12/17 14:59:59", 1, 2, 129801, 7.6, 24, "マコト", "2024/03/31 12:00:00", 200, 0.0, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (201, 0.0, 10142, "2030/12/17 14:59:59", 2, 1, 129801, 2.25, 24, "マコト", "2024/03/31 12:00:00", 201, 0.0, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (202, 0.0, 10142, "2030/12/17 14:59:59", 2, 1, 129801, 3.28, 24, "マコト", "2024/03/31 12:00:00", 202, 0.0, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (203, 0.0, 10142, "2030/12/17 14:59:59", 4, 1, 129801, 2.98, 24, "マコト", "2024/03/31 12:00:00", 203, 0.0, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (204, 0.0, 10142, "2030/12/17 14:59:59", 4, 1, 129801, 3.0, 24, "マコト", "2024/03/31 12:00:00", 204, 0.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (205, 0.0, 10142, "2030/12/17 14:59:59", 1, 2, 129801, 10.3, 24, "マコト", "2024/03/31 12:00:00", 205, 0.0, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (206, 0.0, 10142, "2030/12/17 14:59:59", 6, 1, 129801, 2.93, 24, "マコト", "2024/03/31 12:00:00", 206, 0.0, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (207, 0.0, 10142, "2030/12/17 14:59:59", 3, 1, 129801, 9.3, 24, "マコト", "2024/03/31 12:00:00", 207, 0.0, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (208, 0.0, 10142, "2030/12/17 14:59:59", 4, 1, 129801, 6.9, 24, "マコト", "2024/03/31 12:00:00", 208, 0.0, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (209, 0.0, 10142, "2030/12/17 14:59:59", 2, 2, 129801, 0.0, 24, "マコト", "2024/03/31 12:00:00", 209, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (210, 0.0, 10142, "2030/12/17 14:59:59", 1, 2, 129801, 5.95, 24, "マコト", "2024/03/31 12:00:00", 210, 0.0, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (211, 0.0, 10142, "2030/12/17 14:59:59", 2, 1, 129801, 6.73, 24, "マコト", "2024/03/31 12:00:00", 211, 0.0, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (212, 0.0, 10142, "2030/12/17 14:59:59", 2, 2, 129801, 0.0, 24, "マコト", "2024/03/31 12:00:00", 212, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (213, 0.0, 10142, "2030/12/17 14:59:59", 1, 2, 129801, 6.65, 24, "マコト", "2024/03/31 12:00:00", 213, 0.0, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ");
INSERT INTO `v1_b36f78808fe1f383fa206447cfc0495486fa1385d27c50294a6465741f690f2e` VALUES (214, 0.0, 10142, "2030/12/17 14:59:59", 6, 1, 129801, 3.05, 24, "マコト", "2024/03/31 12:00:00", 214, 0.0, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！");
