INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 0, 1, 0, 60000, 1, 1, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 1, 0, 0, 60000, 2, 2, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 1, 0, 0, 60000, 2, 3, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2, 0, 1, 0, 0, 60000, 2, 4, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 1, 0, 2, 0, 0, 60000, 2, 5, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 1, 0, 2, 0, 0, 60000, 2, 6, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 2, 0, 0, 60000, 2, 7, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 1, 0, 3, 0, 0, 60000, 2, 8, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 1, 0, 3, 0, 0, 60000, 2, 9, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 3, 0, 0, 60000, 2, 10, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 3, 0, 0, 60000, 2, 11, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2, 0, 3, 0, 0, 60000, 2, 12, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 1, 0, 4, 0, 0, 60000, 2, 13, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 1, 0, 4, 0, 0, 60000, 2, 14, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 1, 0, 4, 0, 0, 60000, 2, 15, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 1, 0, 4, 0, 0, 60000, 2, 16, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 4, 0, 0, 60000, 2, 17, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 0, 2, 0, 60000, 3, 18, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 0, 3, 7, 60000, 3, 19, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ハツネ", "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、", 0, 101201, 0, 1, 1, 80001, 0, 20, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ハツネ", "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪", 0, 101201, 0, 2, 1, 80001, 0, 21, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シオリ", "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね", 0, 103801, 0, 1, 1, 80001, 1, 22, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シオリ", "来年も、\n一緒にいられたら…\nうれしいな", 0, 103801, 0, 2, 1, 80001, 1, 23, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キョウカ", "あなたと一緒にいるようになって、\nもう1年ですか…", 0, 103601, 0, 1, 2, 80001, 0, 24, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キョウカ", "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？", 0, 103601, 0, 2, 2, 80001, 0, 25, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミミ", "おにいちゃんとあってから、\n1年たったよ～！", 0, 102001, 0, 1, 2, 80001, 1, 26, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミミ", "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！", 0, 102001, 0, 2, 2, 80001, 1, 27, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミソギ", "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ", 0, 100401, 0, 1, 2, 80001, 2, 28, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミソギ", "…これからもいっぱいミソギと\n遊んでね、にいちゃん！", 0, 100401, 0, 2, 2, 80001, 2, 29, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("マホ", "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ", 0, 101001, 0, 1, 3, 80001, 0, 30, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("マホ", "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ", 0, 101001, 0, 2, 3, 80001, 0, 31, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("イリヤ", "お主と会って早1年じゃな\nこれからも存分に尽くすがよい", 0, 104401, 0, 1, 3, 80001, 1, 32, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("イリヤ", "わらわもたまには、お主の献身に\n報いてやらぬでもない♪", 0, 104401, 0, 2, 3, 80001, 1, 33, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カオリ", "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！", 0, 101701, 0, 1, 3, 80001, 2, 34, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カオリ", "これからも、どうぞよろしく\nお願いするさ～！", 0, 101701, 0, 2, 3, 80001, 2, 35, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("サレン", "あんたと出会ってからもうそんなに\nたつのね～", 0, 102801, 0, 1, 4, 80001, 0, 36, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("サレン", "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！", 0, 102801, 0, 2, 4, 80001, 0, 37, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("スズメ", "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…", 0, 102501, 0, 1, 4, 80001, 1, 38, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("スズメ", "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪", 0, 102501, 0, 2, 4, 80001, 1, 39, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("タマキ", "振り返れば、今までいろんなことが\nあったにゃあ…", 0, 104601, 0, 1, 5, 80001, 0, 40, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("タマキ", "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！", 0, 104601, 0, 2, 5, 80001, 0, 41, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミフユ", "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、", 0, 104801, 0, 1, 5, 80001, 1, 42, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミフユ", "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！", 0, 104801, 0, 2, 5, 80001, 1, 43, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("アキノ", "あなたさまと出会ってもう1年…", 0, 103201, 0, 1, 6, 80001, 0, 44, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("アキノ", "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね", 0, 103201, 0, 2, 6, 80001, 0, 45, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ユカリ", "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう", 0, 103401, 0, 1, 6, 80001, 1, 46, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ユカリ", "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな", 0, 103401, 0, 2, 6, 80001, 1, 47, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("クリスティーナ", "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…", 0, 107101, 0, 1, 7, 80001, 0, 48, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("クリスティーナ", "ともかくこれからも\n愉しませてくれよ☆", 0, 107101, 0, 2, 7, 80001, 0, 49, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ジュン", "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？", 0, 104701, 0, 1, 7, 80001, 1, 50, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ジュン", "見えないから分からないって？\nそれもそうだな…", 0, 104701, 0, 2, 7, 80001, 1, 51, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("トモ", "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪", 0, 103701, 0, 1, 8, 80001, 0, 52, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("トモ", "これからもよろしくね！", 0, 103701, 0, 2, 8, 80001, 0, 53, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("マツリ", "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス", 0, 100501, 0, 1, 8, 80001, 1, 54, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("マツリ", "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！", 0, 100501, 0, 2, 8, 80001, 1, 55, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミヤコ", "オマエと会って1年なの！\n全然成仏もできなかったし、", 0, 100701, 0, 1, 9, 80001, 0, 56, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミヤコ", "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪", 0, 100701, 0, 2, 9, 80001, 0, 57, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シノブ", "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは", 0, 103101, 0, 1, 9, 80001, 1, 58, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シノブ", "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね", 0, 103101, 0, 2, 9, 80001, 1, 59, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("アンナ", "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、", 0, 100901, 0, 1, 10, 80001, 0, 60, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("アンナ", "それでも貴様となら…\n添い遂げる覚悟はできているぞ！", 0, 100901, 0, 2, 10, 80001, 0, 61, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ナナカ", "君と出会ってもう1年だって！\n早いねー！", 0, 101301, 0, 1, 10, 80001, 1, 62, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ナナカ", "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！", 0, 101301, 0, 2, 10, 80001, 1, 63, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミツキ", "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…", 0, 105101, 0, 1, 11, 80001, 0, 64, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ミツキ", "これからも、いろんなところを\n調べさせてね？", 0, 105101, 0, 2, 11, 80001, 0, 65, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ルカ", "お前さんと出会ってもう1年か\n助太刀するつもりが", 0, 105601, 0, 1, 11, 80001, 1, 66, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ルカ", "逆に助けられたりもしたね\nこれからもよろしく頼むよ！", 0, 105601, 0, 2, 11, 80001, 1, 67, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("エリコ", "早いものですわね、あなた様と\nお会いして、もう1年…", 0, 102701, 0, 1, 11, 80001, 2, 68, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("エリコ", "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…", 0, 102701, 0, 2, 11, 80001, 2, 69, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("アヤネ", "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？", 0, 102301, 0, 1, 12, 80001, 0, 70, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("アヤネ", "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？", 0, 102301, 0, 2, 12, 80001, 0, 71, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("チカ", "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、", 0, 104201, 0, 1, 12, 80001, 1, 72, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("チカ", "私のことを見守ってください\nできることなら、ずっと…", 0, 104201, 0, 2, 12, 80001, 1, 73, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("クルミ", "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…", 0, 102101, 0, 1, 12, 80001, 2, 74, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("クルミ", "で、でもこれからもずっと…\n一緒にいたいなぁ…", 0, 102101, 0, 2, 12, 80001, 2, 75, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シズル", "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、", 0, 104901, 0, 1, 13, 80001, 0, 76, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シズル", "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！", 0, 104901, 0, 2, 13, 80001, 0, 77, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("リノ", "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、", 0, 101101, 0, 1, 13, 80001, 1, 78, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("リノ", "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪", 0, 101101, 0, 2, 13, 80001, 1, 79, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ユイ", "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…", 0, 100201, 0, 1, 14, 80001, 0, 80, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ユイ", "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！", 0, 100201, 0, 2, 14, 80001, 0, 81, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ヒヨリ", "騎士くんと出会って、まだ1年しか\nたってないんだ！？", 0, 100101, 0, 1, 14, 80001, 1, 82, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ヒヨリ", "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！", 0, 100101, 0, 2, 14, 80001, 1, 83, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("レイ", "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…", 0, 100301, 0, 1, 14, 80001, 2, 84, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("レイ", "キミに背中を預けられる日が、\n早くきますように", 0, 100301, 0, 2, 14, 80001, 2, 85, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！", 0, 105801, 0, 1, 15, 80001, 0, 86, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "これからもずっと、覚えていて\nくださいね！", 0, 105801, 0, 2, 15, 80001, 0, 87, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、", 0, 105901, 0, 1, 15, 80001, 1, 88, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです", 0, 105901, 0, 2, 15, 80001, 1, 89, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも", 0, 106001, 0, 1, 15, 80001, 2, 90, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！", 0, 106001, 0, 2, 15, 80001, 2, 91, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("スズメ", "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪", 1, 112101, 0, 1, 0, 60001, 1, 92, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！", 1, 0, 1, 0, 0, 60001, 2, 93, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 1, 0, 0, 60001, 2, 94, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 1, 0, 0, 60001, 2, 95, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 1, 0, 0, 60001, 2, 96, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！", 2, 0, 1, 0, 0, 60001, 2, 97, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪", 1, 0, 2, 0, 0, 60001, 2, 98, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪", 1, 0, 2, 0, 0, 60001, 2, 99, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 1, 0, 2, 0, 0, 60001, 2, 100, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 1, 0, 2, 0, 0, 60001, 2, 101, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！", 2, 0, 2, 0, 0, 60001, 2, 102, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！", 1, 0, 3, 0, 0, 60001, 2, 103, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪", 1, 0, 3, 0, 0, 60001, 2, 104, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 3, 0, 0, 60001, 2, 105, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 3, 0, 0, 60001, 2, 106, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？", 1, 0, 3, 0, 0, 60001, 2, 107, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！", 1, 0, 3, 0, 0, 60001, 2, 108, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪", 2, 0, 3, 0, 0, 60001, 2, 109, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪", 1, 0, 4, 0, 0, 60001, 2, 110, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪", 1, 0, 4, 0, 0, 60001, 2, 111, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪", 1, 0, 4, 0, 0, 60001, 2, 112, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 1, 0, 4, 0, 0, 60001, 2, 113, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 1, 0, 4, 0, 0, 60001, 2, 114, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！", 1, 0, 4, 0, 0, 60001, 2, 115, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 4, 0, 0, 60001, 2, 116, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("スズメ", "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪", 0, 112101, 0, 2, 0, 60001, 3, 117, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("スズメ", "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね", 0, 112101, 0, 3, 7, 60001, 3, 118, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "ヒューヒュー♪\nドンドンパフパフー♪", 0, 105801, 0, 1, 1, 80003, 0, 119, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ひゅーひゅー\nどんどんぱふぱふー\nでございます", 0, 105901, 0, 1, 1, 80003, 1, 120, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう", 0, 105901, 0, 2, 1, 80003, 1, 121, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "え？　ちょっと\nいきなりなにっ？", 0, 106001, 0, 1, 1, 80003, 2, 122, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！", 0, 106001, 0, 2, 1, 80003, 2, 123, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──", 0, 105801, 0, 2, 1, 80003, 0, 124, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆", 0, 105801, 0, 3, 1, 80003, 0, 125, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ですからその日に向けて\n今から気分を盛り上げているのです", 0, 105901, 0, 3, 1, 80003, 1, 126, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？", 0, 106001, 0, 3, 1, 80003, 2, 127, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま", 0, 105901, 0, 4, 1, 80003, 1, 128, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆", 0, 105801, 0, 4, 1, 80003, 0, 129, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ", 0, 106001, 0, 4, 1, 80003, 2, 130, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ", 0, 105901, 0, 5, 1, 80003, 1, 131, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…", 0, 106001, 0, 5, 2, 80003, 0, 132, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど", 0, 190801, 0, 1, 2, 80003, 1, 133, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "げっ…カリンじゃない\nなんでもないわよ！", 0, 106001, 0, 6, 2, 80003, 0, 134, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…", 0, 190801, 0, 2, 2, 80003, 1, 135, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？", 0, 106001, 0, 7, 2, 80003, 0, 136, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？", 0, 190801, 0, 3, 2, 80003, 1, 137, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし", 0, 106001, 0, 8, 2, 80003, 0, 138, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど", 0, 106001, 0, 9, 2, 80003, 0, 139, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！", 0, 190801, 0, 4, 2, 80003, 1, 140, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！", 0, 106001, 0, 10, 2, 80003, 0, 141, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！", 0, 190801, 0, 5, 2, 80003, 1, 142, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！", 0, 106001, 0, 11, 2, 80003, 0, 143, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます", 0, 105901, 0, 6, 3, 80003, 0, 144, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪", 0, 105801, 0, 5, 3, 80003, 1, 145, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ", 0, 105901, 0, 7, 3, 80003, 0, 146, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか", 0, 105901, 0, 8, 3, 80003, 0, 147, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？", 0, 105801, 0, 6, 3, 80003, 1, 148, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！", 0, 105801, 0, 7, 3, 80003, 1, 149, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "あとは、牧場に素敵な温泉が\n出来たとか…", 0, 105901, 0, 9, 3, 80003, 0, 150, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆", 0, 105801, 0, 8, 3, 80003, 1, 151, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき", 0, 105901, 0, 10, 3, 80003, 0, 152, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "えへへ、ありがとうございます\nコッコロちゃん！", 0, 105801, 0, 9, 3, 80003, 1, 153, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました", 0, 105901, 0, 11, 3, 80003, 0, 154, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！", 0, 105801, 0, 10, 3, 80003, 1, 155, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆", 0, 105801, 0, 11, 4, 80003, 0, 156, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ", 0, 106001, 0, 12, 4, 80003, 1, 157, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？", 0, 105801, 0, 12, 4, 80003, 0, 158, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？", 0, 106001, 0, 13, 4, 80003, 1, 159, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！", 0, 105801, 0, 13, 4, 80003, 0, 160, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "…積極的？\nなんの話よ？", 0, 106001, 0, 14, 4, 80003, 1, 161, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？", 0, 105801, 0, 14, 4, 80003, 0, 162, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "ムーサの町で\nきゃるきゃる～ん☆　って！", 0, 105801, 0, 15, 4, 80003, 0, 163, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！", 0, 106001, 0, 15, 4, 80003, 1, 164, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～", 0, 105801, 0, 16, 4, 80003, 0, 165, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？", 0, 106001, 0, 16, 4, 80003, 1, 166, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！", 0, 106001, 0, 17, 4, 80003, 1, 167, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？", 0, 106001, 0, 18, 5, 80003, 0, 168, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！", 0, 105901, 0, 12, 5, 80003, 1, 169, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？", 0, 106001, 0, 19, 5, 80003, 0, 170, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです", 0, 105901, 0, 13, 5, 80003, 1, 171, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての", 0, 106001, 0, 20, 5, 80003, 0, 172, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして", 0, 105901, 0, 14, 5, 80003, 1, 173, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど", 0, 106001, 0, 21, 5, 80003, 0, 174, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして", 0, 105901, 0, 15, 5, 80003, 1, 175, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "かくし芸？", 0, 106001, 0, 22, 5, 80003, 0, 176, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…", 0, 105901, 0, 16, 5, 80003, 1, 177, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！", 0, 106001, 0, 23, 5, 80003, 0, 178, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆", 0, 105801, 0, 17, 6, 80003, 0, 179, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？", 0, 106001, 0, 24, 6, 80003, 2, 180, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから", 0, 105901, 0, 17, 6, 80003, 1, 181, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─", 0, 105801, 0, 18, 6, 80003, 0, 182, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ", 0, 106001, 0, 25, 6, 80003, 2, 183, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…", 0, 105901, 0, 18, 6, 80003, 1, 184, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね", 0, 106001, 0, 26, 6, 80003, 2, 185, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって", 0, 106001, 0, 27, 6, 80003, 2, 186, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね", 0, 106001, 0, 28, 6, 80003, 2, 187, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪", 0, 105801, 0, 19, 6, 80003, 0, 188, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪", 0, 105901, 0, 19, 6, 80003, 1, 189, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない", 0, 106001, 0, 29, 6, 80003, 2, 190, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！", 0, 106001, 0, 30, 7, 80003, 0, 191, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？", 0, 106001, 0, 31, 7, 80003, 0, 192, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…", 0, 106001, 0, 32, 7, 80003, 0, 193, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あー\nあー\n…声、これで大丈夫かしら？", 0, 106001, 0, 33, 7, 80003, 0, 194, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？", 0, 106001, 0, 34, 7, 80003, 0, 195, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "オイッスー☆", 0, 106001, 0, 35, 7, 80003, 0, 196, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "……\nいや、ないわ\nいつもどおりが一番ね、うん", 0, 106001, 0, 36, 7, 80003, 0, 197, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…", 0, 106001, 0, 37, 7, 80003, 0, 198, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ど、どど、どうしよう！？\n緊張してきちゃった…！！", 0, 106001, 0, 38, 7, 80003, 0, 199, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました", 0, 105901, 0, 20, 8, 80003, 0, 200, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます", 0, 105901, 0, 21, 8, 80003, 0, 201, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております", 0, 105901, 0, 22, 8, 80003, 0, 202, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…", 0, 105901, 0, 23, 8, 80003, 0, 203, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…", 0, 105901, 0, 24, 8, 80003, 0, 204, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "オイッスー☆", 0, 105901, 0, 25, 8, 80003, 0, 205, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？", 0, 105901, 0, 26, 8, 80003, 0, 206, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます", 0, 105901, 0, 27, 8, 80003, 0, 207, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね", 0, 105901, 0, 28, 8, 80003, 0, 208, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "オイッスー☆", 0, 105801, 0, 20, 9, 80003, 0, 209, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません", 0, 105801, 0, 21, 9, 80003, 0, 210, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あと６日で2.5周年ですか…", 0, 105801, 0, 22, 9, 80003, 0, 211, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆", 0, 105801, 0, 23, 9, 80003, 0, 212, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…", 0, 105801, 0, 24, 9, 80003, 0, 213, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "オイッスー☆", 0, 105801, 0, 25, 9, 80003, 0, 214, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？", 0, 105801, 0, 26, 9, 80003, 0, 215, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！", 0, 105801, 0, 27, 9, 80003, 0, 216, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！", 0, 105801, 0, 28, 9, 80003, 0, 217, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪", 0, 105801, 0, 29, 9, 80003, 0, 218, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪", 0, 105801, 0, 30, 9, 80003, 0, 219, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？", 0, 105801, 0, 31, 10, 80003, 0, 220, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません", 0, 105901, 0, 29, 10, 80003, 1, 221, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？", 0, 105801, 0, 32, 10, 80003, 0, 222, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "…………", 0, 106001, 0, 39, 10, 80003, 2, 223, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…", 0, 105901, 0, 30, 10, 80003, 1, 224, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！", 0, 106001, 0, 40, 10, 80003, 2, 225, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！", 0, 105801, 0, 33, 10, 80003, 0, 226, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？", 0, 106001, 0, 41, 10, 80003, 2, 227, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！", 0, 105801, 0, 34, 10, 80003, 0, 228, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪", 0, 105801, 0, 35, 10, 80003, 0, 229, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！", 0, 106001, 0, 42, 10, 80003, 2, 230, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました", 0, 105901, 0, 31, 10, 80003, 1, 231, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも", 0, 106001, 0, 43, 10, 80003, 2, 232, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆", 0, 105801, 0, 36, 10, 80003, 0, 233, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです", 0, 190801, 0, 6, 11, 80003, 0, 234, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています", 0, 190801, 0, 7, 11, 80003, 0, 235, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください", 0, 190801, 0, 8, 11, 80003, 0, 236, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います", 0, 190801, 0, 9, 11, 80003, 0, 237, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね", 0, 190801, 0, 10, 11, 80003, 0, 238, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "次にコッコロさんですが\nこちらは発声練習をしています", 0, 190801, 0, 11, 11, 80003, 0, 239, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね", 0, 190801, 0, 12, 11, 80003, 0, 240, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…", 0, 190801, 0, 13, 11, 80003, 0, 241, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "あれ？\nもしかして緊張で気を失って…？", 0, 190801, 0, 14, 11, 80003, 0, 242, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！", 0, 190801, 0, 15, 11, 80003, 0, 243, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください", 0, 190801, 0, 16, 11, 80003, 0, 244, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "それでは\nまた明日お会いしましょう♪", 0, 190801, 0, 17, 11, 80003, 0, 245, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…", 0, 106001, 0, 44, 12, 80003, 0, 246, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆", 0, 105801, 0, 37, 12, 80003, 2, 247, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね", 0, 105901, 0, 32, 12, 80003, 1, 248, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから", 0, 106001, 0, 45, 12, 80003, 0, 249, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "ひどいですよぉキャルちゃ～ん！", 0, 105801, 0, 38, 12, 80003, 2, 250, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ふふ。お二人とも\n普段通りの様子で\n安心いたしました", 0, 105901, 0, 33, 12, 80003, 1, 251, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！", 0, 105801, 0, 39, 12, 80003, 2, 252, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！", 0, 106001, 0, 46, 12, 80003, 0, 253, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！", 0, 105901, 0, 34, 12, 80003, 1, 254, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆", 0, 105801, 0, 40, 12, 80003, 2, 255, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？", 0, 105801, 0, 41, 13, 80003, 0, 256, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？", 0, 106001, 0, 47, 13, 80003, 2, 257, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "忘れてきちゃいました☆", 0, 105801, 0, 42, 13, 80003, 0, 258, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！", 0, 106001, 0, 48, 13, 80003, 2, 259, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！", 0, 105901, 0, 35, 13, 80003, 1, 260, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…", 0, 106001, 0, 49, 13, 80003, 2, 261, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？", 0, 105801, 0, 43, 13, 80003, 0, 262, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？", 0, 106001, 0, 50, 13, 80003, 2, 263, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ", 0, 105901, 0, 36, 13, 80003, 1, 264, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね", 0, 106001, 0, 51, 13, 80003, 2, 265, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪", 0, 105901, 0, 37, 13, 80003, 1, 266, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？", 0, 106001, 0, 52, 13, 80003, 2, 267, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆", 0, 105801, 0, 44, 13, 80003, 0, 268, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ふふ\nきっと面白い生放送になると\nわたくし、確信しております", 0, 105901, 0, 38, 13, 80003, 1, 269, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！", 0, 105801, 0, 45, 13, 80003, 0, 270, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！", 0, 106001, 0, 53, 13, 80003, 2, 271, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆", 0, 105801, 0, 46, 13, 80003, 0, 272, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "こんにちは\n【ギルド管理協会】のカリンです", 0, 190801, 0, 18, 14, 80003, 0, 273, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "昨日の生放送は\nいかがだったでしょうか？", 0, 190801, 0, 19, 14, 80003, 0, 274, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います", 0, 190801, 0, 20, 14, 80003, 0, 275, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります", 0, 190801, 0, 21, 14, 80003, 0, 276, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ", 0, 190801, 0, 22, 14, 80003, 0, 277, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います", 0, 190801, 0, 23, 14, 80003, 0, 278, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…", 0, 190801, 0, 24, 14, 80003, 0, 279, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています", 0, 190801, 0, 25, 14, 80003, 0, 280, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう", 0, 190801, 0, 26, 14, 80003, 0, 281, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪", 0, 190801, 0, 27, 14, 80003, 0, 282, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "準備はいいですかっ？\n…いっきますよー！", 0, 105801, 0, 47, 15, 80003, 0, 283, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "祝！", 0, 106001, 0, 54, 15, 80003, 2, 284, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "2.5周年！", 0, 105901, 0, 39, 15, 80003, 1, 285, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "おめでとうございま～す☆", 0, 105801, 0, 48, 15, 80003, 0, 286, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね", 0, 106001, 0, 55, 15, 80003, 2, 287, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです", 0, 105901, 0, 40, 15, 80003, 1, 288, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆", 0, 105801, 0, 49, 15, 80003, 0, 289, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！", 0, 106001, 0, 56, 15, 80003, 2, 290, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪", 0, 105901, 0, 41, 15, 80003, 1, 291, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ", 0, 106001, 0, 57, 15, 80003, 2, 292, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あー！　二人ともずるいです！\nわたしもっ、ギュー♪", 0, 105801, 0, 50, 15, 80003, 0, 293, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ちょっ、ペコリーヌ！\nくっつき過ぎよ！", 0, 106001, 0, 58, 15, 80003, 2, 294, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "記念日だから\n平気平気♪\nやばいですね☆", 0, 105801, 0, 51, 15, 80003, 0, 295, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "意味分かんないわよ！", 0, 106001, 0, 59, 15, 80003, 2, 296, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？", 0, 105901, 0, 42, 15, 80003, 1, 297, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます", 0, 105901, 0, 43, 15, 80003, 1, 298, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！", 0, 105801, 0, 52, 15, 80003, 0, 299, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから", 0, 106001, 0, 60, 15, 80003, 2, 300, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！", 0, 105901, 0, 44, 15, 80003, 1, 301, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！", 0, 105801, 0, 53, 15, 80003, 0, 302, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ・コッコロ・キャル", "2.5周年\nありがとうございます！", 0, 105801, 0, 54, 15, 80003, 0, 303, "105901,45,106001,61", 1);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 0, 1, 0, 60002, 1, 304, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 1, 0, 0, 60002, 2, 305, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 1, 0, 0, 60002, 2, 306, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！", 1, 0, 1, 0, 0, 60002, 2, 307, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！", 1, 0, 1, 0, 0, 60002, 2, 308, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2, 0, 1, 0, 0, 60002, 2, 309, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 1, 0, 2, 0, 0, 60002, 2, 310, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 1, 0, 2, 0, 0, 60002, 2, 311, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！", 1, 0, 2, 0, 0, 60002, 2, 312, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！", 1, 0, 2, 0, 0, 60002, 2, 313, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 2, 0, 0, 60002, 2, 314, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 1, 0, 3, 0, 0, 60002, 2, 315, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 1, 0, 3, 0, 0, 60002, 2, 316, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 3, 0, 0, 60002, 2, 317, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 1, 0, 3, 0, 0, 60002, 2, 318, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！", 1, 0, 3, 0, 0, 60002, 2, 319, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 3, 0, 0, 60002, 2, 320, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪", 2, 0, 3, 0, 0, 60002, 2, 321, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 1, 0, 4, 0, 0, 60002, 2, 322, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 1, 0, 4, 0, 0, 60002, 2, 323, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 1, 0, 4, 0, 0, 60002, 2, 324, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……", 1, 0, 4, 0, 0, 60002, 2, 325, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！", 1, 0, 4, 0, 0, 60002, 2, 326, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～", 1, 0, 4, 0, 0, 60002, 2, 327, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪", 2, 0, 4, 0, 0, 60002, 2, 328, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪", 0, 190804, 0, 2, 0, 60002, 3, 329, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 0, 3, 7, 60002, 3, 330, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ささ、シェフィさまはこちらへ…", 0, 105901, 0, 1, 1, 80005, 4, 331, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "コッコロさん、どこへ行くの…？\n…え？", 0, 106401, 0, 1, 1, 80005, 0, 332, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？", 0, 105801, 0, 1, 1, 80005, 3, 333, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね", 0, 105901, 0, 2, 1, 80005, 4, 334, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "ええと…\n二人とも何をしているの？", 0, 106401, 0, 2, 1, 80005, 0, 335, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～", 0, 106001, 0, 1, 1, 80005, 1, 336, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "あの日…？", 0, 106401, 0, 3, 1, 80005, 0, 337, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！", 0, 105801, 0, 2, 1, 80005, 3, 338, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "『プリンセスコネクト！\nRe:Dive』3.5周年！", 0, 105801, 0, 3, 1, 80005, 3, 339, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "やばいですね☆", 0, 190801, 0, 1, 1, 80005, 2, 340, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "って、なにしれっと\n参加してんのよカリン！", 0, 106001, 0, 2, 1, 80005, 1, 341, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！", 0, 190801, 0, 2, 1, 80005, 2, 342, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪", 0, 190801, 0, 3, 1, 80005, 2, 343, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…", 0, 105801, 0, 4, 1, 80005, 3, 344, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "えっ！？　ええとええと…！\nあ、あと14日…！", 0, 106401, 0, 4, 1, 80005, 0, 345, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆", 0, 105801, 0, 5, 2, 80005, 3, 346, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました", 0, 105901, 0, 3, 2, 80005, 1, 347, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？", 0, 105901, 0, 4, 2, 80005, 1, 348, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり", 0, 105801, 0, 6, 2, 80005, 3, 349, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "さすがでございます\nペコリーヌさま", 0, 105901, 0, 5, 2, 80005, 1, 350, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし", 0, 106001, 0, 3, 2, 80005, 0, 351, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "しゃなり、しゃなり…\nふふ、案外できるものね", 0, 106401, 0, 5, 2, 80005, 2, 352, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪", 0, 105801, 0, 7, 2, 80005, 3, 353, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "さ、次はキャルさまの\n番でございますよ", 0, 105901, 0, 6, 2, 80005, 1, 354, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あ、あたしはいいわよ！\nそういうの全然分かんないし…", 0, 106001, 0, 4, 2, 80005, 0, 355, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "大丈夫\nやってみると結構楽しいわよ\nほらほらっ", 0, 106401, 0, 6, 2, 80005, 2, 356, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！", 0, 106001, 0, 5, 2, 80005, 0, 357, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "いたたた…もぉお～！\nなんであたしがこんな目に～！", 0, 106001, 0, 6, 2, 80005, 0, 358, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ", 0, 105901, 0, 7, 2, 80005, 1, 359, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！", 0, 106001, 0, 7, 2, 80005, 0, 360, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります", 0, 105901, 0, 8, 3, 80005, 1, 361, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！", 0, 105801, 0, 8, 3, 80005, 3, 362, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…", 0, 105901, 0, 9, 3, 80005, 1, 363, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "聖学祭…って、なにかしら？", 0, 106401, 0, 7, 3, 80005, 2, 364, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです", 0, 105901, 0, 10, 3, 80005, 1, 365, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…", 0, 106001, 0, 8, 3, 80005, 0, 366, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？", 0, 106001, 0, 9, 3, 80005, 0, 367, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "なかよし部…\n仲が良さそうな名前で\nやばいですね☆", 0, 105801, 0, 9, 3, 80005, 3, 368, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "いや、そのまんますぎでしょ…", 0, 106001, 0, 10, 3, 80005, 0, 369, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ", 0, 106401, 0, 8, 3, 80005, 2, 370, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪", 0, 105801, 0, 10, 3, 80005, 3, 371, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "えっ？　ええ…\nぎゅーっ…？", 0, 106401, 0, 9, 3, 80005, 2, 372, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…", 0, 106001, 0, 11, 4, 80005, 0, 373, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…", 0, 106401, 0, 10, 4, 80005, 2, 374, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…", 0, 106001, 0, 12, 4, 80005, 0, 375, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから", 0, 105901, 0, 11, 4, 80005, 1, 376, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし", 0, 106001, 0, 13, 4, 80005, 0, 377, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！", 0, 105801, 0, 11, 4, 80005, 3, 378, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！", 0, 106001, 0, 14, 4, 80005, 0, 379, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ", 0, 106001, 0, 15, 4, 80005, 0, 380, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…", 0, 106401, 0, 11, 4, 80005, 2, 381, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！", 0, 105801, 0, 12, 4, 80005, 3, 382, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう", 0, 105901, 0, 12, 4, 80005, 1, 383, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！", 0, 106001, 0, 16, 4, 80005, 0, 384, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！", 0, 106401, 0, 12, 4, 80005, 2, 385, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！", 0, 105801, 0, 13, 5, 80005, 3, 386, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！", 0, 105801, 0, 14, 5, 80005, 3, 387, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！", 0, 105801, 0, 15, 5, 80005, 3, 388, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？", 0, 106001, 0, 17, 5, 80005, 0, 389, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！", 0, 106001, 0, 18, 5, 80005, 0, 390, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？", 0, 106401, 0, 13, 5, 80005, 2, 391, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…", 0, 105901, 0, 13, 5, 80005, 1, 392, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？", 0, 105801, 0, 16, 5, 80005, 3, 393, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…", 0, 106001, 0, 19, 5, 80005, 0, 394, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪", 0, 105801, 0, 17, 5, 80005, 3, 395, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪", 0, 105801, 0, 18, 5, 80005, 3, 396, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！", 0, 105801, 0, 19, 5, 80005, 3, 397, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！", 0, 190801, 0, 4, 6, 80005, 2, 398, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！", 0, 190801, 0, 5, 6, 80005, 2, 399, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "…えっ！？", 0, 106001, 0, 20, 6, 80005, 1, 400, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆", 0, 105801, 0, 20, 6, 80005, 3, 401, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！", 0, 106401, 0, 14, 6, 80005, 0, 402, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま", 0, 105901, 0, 14, 6, 80005, 4, 403, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…", 0, 106401, 0, 15, 6, 80005, 0, 404, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！", 0, 106001, 0, 21, 6, 80005, 1, 405, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！", 0, 105801, 0, 21, 6, 80005, 3, 406, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ぶっ殺すぞ！？", 0, 106001, 0, 22, 6, 80005, 1, 407, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪", 0, 190801, 0, 6, 6, 80005, 2, 408, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま", 0, 105901, 0, 15, 7, 80005, 1, 409, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？", 0, 106001, 0, 23, 7, 80005, 0, 410, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね", 0, 106401, 0, 16, 7, 80005, 2, 411, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…", 0, 105801, 0, 22, 7, 80005, 3, 412, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね", 0, 106001, 0, 24, 7, 80005, 0, 413, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ", 0, 106401, 0, 17, 7, 80005, 2, 414, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう", 0, 105901, 0, 16, 7, 80005, 1, 415, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？", 0, 106401, 0, 18, 7, 80005, 2, 416, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "もちろんでございます\n仲良くお勉強いたしましょう", 0, 105901, 0, 17, 7, 80005, 1, 417, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "しれっと教育ママが\n生まれてる…？", 0, 106001, 0, 25, 7, 80005, 0, 418, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！", 0, 105801, 0, 23, 7, 80005, 3, 419, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "え～～～～！？", 0, 106001, 0, 26, 7, 80005, 0, 420, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪", 0, 106001, 0, 27, 8, 80005, 0, 421, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "…\nねぇコッコロさん\nキャル、どうしちゃったの？", 0, 106401, 0, 19, 8, 80005, 2, 422, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます", 0, 105901, 0, 18, 8, 80005, 1, 423, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪", 0, 105801, 0, 24, 8, 80005, 3, 424, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…", 0, 106401, 0, 20, 8, 80005, 2, 425, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪", 0, 106001, 0, 28, 8, 80005, 0, 426, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね", 0, 105901, 0, 19, 8, 80005, 1, 427, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…", 0, 106401, 0, 21, 8, 80005, 2, 428, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？", 0, 106001, 0, 29, 8, 80005, 0, 429, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？", 0, 106001, 0, 30, 8, 80005, 0, 430, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆", 0, 105801, 0, 25, 8, 80005, 3, 431, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！", 0, 106001, 0, 31, 8, 80005, 0, 432, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね", 0, 106401, 0, 22, 9, 80005, 0, 433, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "…えっ？\n生放送の意気込み！？\n突然なに！？", 0, 106401, 0, 23, 9, 80005, 0, 434, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…", 0, 106401, 0, 24, 9, 80005, 0, 435, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！", 0, 106401, 0, 25, 9, 80005, 0, 436, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "とにかく落ち着いて\nスタッフさんの指示を聞いて…", 0, 106401, 0, 26, 9, 80005, 0, 437, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "それから、それから…！\nぅ、ぅぅっ…！", 0, 106401, 0, 27, 9, 80005, 0, 438, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "―びえぇぇ～んっ！！！", 0, 106401, 0, 28, 9, 80005, 0, 439, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "どうされましたか、シェフィさま", 0, 105901, 0, 20, 9, 80005, 1, 440, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "ううっ…ひっく…\nママッ…！", 0, 106401, 0, 29, 9, 80005, 0, 441, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ", 0, 105901, 0, 21, 9, 80005, 1, 442, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？", 0, 105901, 0, 22, 9, 80005, 1, 443, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "…ううっ\n恥ずかしすぎて\nもう生放送出られない…", 0, 106401, 0, 30, 9, 80005, 0, 444, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "主さま\n3.5周年まであと５日\nでございます", 0, 105901, 0, 23, 10, 80005, 0, 445, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ですがその前に\n生放送がありますね", 0, 105901, 0, 24, 10, 80005, 0, 446, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "…生放送に向けた\n意気込みでございますか？", 0, 105901, 0, 25, 10, 80005, 0, 447, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします", 0, 105901, 0, 26, 10, 80005, 0, 448, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…", 0, 105901, 0, 27, 10, 80005, 0, 449, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります", 0, 105901, 0, 28, 10, 80005, 0, 450, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております", 0, 105901, 0, 29, 10, 80005, 0, 451, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね", 0, 105901, 0, 30, 10, 80005, 0, 452, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね", 0, 105901, 0, 31, 10, 80005, 0, 453, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "3.5周年まであと４日！\nやばいですね☆", 0, 105801, 0, 26, 11, 80005, 0, 454, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？", 0, 106001, 0, 32, 11, 80005, 1, 455, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！", 0, 105801, 0, 27, 11, 80005, 0, 456, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！", 0, 105801, 0, 28, 11, 80005, 0, 457, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？", 0, 105801, 0, 29, 11, 80005, 0, 458, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！", 0, 106001, 0, 33, 11, 80005, 1, 459, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！", 0, 105801, 0, 30, 11, 80005, 0, 460, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！", 0, 105801, 0, 31, 11, 80005, 0, 461, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜", 0, 105801, 0, 32, 11, 80005, 0, 462, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "だからあたしはやるなんて一言も…", 0, 106001, 0, 34, 11, 80005, 1, 463, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "オイッスー☆", 0, 105801, 0, 33, 11, 80005, 0, 464, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "聞きなさいっての！！", 0, 106001, 0, 35, 11, 80005, 1, 465, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "3.5周年まであと３日…\nそして生放送は、明日…", 0, 106401, 0, 31, 12, 80005, 2, 466, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "もっと台本を\n読み込んでおかないと…\nあと発声練習も…", 0, 106401, 0, 32, 12, 80005, 2, 467, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "おいっす！\n…こうじゃなかったわね\nええと確か…", 0, 106401, 0, 33, 12, 80005, 2, 468, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…", 0, 106401, 0, 34, 12, 80005, 2, 469, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…", 0, 106401, 0, 35, 12, 80005, 2, 470, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "やってますねシェフィちゃん！", 0, 105801, 0, 34, 12, 80005, 3, 471, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "なによ、考えることは\nみんな同じってこと？", 0, 106001, 0, 36, 12, 80005, 0, 472, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます", 0, 105901, 0, 32, 12, 80005, 1, 473, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "みんな…もちろんよ！", 0, 106401, 0, 36, 12, 80005, 2, 474, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆", 0, 105801, 0, 35, 12, 80005, 3, 475, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！", 0, 190801, 0, 7, 13, 80005, 2, 476, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！", 0, 190801, 0, 8, 13, 80005, 2, 477, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆", 0, 105801, 0, 36, 13, 80005, 3, 478, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん", 0, 190801, 0, 9, 13, 80005, 2, 479, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう", 0, 105901, 0, 33, 13, 80005, 4, 480, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "さすが【美食殿】のママですね！\nそしてキャルさんは…", 0, 190801, 0, 10, 13, 80005, 2, 481, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "大丈夫…大丈夫…\nあたしは大丈夫…", 0, 106001, 0, 37, 13, 80005, 1, 482, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！", 0, 190801, 0, 11, 13, 80005, 2, 483, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "あうー？", 0, 106401, 0, 37, 13, 80005, 0, 484, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("みんな", "あ…", 0, 105801, 0, 37, 13, 80005, 4, 485, "105901,34,106001,38,190801,12", 1);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！", 0, 106001, 0, 39, 13, 80005, 1, 486, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…", 0, 105901, 0, 35, 13, 80005, 4, 487, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！", 0, 105801, 0, 38, 13, 80005, 3, 488, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！", 0, 190801, 0, 13, 13, 80005, 2, 489, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？", 0, 106401, 0, 38, 14, 80005, 2, 490, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆", 0, 105801, 0, 39, 14, 80005, 3, 491, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし", 0, 106001, 0, 40, 14, 80005, 0, 492, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪", 0, 105901, 0, 36, 14, 80005, 1, 493, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…", 0, 106401, 0, 39, 14, 80005, 2, 494, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！", 0, 105801, 0, 40, 14, 80005, 3, 495, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと", 0, 105901, 0, 37, 14, 80005, 1, 496, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？", 0, 106001, 0, 41, 14, 80005, 0, 497, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "へ、へぇ…", 0, 106401, 0, 40, 14, 80005, 2, 498, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆", 0, 105801, 0, 41, 14, 80005, 3, 499, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの", 0, 106401, 0, 41, 14, 80005, 2, 500, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！", 0, 106401, 0, 42, 14, 80005, 2, 501, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！", 0, 105801, 0, 42, 15, 80005, 3, 502, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "祝！", 0, 106001, 0, 42, 15, 80005, 1, 503, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "3.5周年！", 0, 105901, 0, 38, 15, 80005, 4, 504, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "おめでとうございます！", 0, 106401, 0, 43, 15, 80005, 0, 505, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！", 0, 190801, 0, 14, 15, 80005, 2, 506, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！", 0, 190801, 0, 15, 15, 80005, 2, 507, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…", 0, 105801, 0, 43, 15, 80005, 3, 508, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆", 0, 105801, 0, 44, 15, 80005, 3, 509, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…", 0, 106001, 0, 43, 15, 80005, 1, 510, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("キャル", "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！", 0, 106001, 0, 44, 15, 80005, 1, 511, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？", 0, 106401, 0, 44, 15, 80005, 0, 512, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("シェフィ", "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ", 0, 106401, 0, 45, 15, 80005, 0, 513, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました", 0, 105901, 0, 39, 15, 80005, 4, 514, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です", 0, 105901, 0, 40, 15, 80005, 4, 515, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("コッコロ", "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね", 0, 105901, 0, 41, 15, 80005, 4, 516, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("ペコリーヌ", "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆", 0, 105801, 0, 45, 15, 80005, 3, 517, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("みんな", "これからも\nよろしくおねがいします！", 0, 105801, 0, 46, 15, 80005, 4, 518, "105901,42,106001,45,106401,46,190801,16", 1);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 0, 1, 0, 60003, 1, 519, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！", 1, 0, 1, 0, 0, 60003, 2, 520, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！", 1, 0, 1, 0, 0, 60003, 2, 521, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！", 1, 0, 1, 0, 0, 60003, 2, 522, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪", 1, 0, 1, 0, 0, 60003, 2, 523, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪", 2, 0, 1, 0, 0, 60003, 2, 524, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？", 1, 0, 2, 0, 0, 60003, 2, 525, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！", 1, 0, 2, 0, 0, 60003, 2, 526, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪", 1, 0, 2, 0, 0, 60003, 2, 527, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～", 1, 0, 2, 0, 0, 60003, 2, 528, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！", 2, 0, 2, 0, 0, 60003, 2, 529, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？", 1, 0, 3, 0, 0, 60003, 2, 530, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？", 1, 0, 3, 0, 0, 60003, 2, 531, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！", 1, 0, 3, 0, 0, 60003, 2, 532, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。", 1, 0, 3, 0, 0, 60003, 2, 533, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　ラッキーアイテムはかわいいもの！　　　　ア、アタシはかわいいじゃなくてキレイだってば！　も～っ！", 1, 0, 3, 0, 0, 60003, 2, 534, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！", 1, 0, 3, 0, 0, 60003, 2, 535, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　悩まず行動すれば強運に恵まれる！　　　　守るよりも攻めるが吉だ！　勝負事をするにはいい運勢だな！", 2, 0, 3, 0, 0, 60003, 2, 536, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！", 1, 0, 4, 0, 0, 60003, 2, 537, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！", 1, 0, 4, 0, 0, 60003, 2, 538, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？", 1, 0, 4, 0, 0, 60003, 2, 539, "", 0);
