INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします", 0, 190804, 0, 3, 7, 60005, 3, 599, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("カリン", "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪", 1, 190804, 0, 1, 0, 60006, 1, 600, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　勇気を持って、新しいことに挑戦しよう！\n幸先がいいねえ少年！　今年もいい一年になるよう祈ってるよ。", 2, 0, 1, 0, 0, 60006, 2, 601, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　どんなピンチも簡単に乗り越えられそう！\nよい結果が出たようで何よりです。さて、待人の方は……ふふ。", 2, 0, 1, 0, 0, 60006, 2, 602, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は姫吉！　あの人と仲直り……できちゃうかも！？\nはあ……この私があなたの運勢に興味を抱くわけないでしょう？", 2, 0, 1, 0, 0, 60006, 2, 603, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　身近な友達との絆が深まる予感……？\n騎士様は、どなたと友誼を結ぶのでしょう？　気になります……", 1, 0, 2, 0, 0, 60006, 2, 604, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は大吉！　ラッキーアイテムは「蛇の抜け殻」！\nこのアタシにお目にかかれるなんて……アンタ、幸せものね♪", 2, 0, 2, 0, 0, 60006, 2, 605, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　気になるあの人に会えちゃうかも！？\n『初夢を正夢にする枕』であなたに会え……な、なんでもない！", 2, 0, 3, 0, 0, 60006, 2, 606, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　子供に戻って、一日中遊びつくそう！\nお年玉でけん玉ゲット！　おじちゃん、もしかめ一万回やって！", 2, 0, 3, 0, 0, 60006, 2, 607, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は中吉！　今日はおうちで、ゆーっくりくつろごう。\nお雑煮よし。みかんよし。さ、こたつで寝正月だ。君も入る？", 1, 0, 3, 0, 0, 60006, 2, 608, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉！　突然のドッキリに注意！\nおや、後ろに髪の長い女の霊が──フフ、冗談です。私ですよ。", 2, 0, 4, 0, 0, 60006, 2, 609, "", 0);
INSERT INTO `v1_b386303912d0832c225fae4287b06575d70e665cd16d74b22c26238f231c8dd7` VALUES ("", "今日の運勢は小吉。慌てなくても、幸運はきっとやってくる。\n運は自分で掴むもの──媛が言ってました。でも、どうやって？", 2, 0, 4, 0, 0, 60006, 2, 610, "", 0);
