INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("次に振るダイスが2個に増える。出目の合計の数だけ進める。", "500マイルを獲得する。", 2, 51001, 2, 0, 1, "ペコリーヌの満腹ツインおにぎり", 1, "ペコリーヌの\n満腹ツインおにぎり", 1, 10, 1, 52001, 0, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 500);
