INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", "なし", 5, 51017, 1, 1, 2, "クウカの緊縛触手活け造り", 1, "クウカの緊縛\n触手活け造り", 3, 10, 2, 52017, 0, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("このターンに振るダイスの目が必ず「3」になる。", "1000マイルを獲得する。", 6, 51018, 20006, 0, 2, "ランファの世話焼き三昧肉じゃが", 1, "ランファの世話焼き三昧肉じゃが", 1, 10, 1, 52018, 0, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("このターンに振るダイスの目が必ず「8」になる。", "なし", 0, 0, 20007, 0, 2, "ワカナの節約腹八分目カレー", 1, "ワカナの節約腹八分目カレー", 1, 0, 1, 52019, 0, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 500);
