INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", "平凡な未鑑定トレジャーを1個獲得する。", 1, 51023, 1, 1, 7, "ミミのコロコロお手玉おにぎり", 1, "ミミのコロコロ\nお手玉おにぎり", 1, 10, 2, 52023, 0, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("RANK2のマイルショップを開く。", "次にダイスを振った際、3マス多く進める。", 3, 51024, 2, 0, 8, "アユミの隠密ガトーインビジブル", 3, "アユミの隠密\nガトーインビジブル", 3, 11, 0, 52024, 0, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", "なし", 0, 51025, 3, 0, 9, "アネモネの花爛漫ステーキサラダ", 2, "アネモネの\n花爛漫ステーキサラダ", 3, 0, 1, 52025, 0, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("ランダムなイベントを発生させる。", "福引券を2枚獲得する。", 8, 51026, 1, 0, 4, "アンナの煉獄蕃茄煮混沌卵", 3, "アンナの\n煉獄蕃茄煮混沌卵", 1, 10, 0, 52026, 0, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", "なし", 0, 51027, 20004, 0, 2, "ユイの栄養満点まごころ弁当", 1, "ユイの\n栄養満点まごころ弁当", 3, 0, 2, 52027, 0, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", "なし", 9, 51028, 30003, 0, 2, "リンのナッツ入りぐうたらあんパン", 1, "リンの\nナッツ入りぐうたらあんパン", 3, 10, 1, 52028, 0, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", "なし", 0, 51029, 30004, 0, 3, "シェフィの氷晶煌めく手作りアイス", 1, "シェフィの\n氷晶煌めく手作りアイス", 2, 0, 1, 52029, 0, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", "なし", 0, 51030, 1, 0, 12, "ミソラの気まぐれ味噌ラーメン★", 3, "ミソラの\n気まぐれ味噌ラーメン★", 1, 0, 3, 52030, 0, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("このターンに振るダイスの目が「1・6・6・6・8・8」になる。", "なし", 0, 51031, 20009, 0, 2, "イロハの最強クワガタゼリー（人間用）", 1, "イロハの\n最強クワガタゼリー（人間用）", 3, 0, 1, 52031, 0, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("料理マスをスキップして移動できる。効果は2ターンの間継続する。", "次にダイスを振った際、3マス多く進める。", 3, 51032, 4, 0, 9, "グレイスの目ん玉飛び出るカップケーキ", 2, "グレイスの\n目ん玉飛び出るカップケーキ", 3, 11, 2, 52032, 0, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("RANK3のマイルショップを開く。", "なし", 0, 51033, 3, 0, 8, "ソノの今日は特別ご褒美オムライス", 3, "ソノの今日は特別ご褒美オムライス", 1, 0, 0, 52033, 0, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "平凡な未鑑定トレジャーを1個獲得する。", 1, 51034, 1, 0, 9, "ジータのグリルド・グラッジチャンク", 2, "ジータの\nグリルド・グラッジチャンク", 1, 10, 2, 52034, 0, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 500);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES ("ランダムなイベントを発生させる。", "貴重な未鑑定トレジャーを1個獲得する。", 10, 51035, 1, 0, 4, "ルイズマリーの成功しすぎてるフライ", 3, "ルイズマリーの\n成功しすぎてるフライ", 1, 10, 0, 52035, 0, "ルイズマリーが祖母のレシピを忠実に再現した\n最高傑作。精緻な調合と温度管理が生み出す衣と\n身のサクフワは錬金技術の到達点と言われている\nが、ただの料理なのではとの意見も絶えない。", 500);
