INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 1, 51001, "500マイルを獲得する。", 52001, 10, 1, 500, 2, 0, "ペコリーヌの満腹ツインおにぎり", 2, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", "ペコリーヌの\n満腹ツインおにぎり", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 2, 51002, "500マイルを獲得する。", 52002, 10, 1, 500, 2, 0, "コッコロのゆるりと休憩サラダ", 30001, 3, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", "コッコロの\nゆるりと休憩サラダ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 1, 51003, "平凡な未鑑定トレジャーを1個獲得する。", 52003, 10, 1, 500, 1, 0, "キャルの絶品五つ星バーガー", 20004, 2, "このターンに振るダイスの目が必ず「5」になる。", "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", "キャルの\n絶品五つ星バーガー", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, 3, 51004, "800マイルを獲得する。", 52004, 10, 1, 500, 4, 0, "リリのコツコツまい進かしわ餅", 20001, 2, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", "リリのコツコツ\nまい進かしわ餅", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 3, 51005, "次にダイスを振った際、1マス多く進める。", 52005, 11, 3, 500, 1, 0, "クリアの彩り華やかバラエティ弁当", 1, 4, "ランダムなイベントを発生させる。", "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", "クリアの彩り華やか\nバラエティ弁当", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 1, 51006, "800マイルを獲得する。", 52006, 10, 1, 500, 4, 0, "プレシアの食いしん坊ぶーぶー肉まん", 20002, 2, "このターンに振るダイスの目が「4・5・6」のみになる。", "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", "プレシアの食いしん坊\nぶーぶー肉まん", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 1, 51007, "福引券を1枚獲得する。", 52007, 10, 1, 500, 3, 0, "カヤとイノリの食欲倍増ケバブサンド", 1, 7, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", "カヤとイノリの\n食欲倍増ケバブサンド", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 2, 51008, "500マイルを獲得する。", 52008, 10, 2, 500, 2, 0, "タマキの金運招くレインボーたい焼き", 200, 5, "次にマイルマスに止まった際の獲得マイルが200%アップする。", "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", "タマキの金運招く\nレインボーたい焼き", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 2, 51009, "平凡な未鑑定トレジャーを1個獲得する。", 52009, 10, 1, 500, 1, 1, "クローチェのフルパワーポテトサラダ", 1, 7, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", "クローチェの\nフルパワーポテトサラダ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 3, 51010, "500マイルを獲得する。", 52010, 10, 2, 500, 2, 0, "アヤネとクルミの青空ピクニックセット", 1, 6, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", "アヤネとクルミの\n青空ピクニックセット", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 3, 51011, "福引券を1枚獲得する。", 52011, 10, 1, 500, 3, 0, "リマの不思議なスイートアップル", 1, 7, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", "リマの不思議な\nスイートアップル", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 2, 51012, "平凡な未鑑定トレジャーを1個獲得する。", 52012, 10, 1, 500, 1, 0, "ニノンのオリジナル一二三寿司", 30002, 3, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", "ニノンのオリジナル\n一二三寿司", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 3, 51013, "800マイルを獲得する。", 52013, 10, 3, 500, 4, 0, "クレジッタの開運お菓子盛り合わせ", 2, 8, "RANK2のマイルショップを開く。", "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", "クレジッタの開運\nお菓子盛り合わせ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 2, 51014, "次にダイスを振った際、1マス多く進める。", 52014, 11, 1, 500, 1, 0, "リノの「時は金時」かき氷", 1, 9, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの\n「時は金時」かき氷", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 1, 51015, "次にダイスを振った際、1マス多く進める。", 52015, 11, 1, 500, 1, 0, "シズルの愛情盛り盛りハンバーグ", 2, 1, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", "シズルの愛情\n盛り盛りハンバーグ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 3, 51016, "なし", 52016, 0, 1, 500, 0, 0, "アオイの疾走サンドイッチ", 20005, 2, "このターンに振るダイスの目が必ず「6」になる。", "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", "アオイの疾走\nサンドイッチ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 3, 51017, "なし", 52017, 10, 1, 500, 5, 1, "クウカの緊縛触手活け造り", 1, 2, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", "クウカの緊縛\n触手活け造り", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 1, 51018, "1000マイルを獲得する。", 52018, 10, 1, 500, 6, 0, "ランファの世話焼き三昧肉じゃが", 20006, 2, "このターンに振るダイスの目が必ず「3」になる。", "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", "ランファの世話焼き三昧肉じゃが", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 1, 0, "なし", 52019, 0, 1, 500, 0, 0, "ワカナの節約腹八分目カレー", 20007, 2, "このターンに振るダイスの目が必ず「8」になる。", "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", "ワカナの節約腹八分目カレー", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 2, 51014, "次にダイスを振った際、1マス多く進める。", 52020, 11, 2, 500, 1, 0, "リノの「時は金時」かき氷", 1, 9, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの\n「時は金時」かき氷", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, 3, 51021, "	平凡な未鑑定トレジャーを2個獲得する。", 52021, 10, 2, 500, 7, 0, "カスミの真実見抜くボンボン・ショコラ", 2, 9, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", "カスミの真実見抜く\nボンボン・ショコラ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, 1, 51022, "なし", 52022, 0, 1, 500, 0, 0, "アキノのゲヘニアンチキンプレート", 20008, 2, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", "アキノのゲヘニアン\nチキンプレート", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 1, 51023, "平凡な未鑑定トレジャーを1個獲得する。", 52023, 10, 1, 500, 1, 1, "ミミのコロコロお手玉おにぎり", 1, 7, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", "ミミのコロコロ\nお手玉おにぎり", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 3, 51024, "次にダイスを振った際、3マス多く進める。", 52024, 11, 3, 500, 3, 0, "アユミの隠密ガトーインビジブル", 2, 8, "RANK2のマイルショップを開く。", "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", "アユミの隠密\nガトーインビジブル", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 3, 51025, "なし", 52025, 0, 2, 500, 0, 0, "アネモネの花爛漫ステーキサラダ", 3, 9, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", "アネモネの\n花爛漫ステーキサラダ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 1, 51026, "福引券を2枚獲得する。", 52026, 10, 3, 500, 8, 0, "アンナの煉獄蕃茄煮混沌卵", 1, 4, "ランダムなイベントを発生させる。", "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", "アンナの\n煉獄蕃茄煮混沌卵", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 3, 51027, "なし", 52027, 0, 1, 500, 0, 0, "ユイの栄養満点まごころ弁当", 20004, 2, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", "ユイの\n栄養満点まごころ弁当", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 3, 51028, "なし", 52028, 10, 1, 500, 9, 0, "リンのナッツ入りぐうたらあんパン", 30003, 2, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", "リンの\nナッツ入りぐうたらあんパン", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 2, 51029, "なし", 52029, 0, 1, 500, 0, 0, "シェフィの氷晶煌めく手作りアイス", 30004, 3, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", "シェフィの\n氷晶煌めく手作りアイス", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, 1, 51030, "なし", 52030, 0, 3, 500, 0, 0, "ミソラの気まぐれ味噌ラーメン★", 1, 12, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", "ミソラの\n気まぐれ味噌ラーメン★", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 3, 51031, "なし", 52031, 0, 1, 500, 0, 0, "イロハの最強クワガタゼリー（人間用）", 20009, 2, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", "イロハの\n最強クワガタゼリー（人間用）", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 3, 51032, "次にダイスを振った際、3マス多く進める。", 52032, 11, 2, 500, 3, 0, "グレイスの目ん玉飛び出るカップケーキ", 4, 9, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", "グレイスの\n目ん玉飛び出るカップケーキ", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 1, 51033, "なし", 52033, 0, 3, 500, 0, 0, "ソノの今日は特別ご褒美オムライス", 3, 8, "RANK3のマイルショップを開く。", "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", "ソノの今日は特別ご褒美オムライス", 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 1, 51034, "平凡な未鑑定トレジャーを1個獲得する。", 52034, 10, 2, 500, 1, 0, "ジータのグリルド・グラッジチャンク", 1, 9, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", "ジータの\nグリルド・グラッジチャンク", 0);
