INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 500, 1, 1, 0, 52001, 2, "ペコリーヌの満腹ツインおにぎり", 51001, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 0, "ペコリーヌの\n満腹ツインおにぎり", 1, 10, "500マイルを獲得する。", 1, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (30001, 500, 3, 2, 0, 52002, 2, "コッコロのゆるりと休憩サラダ", 51002, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 0, "コッコロの\nゆるりと休憩サラダ", 1, 10, "500マイルを獲得する。", 1, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20004, 500, 2, 1, 0, 52003, 1, "キャルの絶品五つ星バーガー", 51003, "このターンに振るダイスの目が必ず「5」になる。", 0, "キャルの\n絶品五つ星バーガー", 1, 10, "平凡な未鑑定トレジャーを1個獲得する。", 1, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20001, 500, 2, 3, 0, 52004, 4, "リリのコツコツまい進かしわ餅", 51004, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 0, "リリのコツコツ\nまい進かしわ餅", 3, 10, "800マイルを獲得する。", 1, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 4, 3, 0, 52005, 1, "クリアの彩り華やかバラエティ弁当", 51005, "ランダムなイベントを発生させる。", 0, "クリアの彩り華やか\nバラエティ弁当", 0, 11, "次にダイスを振った際、1マス多く進める。", 3, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20002, 500, 2, 1, 0, 52006, 4, "プレシアの食いしん坊ぶーぶー肉まん", 51006, "このターンに振るダイスの目が「4・5・6」のみになる。", 0, "プレシアの食いしん坊\nぶーぶー肉まん", 1, 10, "800マイルを獲得する。", 1, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 7, 1, 0, 52007, 3, "カヤとイノリの食欲倍増ケバブサンド", 51007, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", 0, "カヤとイノリの\n食欲倍増ケバブサンド", 2, 10, "福引券を1枚獲得する。", 1, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (200, 500, 5, 2, 0, 52008, 2, "タマキの金運招くレインボーたい焼き", 51008, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 1, "タマキの金運招く\nレインボーたい焼き", 0, 10, "500マイルを獲得する。", 2, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 7, 2, 1, 52009, 1, "クローチェのフルパワーポテトサラダ", 51009, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", 0, "クローチェの\nフルパワーポテトサラダ", 2, 10, "平凡な未鑑定トレジャーを1個獲得する。", 1, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 6, 3, 0, 52010, 2, "アヤネとクルミの青空ピクニックセット", 51010, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 2, "アヤネとクルミの\n青空ピクニックセット", 0, 10, "500マイルを獲得する。", 2, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 7, 3, 0, 52011, 3, "リマの不思議なスイートアップル", 51011, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", 0, "リマの不思議な\nスイートアップル", 2, 10, "福引券を1枚獲得する。", 1, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (30002, 500, 3, 2, 0, 52012, 1, "ニノンのオリジナル一二三寿司", 51012, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 0, "ニノンのオリジナル\n一二三寿司", 1, 10, "平凡な未鑑定トレジャーを1個獲得する。", 1, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 500, 8, 3, 0, 52013, 4, "クレジッタの開運お菓子盛り合わせ", 51013, "RANK2のマイルショップを開く。", 0, "クレジッタの開運\nお菓子盛り合わせ", 0, 10, "800マイルを獲得する。", 3, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 9, 2, 0, 52014, 1, "リノの「時は金時」かき氷", 51014, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 0, "リノの\n「時は金時」かき氷", 2, 11, "次にダイスを振った際、1マス多く進める。", 1, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 500, 1, 1, 0, 52015, 1, "シズルの愛情盛り盛りハンバーグ", 51015, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 0, "シズルの愛情\n盛り盛りハンバーグ", 1, 11, "次にダイスを振った際、1マス多く進める。", 1, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20005, 500, 2, 3, 0, 52016, 0, "アオイの疾走サンドイッチ", 51016, "このターンに振るダイスの目が必ず「6」になる。", 0, "アオイの疾走\nサンドイッチ", 1, 0, "なし", 1, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 2, 3, 1, 52017, 5, "クウカの緊縛触手活け造り", 51017, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 0, "クウカの緊縛\n触手活け造り", 2, 10, "なし", 1, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20006, 500, 2, 1, 0, 52018, 6, "ランファの世話焼き三昧肉じゃが", 51018, "このターンに振るダイスの目が必ず「3」になる。", 0, "ランファの世話焼き三昧肉じゃが", 1, 10, "1000マイルを獲得する。", 1, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20007, 500, 2, 1, 0, 52019, 0, "ワカナの節約腹八分目カレー", 0, "このターンに振るダイスの目が必ず「8」になる。", 0, "ワカナの節約腹八分目カレー", 1, 0, "なし", 1, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 9, 2, 0, 52020, 1, "リノの「時は金時」かき氷", 51014, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 0, "リノの\n「時は金時」かき氷", 2, 11, "次にダイスを振った際、1マス多く進める。", 2, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 500, 9, 3, 0, 52021, 7, "カスミの真実見抜くボンボン・ショコラ", 51021, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 0, "カスミの真実見抜く\nボンボン・ショコラ", 3, 10, "	平凡な未鑑定トレジャーを2個獲得する。", 2, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20008, 500, 2, 1, 0, 52022, 0, "アキノのゲヘニアンチキンプレート", 51022, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 0, "アキノのゲヘニアン\nチキンプレート", 3, 0, "なし", 1, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 7, 1, 1, 52023, 1, "ミミのコロコロお手玉おにぎり", 51023, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 0, "ミミのコロコロ\nお手玉おにぎり", 2, 10, "平凡な未鑑定トレジャーを1個獲得する。", 1, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, 500, 8, 3, 0, 52024, 3, "アユミの隠密ガトーインビジブル", 51024, "RANK2のマイルショップを開く。", 0, "アユミの隠密\nガトーインビジブル", 0, 11, "次にダイスを振った際、3マス多く進める。", 3, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, 500, 9, 3, 0, 52025, 0, "アネモネの花爛漫ステーキサラダ", 51025, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 0, "アネモネの\n花爛漫ステーキサラダ", 1, 0, "なし", 2, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 4, 1, 0, 52026, 8, "アンナの煉獄蕃茄煮混沌卵", 51026, "ランダムなイベントを発生させる。", 0, "アンナの\n煉獄蕃茄煮混沌卵", 0, 10, "福引券を2枚獲得する。", 3, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20004, 500, 2, 3, 0, 52027, 0, "ユイの栄養満点まごころ弁当", 51027, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 0, "ユイの\n栄養満点まごころ弁当", 2, 0, "なし", 1, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (30003, 500, 2, 3, 0, 52028, 9, "リンのナッツ入りぐうたらあんパン", 51028, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 0, "リンの\nナッツ入りぐうたらあんパン", 1, 10, "なし", 1, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (30004, 500, 3, 2, 0, 52029, 0, "シェフィの氷晶煌めく手作りアイス", 51029, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 0, "シェフィの\n氷晶煌めく手作りアイス", 1, 0, "なし", 1, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, 500, 12, 1, 0, 52030, 0, "ミソラの気まぐれ味噌ラーメン★", 51030, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 0, "ミソラの\n気まぐれ味噌ラーメン★", 3, 0, "なし", 3, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (20009, 500, 2, 3, 0, 52031, 0, "イロハの最強クワガタゼリー（人間用）", 51031, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 0, "イロハの\n最強クワガタゼリー（人間用）", 1, 0, "なし", 1, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (4, 500, 9, 3, 0, 52032, 3, "グレイスの目ん玉飛び出るカップケーキ", 51032, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 0, "グレイスの\n目ん玉飛び出るカップケーキ", 2, 11, "次にダイスを振った際、3マス多く進める。", 2, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。");
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, 500, 8, 1, 0, 52033, 0, "ソノの今日は特別ご褒美オムライス", 51033, "RANK3のマイルショップを開く。", 0, "ソノの今日は特別ご褒美オムライス", 0, 0, "なし", 3, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。");
