INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ペコリーヌの\n満腹ツインおにぎり", 0, "ペコリーヌの満腹ツインおにぎり", 51001, "500マイルを獲得する。", 2, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 2, 500, 52001, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", 0, 1, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "コッコロの\nゆるりと休憩サラダ", 0, "コッコロのゆるりと休憩サラダ", 51002, "500マイルを獲得する。", 30001, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 1, 2, 500, 52002, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", 0, 3, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "キャルの\n絶品五つ星バーガー", 0, "キャルの絶品五つ星バーガー", 51003, "平凡な未鑑定トレジャーを1個獲得する。", 20004, "このターンに振るダイスの目が必ず「5」になる。", 1, 1, 500, 52003, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", 0, 2, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "リリのコツコツ\nまい進かしわ餅", 0, "リリのコツコツまい進かしわ餅", 51004, "800マイルを獲得する。", 20001, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 1, 4, 500, 52004, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", 0, 2, 3, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "クリアの彩り華やか\nバラエティ弁当", 0, "クリアの彩り華やかバラエティ弁当", 51005, "次にダイスを振った際、1マス多く進める。", 1, "ランダムなイベントを発生させる。", 3, 1, 500, 52005, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", 0, 4, 0, 11);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "プレシアの食いしん坊\nぶーぶー肉まん", 0, "プレシアの食いしん坊ぶーぶー肉まん", 51006, "800マイルを獲得する。", 20002, "このターンに振るダイスの目が「4・5・6」のみになる。", 1, 4, 500, 52006, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", 0, 2, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "カヤとイノリの\n食欲倍増ケバブサンド", 0, "カヤとイノリの食欲倍増ケバブサンド", 51007, "福引券を1枚獲得する。", 1, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", 1, 3, 500, 52007, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", 0, 7, 2, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "タマキの金運招く\nレインボーたい焼き", 0, "タマキの金運招くレインボーたい焼き", 51008, "500マイルを獲得する。", 200, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 2, 2, 500, 52008, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", 1, 5, 0, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "クローチェの\nフルパワーポテトサラダ", 1, "クローチェのフルパワーポテトサラダ", 51009, "平凡な未鑑定トレジャーを1個獲得する。", 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", 1, 1, 500, 52009, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", 0, 7, 2, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "アヤネとクルミの\n青空ピクニックセット", 0, "アヤネとクルミの青空ピクニックセット", 51010, "500マイルを獲得する。", 1, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 2, 2, 500, 52010, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", 2, 6, 0, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "リマの不思議な\nスイートアップル", 0, "リマの不思議なスイートアップル", 51011, "福引券を1枚獲得する。", 1, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", 1, 3, 500, 52011, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", 0, 7, 2, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "ニノンのオリジナル\n一二三寿司", 0, "ニノンのオリジナル一二三寿司", 51012, "平凡な未鑑定トレジャーを1個獲得する。", 30002, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 1, 1, 500, 52012, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", 0, 3, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "クレジッタの開運\nお菓子盛り合わせ", 0, "クレジッタの開運お菓子盛り合わせ", 51013, "800マイルを獲得する。", 2, "RANK2のマイルショップを開く。", 3, 4, 500, 52013, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", 0, 8, 0, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "リノの\n「時は金時」かき氷", 0, "リノの「時は金時」かき氷", 51014, "次にダイスを振った際、1マス多く進める。", 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 1, 1, 500, 52014, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 0, 9, 2, 11);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "シズルの愛情\n盛り盛りハンバーグ", 0, "シズルの愛情盛り盛りハンバーグ", 51015, "次にダイスを振った際、1マス多く進める。", 2, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 1, 500, 52015, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", 0, 1, 1, 11);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "アオイの疾走\nサンドイッチ", 0, "アオイの疾走サンドイッチ", 51016, "なし", 20005, "このターンに振るダイスの目が必ず「6」になる。", 1, 0, 500, 52016, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", 0, 2, 1, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "クウカの緊縛\n触手活け造り", 1, "クウカの緊縛触手活け造り", 51017, "なし", 1, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 1, 5, 500, 52017, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", 0, 2, 2, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ランファの世話焼き三昧肉じゃが", 0, "ランファの世話焼き三昧肉じゃが", 51018, "1000マイルを獲得する。", 20006, "このターンに振るダイスの目が必ず「3」になる。", 1, 6, 500, 52018, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", 0, 2, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ワカナの節約腹八分目カレー", 0, "ワカナの節約腹八分目カレー", 0, "なし", 20007, "このターンに振るダイスの目が必ず「8」になる。", 1, 0, 500, 52019, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", 0, 2, 1, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "リノの\n「時は金時」かき氷", 0, "リノの「時は金時」かき氷", 51014, "次にダイスを振った際、1マス多く進める。", 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 1, 500, 52020, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", 0, 9, 2, 11);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "カスミの真実見抜く\nボンボン・ショコラ", 0, "カスミの真実見抜くボンボン・ショコラ", 51021, "	平凡な未鑑定トレジャーを2個獲得する。", 2, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 2, 7, 500, 52021, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", 0, 9, 3, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "アキノのゲヘニアン\nチキンプレート", 0, "アキノのゲヘニアンチキンプレート", 51022, "なし", 20008, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 1, 0, 500, 52022, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", 0, 2, 3, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ミミのコロコロ\nお手玉おにぎり", 1, "ミミのコロコロお手玉おにぎり", 51023, "平凡な未鑑定トレジャーを1個獲得する。", 1, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 1, 1, 500, 52023, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", 0, 7, 2, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "アユミの隠密\nガトーインビジブル", 0, "アユミの隠密ガトーインビジブル", 51024, "次にダイスを振った際、3マス多く進める。", 2, "RANK2のマイルショップを開く。", 3, 3, 500, 52024, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", 0, 8, 0, 11);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "アネモネの\n花爛漫ステーキサラダ", 0, "アネモネの花爛漫ステーキサラダ", 51025, "なし", 3, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 2, 0, 500, 52025, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", 0, 9, 1, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "アンナの\n煉獄蕃茄煮混沌卵", 0, "アンナの煉獄蕃茄煮混沌卵", 51026, "福引券を2枚獲得する。", 1, "ランダムなイベントを発生させる。", 3, 8, 500, 52026, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", 0, 4, 0, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "ユイの\n栄養満点まごころ弁当", 0, "ユイの栄養満点まごころ弁当", 51027, "なし", 20004, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 1, 0, 500, 52027, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", 0, 2, 2, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "リンの\nナッツ入りぐうたらあんパン", 0, "リンのナッツ入りぐうたらあんパン", 51028, "なし", 30003, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 1, 9, 500, 52028, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", 0, 2, 1, 10);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (2, "シェフィの\n氷晶煌めく手作りアイス", 0, "シェフィの氷晶煌めく手作りアイス", 51029, "なし", 30004, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 1, 0, 500, 52029, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", 0, 3, 1, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ミソラの\n気まぐれ味噌ラーメン★", 0, "ミソラの気まぐれ味噌ラーメン★", 51030, "なし", 1, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 3, 0, 500, 52030, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", 0, 12, 3, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "イロハの\n最強クワガタゼリー（人間用）", 0, "イロハの最強クワガタゼリー（人間用）", 51031, "なし", 20009, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 1, 0, 500, 52031, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", 0, 2, 1, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (3, "グレイスの\n目ん玉飛び出るカップケーキ", 0, "グレイスの目ん玉飛び出るカップケーキ", 51032, "次にダイスを振った際、3マス多く進める。", 4, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 2, 3, 500, 52032, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", 0, 9, 2, 11);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ソノの今日は特別ご褒美オムライス", 0, "ソノの今日は特別ご褒美オムライス", 51033, "なし", 3, "RANK3のマイルショップを開く。", 3, 0, 500, 52033, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", 0, 8, 0, 0);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (1, "ジータの\nグリルド・グラッジチャンク", 0, "ジータのグリルド・グラッジチャンク", 51034, "平凡な未鑑定トレジャーを1個獲得する。", 1, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 1, 500, 52034, "そのあまりの美味さと、ほとばしるカロリーから\n悪意の塊、という物騒な名が付けられた肉料理。\nこの肉を集められるかが騎空士としての力量を\n示すため、ジータは大量に集めたがっている。", 0, 9, 2, 10);
