INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51001, 0, 52001, "ペコリーヌの満腹ツインおにぎり", "500マイルを獲得する。", 2, 1, 0, 500, 10, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 2, 1, "ペコリーヌが丹精込めて握った特大おにぎり。\nごはんは命のエネルギーを体現しているらしい。\n普通の人が満腹に感じる量の倍のサイズの中に\n最高級の具を閉じ込めた【美食殿】らしい一品。", "ペコリーヌの\n満腹ツインおにぎり", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51002, 0, 52002, "コッコロのゆるりと休憩サラダ", "500マイルを獲得する。", 2, 3, 0, 500, 10, "このターンに振るダイスの目が必ず「1」になる。さらに、次のターン数のカウントをスキップする。", 1, 30001, 2, "コッコロが皆のことを思って用意したサラダ。\n栄養バランスも考えられたうえに腹持ちもよく\n味だけではなく見た目にも気を配られている。\n冒険への活力を与えてくれる慈愛溢れる一品。", "コッコロの\nゆるりと休憩サラダ", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51003, 0, 52003, "キャルの絶品五つ星バーガー", "平凡な未鑑定トレジャーを1個獲得する。", 1, 2, 0, 500, 10, "このターンに振るダイスの目が必ず「5」になる。", 1, 20004, 1, "キャルが食べたい物を挟み込んだハンバーガー。\n香ばしいパンとお肉の香りが食欲を刺激する。\n魔物でない素材で作られた、冒険中に食べるには\nゴージャスすぎる一品、とキャルは信じている。", "キャルの\n絶品五つ星バーガー", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51004, 0, 52004, "リリのコツコツまい進かしわ餅", "800マイルを獲得する。", 4, 2, 0, 500, 10, "ダイスを3回振るまでの間、ダイスの目が必ず「4」になる。", 3, 20001, 3, "リリが作った柏餅……の中でも、上手く作れた\n選りすぐりの品。最高の素材を使用した上、\n失敗した分はリリが自分で食べてしまったため\n単価でみるとかなりの高級品となっている。", "リリのコツコツ\nまい進かしわ餅", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51005, 0, 52005, "クリアの彩り華やかバラエティ弁当", "次にダイスを振った際、1マス多く進める。", 1, 4, 0, 500, 11, "ランダムなイベントを発生させる。", 0, 1, 3, "クリアが朝早くから用意した冒険用のお弁当。\nおかずは様々でどれも絶品だが、どんなものが\n入っているかはみんなにも秘密にされているため\n蓋を開けるまでのお楽しみとなっている。", "クリアの彩り華やか\nバラエティ弁当", 3);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51006, 0, 52006, "プレシアの食いしん坊ぶーぶー肉まん", "800マイルを獲得する。", 4, 2, 0, 500, 10, "このターンに振るダイスの目が「4・5・6」のみになる。", 1, 20002, 1, "プレシアが選び抜いた具を詰め込んだ肉まん。\n彼女の希望で、通常の肉まんの数倍のサイズに\nなっている。味もボリュームも満点でハッピー\n間違いなしですよ♪とは調理担当クリアの談。", "プレシアの食いしん坊\nぶーぶー肉まん", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51007, 0, 52007, "カヤとイノリの食欲倍増ケバブサンド", "福引券を1枚獲得する。", 3, 7, 0, 500, 10, "このターンに振るダイスの目を必ず「3」に、次のターンに振るダイスの目を必ず「6」にする。", 2, 1, 1, "カヤとイノリが弁当代わりに持ってきたケバブ。\nイノリ行きつけの店の硬派な味わいで、ギャング\nとしてのやる気が一人3倍、コンビで6倍となり\n下剋上を成し遂げるパワーが湧く……らしい。", "カヤとイノリの\n食欲倍増ケバブサンド", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51008, 0, 52008, "タマキの金運招くレインボーたい焼き", "500マイルを獲得する。", 2, 5, 1, 500, 10, "次にマイルマスに止まった際の獲得マイルが200%アップする。", 0, 200, 2, "タマキたちが『ジオ・テオゴニア』に持ち込んだ\nたい焼きの概念が現地の文化と溶け合い生まれた\n魔改造スイーツ。虹色に輝くその甘さは、実に\nランドソルのたい焼きの数倍と言われている。", "タマキの金運招く\nレインボーたい焼き", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51009, 1, 52009, "クローチェのフルパワーポテトサラダ", "平凡な未鑑定トレジャーを1個獲得する。", 1, 7, 0, 500, 10, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「8」に、次のターンに振るダイスの目を必ず「1」にする。", 2, 1, 2, "クローチェがフルパワーで作ったポテトサラダ。\n本来はガレットを作ろうとしたが、うっかり\n握り潰してしまったため、軌道修正して仕上げ\nた一品。経緯はともかく味は非常に美味である。", "クローチェの\nフルパワーポテトサラダ", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51010, 0, 52010, "アヤネとクルミの青空ピクニックセット", "500マイルを獲得する。", 2, 6, 2, 500, 10, "マップ上の「マイルマス」「トレジャーマス」 「ショップマス」のRANKが1つ上がる。効果は対象のマスに計2回止まるまで継続する。", 0, 1, 3, "アヤネとクルミが二人で用意した特別なランチ\nバスケット。仲間との冒険への希望が詰まった\nご機嫌な料理の数々で、道中の様々な出来事が\n１ランク上のイベントに感じられるようになる。", "アヤネとクルミの\n青空ピクニックセット", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51011, 0, 52011, "リマの不思議なスイートアップル", "福引券を1枚獲得する。", 3, 7, 0, 500, 10, "このターンに振るダイスの目を必ず「2」に、次のターンに振るダイスの目を必ず「7」にする。", 2, 1, 3, "リマがネネカにもらったリンゴで作った秘密の\nスイーツ。爽やかな酸味と濃厚な甘さがまるで\n魔法のようなバランスでお互いを引きたてており\n食べた者をたちまち虜にしてしまうという。", "リマの不思議な\nスイートアップル", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51012, 0, 52012, "ニノンのオリジナル一二三寿司", "平凡な未鑑定トレジャーを1個獲得する。", 1, 3, 0, 500, 10, "このターンに振るダイスの目が「1・2・3」のみになる。さらに、次のターン数のカウントをスキップする。", 1, 30002, 2, "ニノンがジオ・テオゴニアに持ち込んだ特別な\n寿司。厳選された新鮮な魚介を使った逸品だが、\n一部のネタは、寿司のセオリーを逸脱したニノン\n我流のものに仕上がっている。", "ニノンのオリジナル\n一二三寿司", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51013, 0, 52013, "クレジッタの開運お菓子盛り合わせ", "800マイルを獲得する。", 4, 8, 0, 500, 10, "RANK2のマイルショップを開く。", 0, 2, 3, "クレジッタが考案した、ジオ・テオゴニアで流通\nしているチョコの貨幣の盛り合わせ。一見すると\n金貨を口に入れるおバカさんではあるが、通貨を\n食べるという背徳感が隠し味となっている。", "クレジッタの開運\nお菓子盛り合わせ", 3);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51014, 0, 52014, "リノの「時は金時」かき氷", "次にダイスを振った際、1マス多く進める。", 1, 9, 0, 500, 11, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 1, 2, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの\n「時は金時」かき氷", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51015, 0, 52015, "シズルの愛情盛り盛りハンバーグ", "次にダイスを振った際、1マス多く進める。", 1, 1, 0, 500, 11, "次に振るダイスが2個に増える。出目の合計の数だけ進める。", 1, 2, 1, "お姉ちゃんが弟くんのために心を込めて作った\nスペシャルなハンバーグ。アツアツの肉汁と共に\n溢れんばかりの愛が詰まっており、一口食べる\nだけで、走り回りたくなるような美味しさ。", "シズルの愛情\n盛り盛りハンバーグ", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51016, 0, 52016, "アオイの疾走サンドイッチ", "なし", 0, 2, 0, 500, 0, "このターンに振るダイスの目が必ず「6」になる。", 1, 20005, 3, "アオイがお弁当として作ったエルフ伝統の野菜\nサンド。一緒についてきてくれる最高の友だち\nの助言により、どんなに深い森の中でも全速力\nで駆け抜けることができるらしい。（アオイ談）", "アオイの疾走\nサンドイッチ", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51017, 1, 52017, "クウカの緊縛触手活け造り", "なし", 5, 2, 0, 500, 10, "[FF7C4E,D62146]【上書き不可】[-]次のターン「移動系カテゴリーの料理」が食べられない代わりに、その場で2000マイルを獲得する。", 2, 1, 3, "数多くのドM妄想をしてきたクウカの中で最近の\nマイブームである触手をモチーフとした活造り。\n新鮮な蛸足が、濃厚なうま味と共に物理的に絡み\n付き身も心も縛りあげられる様な体験ができる。", "クウカの緊縛\n触手活け造り", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51018, 0, 52018, "ランファの世話焼き三昧肉じゃが", "1000マイルを獲得する。", 6, 2, 0, 500, 10, "このターンに振るダイスの目が必ず「3」になる。", 1, 20006, 1, "コッコロの指導により料理の腕を上げたランファ\nが手作りした肉じゃが。まだ改善の余地はある\nものの、不器用なランファが誰かのために積み\n重ねた努力を思うととてもおいしく味わえる。", "ランファの世話焼き三昧肉じゃが", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (0, 0, 52019, "ワカナの節約腹八分目カレー", "なし", 0, 2, 0, 500, 0, "このターンに振るダイスの目が必ず「8」になる。", 1, 20007, 1, "バンディ・シスターズの昨夜の夕食カレー。作り\n過ぎて食べきれずにいたものをワカナがお裾分け\nしてくれた。寝かされていたためしっかりと味の\n染みた、極上の二日目カレーとなっている。", "ワカナの節約腹八分目カレー", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51014, 0, 52020, "リノの「時は金時」かき氷", "次にダイスを振った際、1マス多く進める。", 1, 9, 0, 500, 11, "マイルマスをスキップして移動できる。効果は2ターンの間継続する。", 2, 1, 2, "可愛い妹がお兄ちゃんのために心を込めて作った\nスペシャルなかき氷。一口食べるとたちどころに\nクールな甘さが精神を研ぎ澄まし、いかなる\nお得をも見逃さない要塞賢母と化すという。", "リノの\n「時は金時」かき氷", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51021, 0, 52021, "カスミの真実見抜くボンボン・ショコラ", "	平凡な未鑑定トレジャーを2個獲得する。", 7, 9, 0, 500, 10, "トレジャーマスをスキップして移動できる。効果は3ターンの間継続する。", 3, 2, 3, "綺麗な装飾箱に納められたボンボン・ショコラ。\nそのほろ苦さを秘めた濃密な甘さが灰色の脳細胞\nに働きかける時、名探偵の鋭い推理が隠された\n謎を全て白日の下へと晒し、事件を解決へ導く。", "カスミの真実見抜く\nボンボン・ショコラ", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51022, 0, 52022, "アキノのゲヘニアンチキンプレート", "なし", 0, 2, 0, 500, 0, "ダイスを3回振るまでの間、「5と6」の⽬が出る確率が3倍になる。", 3, 20008, 1, "アキノがジオ・ゲヘナで仕入れてきたスパイスを\n使用した、お洒落で豪華なチキンプレート。牧場\n特製ヨーグルトソースと奏でる刺激的な味わいは\nまさに二つの世界の奇跡のマリアージュである。", "アキノのゲヘニアン\nチキンプレート", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51023, 1, 52023, "ミミのコロコロお手玉おにぎり", "平凡な未鑑定トレジャーを1個獲得する。", 1, 7, 0, 500, 10, "[FF7C4E,D62146]【上書き不可】[-]このターンに振るダイスの目を必ず「6」に、次のターンに振るダイスの目を必ず「3」にする。", 2, 1, 1, "ミミが、ジオ・ゲヘナで助けてくれたお姉さん達\nのことを思い作ったおにぎり。それぞれ異なる\n味付けがされているが、ミミの願い通りお互いに\nケンカをせず仲良くハーモニーを奏でている。", "ミミのコロコロ\nお手玉おにぎり", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51024, 0, 52024, "アユミの隠密ガトーインビジブル", "次にダイスを振った際、3マス多く進める。", 3, 8, 0, 500, 11, "RANK2のマイルショップを開く。", 0, 2, 3, "気が付くとリリの手元にあった不思議なお菓子。\n一見普通のケーキだが、内部に様々な具材が\n隠れるように入っている。複雑で重層的な\n味わいを見せる様は、まるで誰かさんのよう。", "アユミの隠密\nガトーインビジブル", 3);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51025, 0, 52025, "アネモネの花爛漫ステーキサラダ", "なし", 0, 9, 0, 500, 0, "「マイルマス」「トレジャーマス」をスキップして移動できる。効果は1ターンの間継続する。", 1, 3, 3, "アネモネが世話する秘密の菜園の野菜を使った\nサラダ。栄養を重視するアネモネは草食獣の肉を\n野菜と解釈、当初は野菜の上に肉を置いただけ\nであったが、ネラの指導で盛り付け直された。", "アネモネの\n花爛漫ステーキサラダ", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51026, 0, 52026, "アンナの煉獄蕃茄煮混沌卵", "福引券を2枚獲得する。", 8, 4, 0, 500, 10, "ランダムなイベントを発生させる。", 0, 1, 1, "ジオ・ゲヘナを探索中の冥姫たちが現地で採取\nしたスパイスで味付けした一皿。赤く煮え滾る\nソースは刺激的かつ非常に奥深い味わいで未知の\n大地の如く冒険者たちをその深奥へと誘う。", "アンナの\n煉獄蕃茄煮混沌卵", 3);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51027, 0, 52027, "ユイの栄養満点まごころ弁当", "なし", 0, 2, 0, 500, 0, "ダイスを2回振るまでの間、ダイスの目が必ず「5」になる。", 2, 20004, 3, "ユイが食べる人のことを考え、心を込めて作った\nお弁当。程よいサイズのお弁当箱に愛情と手間暇\nをたっぷりとかけた極上の料理が詰まっており、\n食べた者の心と胃袋を優しく満たしてくれる。", "ユイの\n栄養満点まごころ弁当", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51028, 0, 52028, "リンのナッツ入りぐうたらあんパン", "なし", 9, 2, 0, 500, 10, "このターンに振るダイスの目が「1・2」のみに\nなる代わりに、その場で福引券を3枚獲得する。", 1, 30003, 3, "リンからもらった非常食のあんパン。ナッツが\n練り込まれクランチな食感で食べ飽きない。\nゲームのため部屋に引きこもる際のお供であり、\n必要な栄養素が全て賄える完全栄養食、らしい。", "リンの\nナッツ入りぐうたらあんパン", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51029, 0, 52029, "シェフィの氷晶煌めく手作りアイス", "なし", 0, 3, 0, 500, 0, "このターンに振るダイスの目が必ず「2」になる。さらに、次のターン数のカウントをスキップする。", 1, 30004, 2, "シェフィが自身の魔力の氷を使い作ったアイス。\nペコリーヌにねだられて作ったものだがキャル達\nにも好評である。コッコロ曰くひんやりしている\nのに食べると心が温かくなってきます、との事。", "シェフィの\n氷晶煌めく手作りアイス", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51030, 0, 52030, "ミソラの気まぐれ味噌ラーメン★", "なし", 0, 12, 0, 500, 0, "3ターンの間、すべての「マイルマス」をランク3の「トレジャーマス」に変化させる。", 3, 1, 1, "ミソラが作ったラーメン。適当に言った好きな物\nをランファが憶えていたために、材料を用意され\n仕方なく作ったものだが味はイイコト級の一品。\n美味しそうに食べられるのは嫌いではない様子。", "ミソラの\n気まぐれ味噌ラーメン★", 3);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51031, 0, 52031, "イロハの最強クワガタゼリー（人間用）", "なし", 0, 2, 0, 500, 0, "このターンに振るダイスの目が「1・6・6・6・8・8」になる。", 1, 20009, 3, "イロハがいつも持ち歩いているカラフルなゼリー\n…なのだがアルケス錬金堂で作られたクワガタ用\n餌ゼリーとそっくりな見た目をしている。甘くて\n美味しいが、人間用か尋ねると目を逸らされる。", "イロハの\n最強クワガタゼリー（人間用）", 1);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51032, 0, 52032, "グレイスの目ん玉飛び出るカップケーキ", "次にダイスを振った際、3マス多く進める。", 3, 9, 0, 500, 11, "料理マスをスキップして移動できる。効果は2ターンの間継続する。", 2, 4, 3, "グレイスお手製のカップケーキ。ショッキングな\n見た目に全振り…と思わせて目玉も食べることが\n可能で大変美味…と思わせて一部だけ魂消るほど\n甘い部分がある。原材料は頑なに秘されている。", "グレイスの\n目ん玉飛び出るカップケーキ", 2);
INSERT INTO `v1_b45fcd7363ab3cc7ac474ee20d6f4f2a869a3eecbb7a1b55732f798efdd89332` VALUES (51033, 0, 52033, "ソノの今日は特別ご褒美オムライス", "なし", 0, 8, 0, 500, 0, "RANK3のマイルショップを開く。", 0, 3, 1, "ソノが作ったオムライス。イロハの大好物であり\n家庭の味。普段は面倒くさがりやらないがイロハ\nが良い子の時はケチャップでクワガタを描いて\nあげている。その時は普段より美味しいらしい。", "ソノの今日は特別ご褒美オムライス", 3);
