INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 0.0, 2, 0.0, 24, 10046, "アヤネ", 401, 2, 0.0, 24001, "2020/07/31 12:00:00", 102301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 2.21, 2, 0.0, 24, 10046, "アヤネ", 402, 1, 0.0, 24002, "2020/07/31 12:00:00", 102301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 0.0, 2, 0.0, 24, 10046, "アヤネ", 403, 2, 0.0, 24003, "2020/07/31 12:00:00", 102301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 1.99, 1, 0.0, 24, 10046, "アサヒ", 701, 2, 0.0, 24004, "2020/07/31 12:00:00", 192201);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 2.21, 2, 0.0, 24, 10046, "アサヒ", 702, 1, 0.0, 24005, "2020/07/31 12:00:00", 192201);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 3.33, 1, 0.0, 24, 10046, "アサヒ", 703, 2, 0.0, 24006, "2020/07/31 12:00:00", 192201);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 0.0, 2, 0.0, 24, 10046, "部員３", 801, 2, 0.0, 24007, "2020/07/31 12:00:00", 192301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 2.68, 2, 0.0, 24, 10046, "部員３", 802, 1, 0.0, 24008, "2020/07/31 12:00:00", 192301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 0.0, 2, 0.0, 24, 10046, "部員３", 803, 2, 0.0, 24009, "2020/07/31 12:00:00", 192301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "やった！", 0.0, 1, 0.0, 24, 10046, "", 501, 1, 0.0, 24010, "2020/07/31 12:00:00", 192501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "勝ったよ！", 0.0, 1, 0.0, 24, 10046, "", 502, 1, 0.0, 24011, "2020/07/31 12:00:00", 192501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "いい調子！", 0.0, 1, 0.0, 24, 10046, "", 503, 1, 0.0, 24012, "2020/07/31 12:00:00", 192501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 5.33, 2, 0.0, 24, 10046, "ミサト", 111, 6, 0.0, 24013, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 6.85, 2, 0.0, 24, 10046, "ミサト", 112, 1, 0.0, 24014, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 3.0, 2, 0.0, 24, 10046, "ミサト", 113, 1, 0.0, 24015, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 6.11, 2, 0.0, 24, 10046, "ダイチ", 611, 1, 0.0, 24016, "2020/07/31 12:00:00", 192401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 6.75, 2, 0.0, 24, 10046, "ダイチ", 612, 1, 0.0, 24017, "2020/07/31 12:00:00", 192401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 0.0, 2, 0.0, 24, 10046, "ダイチ", 613, 2, 0.0, 24018, "2020/07/31 12:00:00", 192401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 4.52, 1, 0.0, 24, 10046, "ハツネ", 211, 2, 0.0, 24019, "2020/07/31 12:00:00", 113401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 3.89, 1, 0.0, 24, 10046, "ハツネ", 212, 2, 0.0, 24020, "2020/07/31 12:00:00", 113401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 13.49, 2, 0.0, 24, 10046, "ハツネ", 213, 1, 0.0, 24021, "2020/07/31 12:00:00", 113401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 3.17, 2, 0.0, 24, 10046, "カヤ", 311, 1, 0.0, 24022, "2020/07/31 12:00:00", 106501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 4.42, 2, 0.0, 24, 10046, "カヤ", 312, 1, 0.0, 24023, "2020/07/31 12:00:00", 106501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 2.89, 1, 0.0, 24, 10046, "カヤ", 313, 6, 0.0, 24024, "2020/07/31 12:00:00", 106501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 3.71, 3, 0.0, 24, 10046, "アヤネ", 421, 4, 0.0, 24025, "2020/07/31 12:00:00", 102301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 5.1, 1, 0.0, 24, 10046, "アサヒ", 721, 2, 0.0, 24026, "2020/07/31 12:00:00", 192201);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 4.34, 3, 0.0, 24, 10046, "部員３", 821, 1, 0.0, 24027, "2020/07/31 12:00:00", 192301);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "いい勝負だったよ\nまたやろうね", 0.0, 1, 0.0, 24, 10046, "", 521, 1, 0.0, 24028, "2020/07/31 12:00:00", 192501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 5.62, 2, -360.0, 24, 10046, "ミサト", 101, 1, 0.0, 24029, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 9.58, 2, -360.0, 24, 10046, "ミサト", 102, 1, 0.0, 24030, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 0.0, 2, -360.0, 24, 10046, "ミサト", 103, 2, 0.0, 24031, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 9.13, 1, -360.0, 24, 10046, "ミサト", 104, 2, 0.0, 24032, "2020/07/31 12:00:00", 113501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES ("2030/12/17 14:59:59", "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 8.27, 2, -360.0, 24, 10046, "ミサト", 105, 1, 0.0, 24033, "2020/07/31 12:00:00", 113501);
