INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24001, 401, 0.0, 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 102301, "アヤネ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24002, 402, 2.21, 0.0, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 102301, "アヤネ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24003, 403, 0.0, 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 102301, "アヤネ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24004, 701, 1.99, 0.0, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192201, "アサヒ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24005, 702, 2.21, 0.0, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192201, "アサヒ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24006, 703, 3.33, 0.0, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192201, "アサヒ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24007, 801, 0.0, 0.0, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192301, "部員３", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24008, 802, 2.68, 0.0, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192301, "部員３", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24009, 803, 0.0, 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192301, "部員３", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24010, 501, 0.0, 0.0, "やった！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192501, "", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24011, 502, 0.0, 0.0, "勝ったよ！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192501, "", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24012, 503, 0.0, 0.0, "いい調子！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192501, "", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24013, 111, 5.33, 0.0, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 113501, "ミサト", 6, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24014, 112, 6.85, 0.0, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 113501, "ミサト", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24015, 113, 3.0, 0.0, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 113501, "ミサト", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24016, 611, 6.11, 0.0, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192401, "ダイチ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24017, 612, 6.75, 0.0, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192401, "ダイチ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24018, 613, 0.0, 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192401, "ダイチ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24019, 211, 4.52, 0.0, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 113401, "ハツネ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24020, 212, 3.89, 0.0, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 113401, "ハツネ", 2, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24021, 213, 13.49, 0.0, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 113401, "ハツネ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24022, 311, 3.17, 0.0, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 106501, "カヤ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24023, 312, 4.42, 0.0, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 24, 2, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 106501, "カヤ", 1, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24024, 313, 2.89, 0.0, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 106501, "カヤ", 6, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24025, 421, 3.71, 0.0, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 24, 3, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 102301, "アヤネ", 4, 0.0);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (10046, 24026, 721, 5.1, 0.0, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 24, 1, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 192201, "アサヒ", 2, 0.0);
