INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アヤネ", 102301, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 0.0, 24001, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 2, 401);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アヤネ", 102301, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 2.21, 24002, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 1, 402);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アヤネ", 102301, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 0.0, 24003, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 2, 403);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アサヒ", 192201, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 1.99, 24004, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 2, 701);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アサヒ", 192201, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 2.21, 24005, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 1, 702);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アサヒ", 192201, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 3.33, 24006, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 2, 703);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "部員３", 192301, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 0.0, 24007, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 2, 801);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "部員３", 192301, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 2.68, 24008, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", 1, 802);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "部員３", 192301, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 0.0, 24009, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 2, 803);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "", 192501, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 0.0, 24010, "やった！", 1, 501);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "", 192501, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 0.0, 24011, "勝ったよ！", 1, 502);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "", 192501, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 0.0, 24012, "いい調子！", 1, 503);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 5.33, 24013, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 6, 111);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 6.85, 24014, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 1, 112);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 3.0, 24015, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", 1, 113);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ダイチ", 192401, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 6.11, 24016, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", 1, 611);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ダイチ", 192401, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 6.75, 24017, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 1, 612);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ダイチ", 192401, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 0.0, 24018, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 2, 613);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ハツネ", 113401, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 4.52, 24019, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", 2, 211);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ハツネ", 113401, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 3.89, 24020, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", 2, 212);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "ハツネ", 113401, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 13.49, 24021, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", 1, 213);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "カヤ", 106501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 3.17, 24022, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", 1, 311);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "カヤ", 106501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 4.42, 24023, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", 1, 312);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "カヤ", 106501, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 2.89, 24024, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", 6, 313);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アヤネ", 102301, "2030/12/17 14:59:59", 24, 3, "2020/07/31 12:00:00", 0.0, 3.71, 24025, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", 4, 421);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "アサヒ", 192201, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 5.1, 24026, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", 2, 721);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "部員３", 192301, "2030/12/17 14:59:59", 24, 3, "2020/07/31 12:00:00", 0.0, 4.34, 24027, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", 1, 821);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, 10046, "", 192501, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 0.0, 24028, "いい勝負だったよ\nまたやろうね", 1, 521);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (-360.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 5.62, 24029, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", 1, 101);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (-360.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 9.58, 24030, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", 1, 102);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (-360.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 0.0, 24031, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", 2, 103);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (-360.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 1, "2020/07/31 12:00:00", 0.0, 9.13, 24032, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", 2, 104);
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (-360.0, 10046, "ミサト", 113501, "2030/12/17 14:59:59", 24, 2, "2020/07/31 12:00:00", 0.0, 8.27, 24033, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", 1, 105);
