INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, "部員３", 2, 0.0, 0.0, 192301, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 24, 24009, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 803, 2, 10046);
