INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (0.0, "ダイチ", 2, 0.0, 6.75, 192401, "2020/07/31 12:00:00", "2030/12/17 14:59:59", 24, 24017, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", 612, 1, 10046);
