INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", "アヤネ", 24001, 2, 10046, 102301, "2020/07/31 12:00:00", 0.0, 0.0, 401, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", "アヤネ", 24002, 1, 10046, 102301, "2020/07/31 12:00:00", 0.0, 2.21, 402, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", "アヤネ", 24003, 2, 10046, 102301, "2020/07/31 12:00:00", 0.0, 0.0, 403, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", "アサヒ", 24004, 2, 10046, 192201, "2020/07/31 12:00:00", 0.0, 1.99, 701, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", "アサヒ", 24005, 1, 10046, 192201, "2020/07/31 12:00:00", 0.0, 2.21, 702, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", "アサヒ", 24006, 2, 10046, 192201, "2020/07/31 12:00:00", 0.0, 3.33, 703, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", "部員３", 24007, 2, 10046, 192301, "2020/07/31 12:00:00", 0.0, 0.0, 801, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", "部員３", 24008, 1, 10046, 192301, "2020/07/31 12:00:00", 0.0, 2.68, 802, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", "部員３", 24009, 2, 10046, 192301, "2020/07/31 12:00:00", 0.0, 0.0, 803, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "やった！", "", 24010, 1, 10046, 192501, "2020/07/31 12:00:00", 0.0, 0.0, 501, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "勝ったよ！", "", 24011, 1, 10046, 192501, "2020/07/31 12:00:00", 0.0, 0.0, 502, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "いい調子！", "", 24012, 1, 10046, 192501, "2020/07/31 12:00:00", 0.0, 0.0, 503, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", "ミサト", 24013, 6, 10046, 113501, "2020/07/31 12:00:00", 0.0, 5.33, 111, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", "ミサト", 24014, 1, 10046, 113501, "2020/07/31 12:00:00", 0.0, 6.85, 112, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", "ミサト", 24015, 1, 10046, 113501, "2020/07/31 12:00:00", 0.0, 3.0, 113, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", "ダイチ", 24016, 1, 10046, 192401, "2020/07/31 12:00:00", 0.0, 6.11, 611, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", "ダイチ", 24017, 1, 10046, 192401, "2020/07/31 12:00:00", 0.0, 6.75, 612, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", "ダイチ", 24018, 2, 10046, 192401, "2020/07/31 12:00:00", 0.0, 0.0, 613, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", "ハツネ", 24019, 2, 10046, 113401, "2020/07/31 12:00:00", 0.0, 4.52, 211, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", "ハツネ", 24020, 2, 10046, 113401, "2020/07/31 12:00:00", 0.0, 3.89, 212, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", "ハツネ", 24021, 1, 10046, 113401, "2020/07/31 12:00:00", 0.0, 13.49, 213, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", "カヤ", 24022, 1, 10046, 106501, "2020/07/31 12:00:00", 0.0, 3.17, 311, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", "カヤ", 24023, 1, 10046, 106501, "2020/07/31 12:00:00", 0.0, 4.42, 312, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", "カヤ", 24024, 6, 10046, 106501, "2020/07/31 12:00:00", 0.0, 2.89, 313, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (3, 0.0, "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", "アヤネ", 24025, 4, 10046, 102301, "2020/07/31 12:00:00", 0.0, 3.71, 421, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", "アサヒ", 24026, 2, 10046, 192201, "2020/07/31 12:00:00", 0.0, 5.1, 721, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (3, 0.0, "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", "部員３", 24027, 1, 10046, 192301, "2020/07/31 12:00:00", 0.0, 4.34, 821, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "いい勝負だったよ\nまたやろうね", "", 24028, 1, 10046, 192501, "2020/07/31 12:00:00", 0.0, 0.0, 521, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", "ミサト", 24029, 1, 10046, 113501, "2020/07/31 12:00:00", -360.0, 5.62, 101, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", "ミサト", 24030, 1, 10046, 113501, "2020/07/31 12:00:00", -360.0, 9.58, 102, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", "ミサト", 24031, 2, 10046, 113501, "2020/07/31 12:00:00", -360.0, 0.0, 103, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (1, 0.0, "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", "ミサト", 24032, 2, 10046, 113501, "2020/07/31 12:00:00", -360.0, 9.13, 104, 24, "2030/12/17 14:59:59");
INSERT INTO `v1_b54b0124696648883dca4d515c0caf5a83fedbac4de1e9cd537f4c6b4ed6deed` VALUES (2, 0.0, "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", "ミサト", 24033, 1, 10046, 113501, "2020/07/31 12:00:00", -360.0, 8.27, 105, 24, "2030/12/17 14:59:59");
