INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (6, 24029, 6, 31461, "河童の川流れ", 2, 1, "どんなに上手な人でも、時には失敗すること。とある妖怪の名を使ったことわざ", 27827, 2, "河童川流れ", "これ、何と言う？", 4, 2217, 27969, 6, 0, 6, 12428, "河童の川流れ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12392, 4, 12435, "", 2, 1, "「西遊記」に登場する孫悟空が乗る雲。仙人のみ乗ることが出来る", 12365, 2, "きんとうん", "これ、何と言う？", 4, 2224, 12358, 4, 0, 4, 12435, "きんとうん");
