INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12371, 4, 12387, "", 2, 2, "キャルの口癖の一つ。主に腹を立てたときに言うことが多いが、本気で言ってるわけではない", 12406, 1, "ぶっころす", "これ、何と言う？", 4, 2229, 12429, 4, 2229, 4, 12377, "ぶっころすわよ！");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12452, 4, 12496, "", 2, 2, "キャルの口癖の一つ。良い意味でも悪い意味でもヤバイことが起きたときに言うことが多い", 12516, 1, "ヤバイわよ", "これ、何と言う？", 4, 2230, 12431, 5, 2230, 5, 12424, "ヤバイわよ！");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12387, 4, 12356, "", 2, 2, "ペコリーヌがよく口にする、独特なあいさつ。片手を前に突き出しながら言うのがお決まり", 12362, 1, "おいっす～", "これ、何と言う？", 4, 2231, 12377, 4, 1255, 4, 65374, "おいっす～☆");
