INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12487, 5, 12486, "", 2, 2, "「位置について、よーい、どん！」これに意味の近い英語のフレーズ", 12473, 2, "ステディー", "これ、何と言う？", 5, 2245, 12451, 5, 0, 5, 12540, "レディ ステディー ゴー！");
