INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12496, 5, 12531, "", 5, 3, "蒸した鶏肉をごまだれで味付けた中華料理。キュウリを添えることが多い", 12496, 2, "バンバンジ", "これ、何と読む？", 5, 5152, 12531, 5, 0, 5, 12472, "棒棒鶏＝バンバンジー");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12409, 4, 12381, "", 5, 2, "天ぷらの一種。「青のり」を混ぜた衣で、ちくわなどの練り物を揚げた食べ物", 12356, 2, "いそべあげ", "これ、何と言う？", 4, 5153, 12354, 4, 0, 4, 12370, "いそべあげ");
