INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12472, 5, 12540, "", 5, 1, "非常にポピュラーな紅茶の種類。「ウバ」「キーマン」に並び、「世界三大銘茶」と評される", 12480, 2, "ダージリン", "これ、何と言う？", 5, 5155, 12522, 5, 0, 5, 12531, "ダージリン");
