INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12472, 5, 12531, "", 5, 1, "非常にポピュラーな、炭酸飲料の一種。ショウガの風味が特徴的", 12472, 2, "ジンジャー", "これ、何と言う？", 5, 5157, 12515, 5, 0, 5, 12540, "ジンジャーエール");
