INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12531, 4, 12522, "", 5, 2, "一般にビールやレバー、エビ、牡蠣などに多く含まれる成分。あまりとり過ぎると痛風の原因に", 12503, 2, "プリンたい", "これ、何と言う？", 4, 5159, 12383, 5, 0, 5, 12356, "プリンたい");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12393, 4, 12396, "", 5, 1, "豆腐の種類として特によく食べられる2種類。1つは「木綿豆腐」で、もう一つは？", 12365, 2, "きぬどうふ", "これ、何と言う？", 4, 5160, 12358, 4, 0, 4, 12405, "きぬどうふ");
