INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12363, 4, 12462, "", 5, 2, "花粉症の原因として最も多いのは、どんな種類の花粉？", 12473, 2, "スギかふん", "これ、何と言う？", 4, 5163, 12405, 5, 0, 5, 12435, "スギかふん");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12483, 5, 12524, "", 5, 2, "固定の時間帯で働く従来の働き方とは違い、始業と終業の時間を自由に決められる労働制度", 12501, 2, "フレックス", "これ、何と言う？", 5, 5164, 12463, 5, 0, 5, 12473, "フレックスタイム制");
