INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12483, 5, 12524, "", 6, 2, "『ティラノサウルス』の別の言い方", 84, 2, "Tレックス", "これ、何と言う？", 5, 6049, 12463, 5, 0, 2, 12473, "Tレックス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12488, 5, 12521, "", 6, 1, "3本の立派なツノが特徴の、有名な恐竜。名前の読み方は、「トリ」で区切るのが実は正しい", 12465, 2, "ケラトプス", "これ、何と言う？", 5, 6050, 12503, 5, 0, 5, 12473, "トリケラトプス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12465, "エコーロケーション", 6, 2, "イルカやコウモリなどが持つ能力。超音波を発し、その反響によって周囲の様子を探る", 12525, 2, "ロケション", "これ、何と言う？", 5, 6051, 12519, 5, 0, 5, 12531, "エコーロケーション");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12464, 5, 12531, "マングローブ", 6, 1, "熱帯や亜熱帯の一部の水辺に生育する植物の総称で、非常にユニークな形の根っこを形成する", 12510, 2, "マングロブ", "これ、何と言う？", 5, 6052, 12525, 5, 0, 5, 12502, "マングローブ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (6, 35352, 6, 28982, "", 6, 1, "学術的に貴重なものとして保護される、天然の動物・植物・地質・鉱物。アホウドリやトキなど", 22825, 2, "天然記念物", "これ、何と言う？", 6, 6053, 24565, 6, 0, 6, 29289, "天然記念物");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12405, 4, 12385, "", 6, 1, "人間の足の裏の、地面と接しないくぼんだ部分。人によっては無い場合もある", 12388, 2, "つちふまず", "これ、何と言う？", 4, 6054, 12414, 4, 0, 4, 12378, "つちふまず");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12531, 5, 12449, "", 6, 2, "『タマキ』の裏の名前にして、恵まれない人たちを助ける、謎に包まれた義賊", 12501, 1, "ファントム", "これは何？", 5, 6055, 12488, 5, 5036, 5, 12512, "ファントムキャッツ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12450, 5, 12531, "", 6, 3, "裏社会で恐れられる『ミツキ』の二つ名。彼女の隻眼からは逃れられない", 12527, 1, "ワンアイド", "これ、何と読む？", 5, 6056, 12452, 5, 6056, 5, 12489, "隻眼の赤い悪魔\n＝ワンアイドデビル");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12463, 5, 12524, "", 6, 3, "『ナナカ』の二つ名。さまざまな魔法を探し集めていることに由来する", 12467, 1, "コレクター", "これ、何と読む？", 5, 6057, 12479, 5, 6057, 5, 12540, "蒐集家（＝コレクター）");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12490, 5, 12464, "", 6, 3, "『ネネカ』の、『七冠』としての二つ名。あらゆるものに姿を変化させられることから", 12524, 1, "レグナント", "これ、何と読む？", 5, 6058, 12531, 5, 6058, 5, 12488, "変貌大妃（＝メタモルレグナント）");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12459, 5, 12531, "", 6, 2, "『イノリ』が『カヤ』に対して、ムキになったり、おちょくったりするときの呼び方", 12465, 1, "ケンカバカ", "これ、何と言う？", 5, 6059, 12496, 5, 6059, 5, 12459, "脳筋ケンカバカ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12502, 5, 12521, "サラブレッド", 6, 1, "レースのために、長い年月をかけて改良された類の馬。家柄の良い人のたとえ表現でもおなじみ", 12469, 2, "サラブレド", "これ、何と言う？", 5, 6060, 12524, 5, 0, 5, 12489, "サラブレッド");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12462, 5, 12490, "", 6, 3, "キツネの一種。冷ややかで悟りを開いたような表情に見えることで近年人気を博している", 12473, 2, "スナギツネ", "これ、何と言う？", 5, 6061, 12484, 5, 0, 5, 12493, "チベットスナギツネ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12459, 5, 12521, "シーラカンス", 6, 2, "数億年も前から変わらない姿のまま現存しているため、「生きた化石」と呼ばれる深海魚", 12471, 2, "シラカンス", "これ、何と言う？", 5, 6062, 12531, 5, 0, 5, 12473, "シーラカンス");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12463, 5, 12521, "", 6, 1, "カブトムシの中で最大のサイズを誇る種として、子どもたちからも絶大な人気を誇る昆虫", 12504, 2, "ヘラクレス", "これ、何と言う？", 5, 6063, 12524, 5, 0, 5, 12473, "ヘラクレスオオカブト");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12521, 5, 12502, "", 6, 1, "昔の子どもたちがこの虫の体液を髪に塗り、テカテカさせて遊んでいたことに由来する虫の名前", 12450, 2, "アブラムシ", "これ、何と言う？", 5, 6064, 12512, 5, 0, 5, 12471, "アブラムシ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12412, 4, 12365, "", 6, 1, "目のすぐ下のあたりにあるほくろの通称", 27875, 2, "泣きぼくろ", "これ、何と言う？", 4, 6065, 12367, 4, 0, 6, 12429, "泣きぼくろ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12496, 5, 12523, "シルバーバック", 6, 3, "年を重ねたオスのゴリラの背中の毛が白くなる現象。群れの中のボス個体によく見られる", 12471, 2, "シルババク", "これ、何と言う？", 5, 6066, 12496, 5, 0, 5, 12463, "シルバーバック");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12524, 5, 12513, "", 6, 1, "身体の色を自在に変化させることでおなじみの爬虫類。演技の幅が広い俳優や声優のたとえにも", 12459, 2, "カメレオン", "これ、何と言う？", 5, 6067, 12458, 5, 0, 5, 12531, "カメレオン");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12516, 5, 12491, "", 6, 1, "非常に大きなトンボの一種。「鬼」の服装とよく似た、黒と黄色のシマシマ模様が特徴的", 12458, 2, "オニヤンマ", "これ、何と言う？", 5, 6068, 12531, 5, 0, 5, 12510, "オニヤンマ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12511, 5, 12523, "グルーミング", 6, 3, "猫などの動物の習性として知られる「毛づくろい」。英語でなんと言う？", 12464, 2, "グルミング", "これ、何と言う？", 5, 6069, 12531, 5, 0, 5, 12464, "グルーミング");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12464, 5, 12531, "", 6, 1, "一般に「ハブ」の天敵というイメージを持たれている、体長数十センチほどの哺乳類", 12510, 2, "マングース", "これ、何と言う？", 5, 6070, 12540, 5, 0, 5, 12473, "マングース");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12516, 5, 12531, "", 6, 2, "トンボの一種。身体は黄緑色だが、よく見るとキレイな銀色の模様があることからこう呼ばれる", 12462, 2, "ギンヤンマ", "これ、何と言う？", 5, 6071, 12531, 5, 0, 5, 12510, "ギンヤンマ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12511, 5, 12514, "", 6, 3, "「カミツレ」とも呼ばれるハーブの一種。りんごのような香りがあり、お茶としてよく好まれる", 12459, 2, "カモミール", "これ、何と言う？", 5, 6072, 12540, 5, 0, 5, 12523, "カモミール");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12523, 5, 12479, "", 6, 2, "指先ほどのサイズの小さなイカ。青白く発光する性質があり、また食用としても好まれる", 12507, 2, "ホタルイカ", "これ、何と言う？", 5, 6073, 12452, 5, 0, 5, 12459, "ホタルイカ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12513, 5, 12531, "", 6, 1, "高級魚や縁起物としておなじみのタイの一種。名前は、目が金色に輝くことから", 12461, 2, "キンメダイ", "これ、何と言う？", 5, 6074, 12480, 5, 0, 5, 12452, "キンメダイ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12463, 5, 12477, "", 6, 3, "深海に生息する甲殻類。姿はダンゴムシに似ていて、大きなものは体長50cmを超える", 12464, 2, "グソクムシ", "これ、何と言う？", 5, 6075, 12512, 5, 0, 5, 12471, "ダイオウグソクムシ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12450, 5, 12531, "", 6, 2, "水族館でも人気なアナゴの一種。砂底から身体を出し、群れで同じ方向をじっと向く姿が特徴的", 12481, 2, "チンアナゴ", "これ、何と言う？", 5, 6076, 12490, 5, 0, 5, 12468, "チンアナゴ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12524, 5, 12501, "", 6, 1, "「世界最大の花」としておなじみの、熱帯地域に生息する花。強い悪臭を放つことでも有名", 12521, 2, "ラフレシア", "これ、何と言う？", 5, 6077, 12471, 5, 0, 5, 12450, "ラフレシア");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12486, 5, 12491, "", 6, 3, "真っ赤なカサに白い点のある、キノコの一種。毒があり、食べると幻覚・腹痛などの症状が出る", 12505, 2, "ベニテング", "これ、何と言う？", 5, 6078, 12531, 5, 0, 5, 12464, "ベニテングタケ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12495, 5, 12494, "", 6, 3, "「フクロウ」の一種。身の危険を感じたときに身体を細く縮めて、木の枝に擬態する習性がある", 12467, 2, "コノハズク", "これ、何と言う？", 5, 6079, 12474, 5, 0, 5, 12463, "コノハズク");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12471, 5, 12531, "", 6, 3, "両生類の一種。防衛のために出す粘液の匂いが、和食に使うとあるスパイスに似ているのが由来", 12469, 2, "サンショウ", "これ、何と言う？", 5, 6080, 12519, 5, 0, 5, 12454, "サンショウウオ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12494, 5, 12461, "", 6, 3, "「蕗（フキ）」が大きく成長する前の、つぼみの部分。春の山菜として、天ぷらなどで好まれる", 12501, 2, "フキノトウ", "これ、何と言う？", 5, 6081, 12488, 5, 0, 5, 12454, "フキノトウ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12454, 5, 12488, "", 6, 1, "砂糖の原料となる、イネ科の植物", 12469, 2, "サトウキビ", "これ、何と言う？", 5, 6082, 12461, 5, 0, 5, 12499, "サトウキビ");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12385, 4, 12375, "", 6, 1, "フェニックスとも呼ばれる、死しても再びよみがえるとされる伝説の鳥", 12405, 2, "ふしちょう", "これ、何と言う？", 4, 6083, 12423, 4, 0, 4, 12358, "ふしちょう");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12358, 4, 12421, "", 6, 1, "「五感」と呼ばれる動物の機能、視覚・聴覚・触覚・味覚と、あと一つは？", 12365, 2, "きゅうかく", "これ、何と言う？", 4, 6084, 12363, 4, 0, 4, 12367, "きゅうかく");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12511, 5, 12521, "", 6, 2, "ゴリラが手で胸をドコドコと叩いて音をたてる行動", 12489, 2, "ドラミング", "これ、何と言う？", 5, 6085, 12531, 5, 0, 5, 12464, "ドラミング");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (5, 12473, 5, 12512, "", 6, 3, "太ももの裏にある、大きな筋肉の集まり。筋トレでも重要視されることが多い", 12495, 2, "ハムストリ", "これ、何と言う？", 5, 6086, 12488, 5, 0, 5, 12522, "ハムストリング");
INSERT INTO `v1_b5c296b4c123940a387bbbcfebcf7081e945a2211aa30b2e69bf315c66f3b966` VALUES (4, 12375, 4, 12387, "", 6, 1, "肉を食べるのは肉食。植物を食べるのは草食。では、どちらかに限らずいろいろ食べるのは？", 12374, 2, "ざっしょく", "これ、何と言う？", 4, 6087, 12423, 4, 0, 4, 12367, "ざっしょく");
